float drawLine (vec2 p1, vec2 p2, vec2 uv, float a)
{
    float r = 0.;
    float one_px = 1. / iResolution.x; //not really one px

    // get dist between points
    float d = distance(p1, p2);

    // get dist between current pixel and p1
    float duv = distance(p1, uv);

    //if point is on line, according to dist, it should match current uv 
    r = 1.-floor(1.-(a*one_px)+distance (mix(p1, p2, clamp(duv/d, 0., 1.)),  uv));

    return r;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
vec2 uv = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    // Time varying pixel color
    vec3 col = vec3(0.0f);
    vec3 col2 = vec3(0.0f);
    float radius = .5f;
    float tt = iTime;
       uv.y += .15;

    vec3 l1 = vec3(0.0f);
        vec3 l2 = vec3(0.0f);
    for(int i = 0; i < 25; i++)
    {
        l1 += vec3(
        drawLine(
        vec2(0.0, 0.0), 
        vec2( radius * sin(tt + float(i)), 
        (cos(uv.y * 2.7) - radius * cos(tt + float(i)))) - .15, 
        uv - vec2(0.0,0.0f), 
        1.0));


        l2 += vec3(
        drawLine(
        vec2(0.0, 0.0), 
        vec2( radius * sin(tt - float(i)), 
        (sin(uv.x * 1.7) - radius * sin(tt - float(i)))) - .15, 
        uv - vec2(0.0,0.0f), 
        1.0));

    }


    //col += vec3(drawLine(vec2(0.0, 0.0), vec2( radius * sin(tt + 2.0f),radius * cos(tt + 2.0f)), uv, 2.5));
    l1 = clamp(l1, 0.0f, 1.0f);
    l2 = clamp(l2, 0.0f, 1.0f);

    vec3 cc = l1 * vec3(abs(uv.x) * 22. + abs(uv.y), uv.y, 0.0f);
    cc += l2;

    // Output to screen
    fragColor = vec4(cc,1.0);
}

