vec3 stars(vec3 uv)
{
    vec3 col = vec3(0.);
    for(int k = 0; k < 6; k++){ 
        vec3 offset =
            (vec3(uv.y+uv.x+.5,uv.z+uv.y+.5,uv.x+uv.z))
            //vec3(uv.y-uv.x+.5,uv.z-uv.y+.5,uv.x+uv.z)
        ;
        uv =
            min(offset.yzx+uv.yzx,abs(fract(offset)-.5))
        ;

        //uv += abs(fract(uv*4.+iTime)-.5)/64.;


        col = vec3(col.yz,uv.x-uv.y).yzx;
    }

    return
        col.yzx
        //col - uv;
    ;
}

