      1.+fmod(floor(t/s1)*floor(t/s1+z1),2.)
  ;
  t /= z1;

  float a=sqrt((1.-sqrt(fract(t*s1))))*.2,

  nb = pow(2.,(m3+z1)/5.+7.5);

  return abs(.5-vec2(fract(t*nb*.998)
   ,fract(t*nb)))*a;

}

