#define BOUNCES 4

#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define hash(p) fract(sin(p)*43758.5453)
#define hash33(p) fract(sin((p) * mat3(127.1,311.7,74.7, 269.5,183.3,246.1, 113.5,271.9,124.6))*43758.5453123)

vec3 lightPos = vec3(0., 1.4, 0.);
vec3 groundColor = vec3(.9, .8, .7);

float sdBox(vec3 p, vec3 s) {
  vec3 q = abs(p) - s;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0)-.0;
}

vec2 scene(vec3 p) {
    float ground = p.y + .2;

    vec3 p0 = p;
    p.xz = fract(p.xz+.5)-.5;
    float boxes = sdBox(p, vec3(.2, .2 + length(sin(floor(p0+.5)*8.))*.1, .35))/2.;

    if (boxes < ground) return vec2(boxes, 1.);
    return vec2(ground, 0.);
}

vec3 cosineDirection(float seed, vec3 nor) {
    vec3 tc = vec3( 1.0+nor.z-nor.xy*nor.xy, -nor.x*nor.y)/(1.0+nor.z);
    vec3 uu = vec3( tc.x, tc.z, -nor.x );
    vec3 vv = vec3( tc.z, tc.y, -nor.y );

    float u = hash( 78.233 + seed);
    float v = hash( 10.873 + seed);
    float a = 6.283185 * v;

    return sqrt(u)*(cos(a)*uu + sin(a)*vv) + sqrt(1.0-u)*nor;
}

vec2 worldIntersect(vec3 ro, vec3 rd, float maxt) {
    float t = 0.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + t*rd;
        vec2 d = scene(p);
        t += d.x;

        if (d.x < 0.01) return vec2(t, d.y);
        if (t > maxt) break;
    }
    return vec2(-1.);
}

float worldShadow(vec3 ro, vec3 rd, float maxt) {
    float t = 0.;
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + t*rd;

        float d = scene(p).x;
        t += d;

        if (d < 0.01) return 0.;
        if (t > maxt) break;
    }
    return 1.;
}

vec3 worldGetNormal(vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*scene( p + k.xyy*h ).x + k.yyx*scene( p + k.yyx*h ).x + 
                      k.yxy*scene( p + k.yxy*h ).x + k.xxx*scene( p + k.xxx*h ).x );
}

vec3 worldApplyLighting(vec3 p, vec3 nor) {
    vec3 lightDir = normalize(lightPos - p);
    float lightDist = length(lightPos - p);
    float diffuse = clamp(dot(nor, lightDir), 0., 1.);
    float shadow = worldShadow(p, lightDir, lightDist);
    float att = clamp(1./pow(lightDist*.4, 2.), 0., 1.);

    return vec3(diffuse * shadow * att);
}

vec3 worldGetColor(vec3 p, float objectID) {
    if (objectID == 0.) return groundColor;
    else {
        vec3 fp = floor(p-vec3(.5,0.,.5));
        if (fp.xz == vec2(-1.)) return vec3(1.);
        return sin(vec3(.02,.04,.06)*fp*1000.)*.5+.5;
    }
}

vec3 worldGetBackground(vec3 rd) {
    return rd.y * vec3(.4, .4, .5);
}

#define isLightCube(p) floor(p-.5).xz == vec2(-1.)
vec3 renderPixel(vec3 ro, vec3 rd, float sa, bool renderRaymarching) {
    vec3 tcol = vec3(0.0);
    vec3 fcol = vec3(1.0);

    for (int i = 0; i < BOUNCES; i++) {
        vec2 res = worldIntersect(ro, rd, 30.);

        if (res.y < 0.) {
            if (i == 0) return worldGetBackground(rd);
            break;
        }

        vec3 p = ro + (res.x-.02)*rd;
        vec3 n = worldGetNormal(p);

        vec3 scol = worldGetColor(p, res.y);
        vec3 dcol = (res.y == 1. && isLightCube(p)) ? vec3(1.) : worldApplyLighting(p, n);

        ro = p;
        rd = cosineDirection(76.2 + 73.1*float(i) + sa + 17.7*float(iFrame), n);

        fcol *= scol;
        tcol += fcol * dcol; 

        if (renderRaymarching) break;
    }

    return tcol;
}

void initRayOriginAndDirection(vec2 uv, inout vec3 ro, inout vec3 rd) {
    vec2 m = iMouse.xy/iResolution.xy*2.-1.;

    float z = iMouse.z > 0. ? 1. : smoothstep(0., 6., iTime);
    ro = vec3(0., 0., iMouse.z == 0. ? 4. : 4. - 1.+(m.y*.5+.5)); 
    ro.yz *= rot(iMouse.z == 0. ? -0.3 : (m.y-.9)*.5);
    ro.zx *= rot(iMouse.z == 0. ? -5.6 : m.x*2.);

    vec3 f = normalize(-ro), r = normalize(cross(vec3(0,1,0), f));
    rd = normalize(f + uv.x*r + uv.y*cross(f, r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 color = texelFetch( iChannel0, ivec2(fragCoord), 0 ).xyz;
    if (iMouse.z > 0. || iFrame == 0) color *= 0.;

    bool renderRaymarching = fragCoord.x/iResolution.x > .5;
    float sa = renderRaymarching ? 0. : hash(dot(fragCoord, vec2(12.9898, 78.233)) + 1113.1*iTime);

    vec2 of = (vec2( hash(sa+13.271), hash(sa+63.216) )-0.5);
    vec2 uv = (fragCoord+of - .5*iResolution.xy)/iResolution.y;

    vec3 ro, rd;
    initRayOriginAndDirection(uv, ro, rd);

    color += renderPixel(ro, rd, sa, renderRaymarching);
    color *= smoothstep(0.003, .004, abs(fragCoord.x/iResolution.x - .5));

    if (fragCoord == vec2(0.5, 0.5) && iMouse.z > 0.) color.r = float(iFrame);

    fragColor.rgb = color;
}

