        }
        float g = h[0]
                    + (h[2]-h[0])*s.z
                    + (h[1]-h[0])*s.w
                    + (h[0]-h[1]-h[2]+h[3])*s.z*s.w;

        k += factor * g;

        factor *=.6;
        p *= 2.;
        p *= m4;
    }
    return k;
}

float CloudFBM_ld(vec3 p)
{
    float k = 0.;
    vec3 dk = vec3(0.);
    float factor=1.;
    float uvFactor = 1.;
    mat3 mTotal = mat3(1,0,0,0,1,0,0,0,1);
    for ( int i=0;i<3;++i)
    {
        p+=vec3(1.56437612,6.415345893,8.62512677);
        vec3 q = floor(p);

        float a0 = noise(q);
        float b0 = noise(q+vec3(1.,0.,0.));
        float c0 = noise(q+vec3(0.,1.,0.));
        float d0 = noise(q+vec3(1.,1.,0.));

        float a1 = noise(q+vec3(0.,0.,1.));
        float b1 = noise(q+vec3(1.,0.,1.));
        float c1 = noise(q+vec3(0.,1.,1.));
        float d1 = noise(q+vec3(1.,1.,1.));

        vec3 s = smoothstep(0.,1.,fract(p));

        float k0 = (
            a0
            + (b0-a0)*s.x
            + (c0-a0)*s.y
            + (a0-b0-c0+d0)*s.x*s.y);
        float k1 = (
            a1
            + (b1-a1)*s.x
            + (c1-a1)*s.y
            + (a1-b1-c1+d1)*s.x*s.y);
        k+=factor*((k1-k0)*s.z+k0);
        factor *=.6;
        p *= 2.;
        //p.y*=2.5;
        p *= m3;
    }
    return k;
}

float CloudDensity_ld(vec3 p)
{
    float v = clamp(CloudFBM_ld(vec4(p*vec3(.1,.1,.1)*.04,morphSpeed*iTime*.005)),-1.,1.)*.5 + .5;
    float d = (p.y-cloudStartHeight) / (cloudEndHeight - cloudStartHeight);
    v *= smoothstep(0.,0.4,d);
    v *= 1. - smoothstep(0.6,1.,d);
    return v;
}

float CloudDensity_hd(vec3 p, int detail)
{
    float v = clamp(CloudFBM(vec4(p*vec3(.1,.1,.1),morphSpeed*iTime*.02),detail),-1.,1.)*.5 + .5;
    return v;
}

