#define PI 3.1415927

mat3 Rotation(float phi, float theta)
{
    float cp = cos(phi);
    float sp = sin(phi);
    float ct = cos(theta);
    float st = sin(theta);
    mat3 Mp = mat3(
        cp,0.,-sp,
        0.,1.,0.,
        sp,0.,cp);
    mat3 Mt = mat3(
        1.,0.,0.,
        0.,ct,-st,
        0.,st,ct);
    return Mp*Mt;
}

vec4 mouse;
vec3 resolution;

mat3 MouseToRot()
{
    vec2 mouse = (2.*mouse.xy-resolution.xy) / resolution.xy;
    float phi = mouse.x*PI;
    float theta = (mouse.y)*PI*.5;
    return Rotation(phi,theta);
}

