float map(vec3 p, out worm W){
    float d = 1e10;    
    p += vec3(1., 0., 1.);
    d = Worm(p, W);
    return d;
}

void rayMarch(vec3 ro, vec3 rd, float start, out worm W1) {
  float depth = start;
  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    float d = map(p, W1);
    depth += d*.5;
    if (d < PRECISION){
        W1.depth = depth;
        break;
        }
    else W1.id = 0.;
    if (d>MAX_DIST){
        W1.depth = 1e10;
        break;
    }
    }
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.001;
    vec3 c;
    worm obj;
    return normalize(
      e.xyy * map(p + e.xyy, obj) +
      e.yyx * map(p + e.yyx, obj) +
      e.yxy * map(p + e.yxy, obj) +
      e.xxx * map(p + e.xxx, obj));
}

vec4 rayTransp(vec3 ro, vec3 rd, float depth, out worm W2, out float density, vec3 normal, float eta) {
  vec3 p = ro + depth*rd;
  ro = p;
  depth = 0.;
  float d=1.;
  int iter = 0;
  float dit = (fract(sin(dot(p, vec3(10.4886, 14.214654, 21.4567)))))*0.0002;
  while (d>0.){
      p = ro+depth*rd;
      d = map(p, W2);
      depth += .01;
      iter += 1;
      if (iter>10) break;
  }
  rd = refract(rd, normal, eta);

  for (int i = 0; i < 400; i++) {
    p = ro + depth * rd;
    d = map(p, W2);
    depth += 0.002;
    if (d<0.) density += 0.00025;
        else break;
     }

   return vec4(depth*rd, depth);
}

mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


vec3 phong(vec3 lightDir, vec3 normal, vec3 rd, vec3 col, float spec) {
  // ambient
  float N = 2.;
  vec3 ambient = (col*N)/N;

  // diffuse
  float dotLN = clamp(dot(lightDir, normal), 0., 1.);
  vec3 diffuse = (col * dotLN*N)/N;

  // specular
  float dotRV = clamp(dot(reflect(lightDir, normal)/1.4, -rd), 0., 1.);
  vec3 specular = 50.*spec*((.5*col+vec3(.5)) * pow(dotRV, 15.)*N)/N;

  return ambient + diffuse + specular;
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up

	return mat3(-cr, cu, -cd);
}

