  }
    vec3 tmp = vec3(-2., 0.,0. );
  vec3 lookat = tmp;
  ro.yz = ro.yz * 1. * rot(mix(PI/2., 0., m.y));
  ro.xz = ro.xz * rot(mix(-PI, PI, m.x)) + vec2(lookat.x, lookat.z); 
  vec3 rd = camera(ro, lookat)*normalize(vec3(uv, -1));
  worm W1 = worm(1e10, vec3(0.,0.,0.), 0., 0., 1.);
  worm W2 = W1;
  vec3 normal, normal2;
  float d, d2;
  for (int i = 0; i<AA+1; i++){
      for (int j = 0; j<AA+1; j++){

          vec3 rd2 = rd + vec3(float(i), float(j), 0.)*(.0005);
          rayMarch(ro, rd2, MIN_DIST, W1); 
          float d = W1.depth;
