  } else {
    if (cDir != 0.) p = 0.5 * vec2 (p.x - cDir * sqrt3 * p.y, cDir * sqrt3 * p.x + p.y);
    pc1 = vec2 (sqrt3/2. * sign (p.x), 0.);
    pc2 = vec2 (sqrt3 * sign (p.x), 0.);
    d1 = sqrt (Ddot (p - pc1));
    d2 = abs (sqrt (Ddot (p - pc2)) - 1.5);
    d = min (d1, d2);
    pc = (d == d1) ? pc1 : pc2;
    rc = (d == d1) ? 0.: 1.5;
  }
  dp = p - pc;
  ac = 0.5 - atan (dp.y, - dp.x) / (2. * pi);
  cxy = cIdB.x - cIdB.y;
#if P_LONG  
  if (ct && cxy < 0. || ! ct && abs (cxy - 2.) == 1.) ac *= -1.;
  if (! ct && abs (cxy - 2.) <= 1.) ac += 1./6.;
#else
  if (ct && cxy < 0.) ac *= -1.;
#endif
  return vec3 (d, rc, ac);
}

bool OnTrk (vec2 w)
{
  vec2 wp, wm;
  float cxy;
  bool cyo, offTrk;
  cxy = cIdB.x - cIdB.y;
  cyo = (mod (cIdB.y, 2.) == 1.);
  wm = Rot2Cs (w, sin (- pi / 3. + vec2 (0.5 * pi, 0.))) - vec2 (0., 0.3);
  wp = Rot2Cs (w, sin (pi / 3. + vec2 (0.5 * pi, 0.)));
#if P_LONG  
  offTrk = (cxy == -2. && wm.y > 0. ||
     ! cyo && (cxy == -3. && w.y > -0.3 || cxy == -2. || cxy == -1. && wm.y > 0. ||
     cxy == 1. && wm.y < 0. || (cxy == 2. || cxy == 3.) && w.x < 0. || cxy == 4. || cxy == 5.) ||
     cyo && (cxy == -3. || cxy == 0. && wp.x > 0. || (cxy == 1. || cxy == 2.) && w.x > 0. || 
     cxy == 3. || cxy == 4. && wm.x < 0. || cxy == 5. && wm.y < 0.));
#else
  offTrk = (cxy == -2. && wm.y > 0. || ! cyo && (abs (cxy) == 2. || cxy == -3. && w.y > -0.3 ||
     cxy == -1. && wm.y > 0. || cxy == 1. && wm.y < 0.) ||
     cyo && (cxy == -3. || cxy == 0. && wp.x > 0. || cxy == 1. && wm.x < 0. ||
     cxy == 2. && wm.y < 0.));
#endif
     return ! offTrk;
}

void SetPath ()
{
  float ts, tl;
  ts = 1.;
  tl = 1.5;
  cPath[ 0] = vec4 (0., 0., -1./6., tl);
  cPath[ 1] = vec4 (1., 0., 1./3., - ts);
  cPath[ 2] = vec4 (0., 1., 5./6., tl);
  cPath[ 3] = vec4 (-1., 1., -1./3., ts);
  cPath[ 4] = vec4 (-1., 2., 1., ts);
  cPath[ 5] = vec4 (0., 1., - 1./6., tl);
  cPath[ 6] = vec4 (1., 1., 1./6., tl);
  cPath[ 7] = vec4 (1., 2., 1., ts);
#if P_LONG
  cPath[ 8] = vec4 (2., 1., 1./2., - tl);
  cPath[ 9] = vec4 (2., 0., -1./2., tl);
  cPath[10] = vec4 (3., -1., -1./6., tl);
  cPath[11] = vec4 (4., -1., 1./3., - ts);
  cPath[12] = vec4 (3., 0., -1./2., - tl);
  cPath[13] = vec4 (3., 1., 1./2., tl);
  cPath[14] = vec4 (2., 2., 5./6., tl);
#else
  cPath[ 8] = vec4 (2., 1., -1./6., tl);
  cPath[ 9] = vec4 (3., 1., 1./3., - ts);
  cPath[10] = vec4 (2., 2., 5./6., tl);
#endif
  cPath[PLEN - 2] = vec4 (1., 2., -1./3., ts);
  cPath[PLEN - 1] = vec4 (1., 3., 1., ts);
  tEnd[0] = 0.;
  for (int k = 0; k < PLEN; k ++) tEnd[k + 1] = tEnd[k] + abs (cPath[k].w);
  tLen = tEnd[PLEN];
}

vec2 EvalPPos (float t)
{
  vec4 cp;
  vec2 tp, vd;
  float tt, r, a, dc;
  t /= 3.;
  tp = floor (t / tLen) * vec2 (2.);
  t = mod (t, tLen);
  for (int k = 0; k < PLEN; k ++) {
    if (t >= tEnd[k] && t < tEnd[k + 1]) {
      cp = cPath[k];
      tt = 2. * (t - tEnd[k]) / (tEnd[k + 1] - tEnd[k]) - 1.;
      break;
    }
  }
  tp += cp.xy;
  if (abs (cp.w) == 1.5) {
    r = 1.5;
    dc = sqrt3;
    a = pi / 6.;
    tt *= sign (cp.w);
  } else {
    r = 0.5;
    dc = 1.;
    a = - sign (cp.w) * pi / 3.;
  }
  vd = Rot2D (vec2 (0., 1.), pi * cp.z);
  return (HexToPix (tp) + dc * vd - r * Rot2D (vd, tt * a)) * hgSizeB;
}

float EngDf (vec3 p, float dMin)
{  // (from "White Pass Route")
  vec3 q;
  float wRad, d, aw, sx, tw;
  tw = wlBase.x;
  wRad = 0.8;
  q = p;
  d = max (max (PrBoxDf (vec3 (q.xy, q.z + 1.75), vec3 (1., 1.4, 0.6)),
     - PrBoxDf (vec3 (q.xy, q.z + 1.95), vec3 (0.95, 1.3, 0.65))),
     - PrBox2Df (q.yz - vec2 (0.7, -1.75), vec2 (0.5, 0.4)));
  d = min (d, max (PrCapsDf (q - vec3 (0., -0.2, 0.5), 1., 2.), - (q.z + 1.2)));
  d = min (min (min (min (d, PrBoxDf (q - vec3 (0., -0.5, -3.15), vec3 (1., 0.7, 0.3))),
     PrBoxDf (q - vec3 (0., -1.3, -0.25), vec3 (1., 0.1, 3.2))),
     PrBoxDf (q - vec3 (0., -1.4, 3.), vec3 (1.1, 0.2, 0.07))),
     PrCylDf (vec3 (abs (q.x) - 0.6, q.y + 1.4, q.z - 3.1), 0.2, 0.1));
  DMINQ (idEng);
  d = max (PrCylDf (q - vec3 (0., -2.35, -1.77), 4., 0.7), max (1.4 - q.y, abs (q.x) - 1.35));
  DMINQ (idRf);
  d = PrCylDf ((q - vec3 (0., -1.4, -3.5)).xzy, 0.4, 0.03);
  DMINQ (idCpl);
  d = min (PrCylDf ((q - vec3 (0., 1.1, 2.)).xzy, 0.3, 0.5),
     PrCylDf ((q - vec3 (0., 1.5, 2.)).xzy, 0.4, 0.15));
  d = min (d, PrCapsDf ((q - vec3 (0., 0.8, 0.5)).xzy, 0.3, 0.2));
  DMINQ (idFun);
  q = p;
  q.yz -= vec2 (-0.2, 3.5);
  d = PrCylDf (q, 0.2, 0.2);
  DMINQ (idLamp);
  q = p; 
  q.xz = abs (q.xz);
  q -= vec3 (tw - 0.12, -1.4, wlBase.y);
  d = min (max (min (PrCylDf (q.zyx, wRad, 0.1), PrCylDf (q.zyx - vec3 (0.,0., -0.07),
     wRad + 0.05, 0.03)), - PrCylDf (q.zyx, wRad - 0.1, 0.12)), PrCylDf (q.zyx, 0.15, 0.10));
  q = p;
  q.x = abs (q.x);
  q -= vec3 (tw - 0.17, -1.4, sign (q.z * wlBase.y));
  aw = - trVel * tCur / (wRad * trSzFac);
  q.yz = Rot2D (q.yz, aw);  
  q.yz = Rot2D (q.yz, 2. * pi * floor ((atan (q.y, q.z) + pi) * 8. / (2. * pi) + 0.5) / 8.);
  q.z -= -0.5 * wRad;
  d = min (d, PrCylDf (q, 0.05, 0.5 * wRad));
  DMINQ (idWhl);
  q = p;
  sx = sign (q.x);
  q.x = abs (q.x);
  q.xy -= vec2 (tw + 0.08, -1.4);
  aw -= 0.5 * pi * sx; 
  q.yz -= 0.3 * vec2 (cos (aw), - sin (aw));
  d = PrCylDf (q, 0.04, 1.2);
  q.z = abs (q.z);
  q.xz -= vec2 (-0.1, wlBase.y);
  d = min (d, PrCylDf (q.zyx, 0.06, 0.15));
  DMINQ (idCrod);
  q = p;
  q.z = abs (q.z);
  q -= vec3 (0., -1.4, wlBase.y);
  d = PrCylDf (q.zyx, 0.1, tw - 0.1);
  DMINQ (idAxle);
  return dMin;
}

float CarDf (vec3 p, float dMin)
{
  vec3 q;
  float wRad, d, tw;
  wRad = 0.35;
  tw = wlBase.x;
  q = p;
  d = PrBoxDf (q, vec3 (1.3, 1.4, 2.8));
  DMINQ (idCar);
  d = max (PrCylDf (vec3 (q.x, q.y + 2.35, q.z), 4., 2.85), max (1.4 - q.y, abs (q.x) - 1.35));
  DMINQ (idRf);
  q = p;
  q.z = abs (q.z);
  q.yz -= vec2 (-1.4, 2.9);
  d = PrCylDf (q.xzy, 0.4, 0.03);
  DMINQ (idCpl);
  q = p;
  q.xz = abs (q.xz);
  q -= vec3 (tw - 0.1, -1.85, wlBase.y);
  d = min (min (PrCylDf (q.zyx, wRad, 0.1), PrCylDf (q.zyx - vec3 (0.,0., -0.07),
     wRad + 0.05, 0.03)), PrCylDf (q.zyx, 0.15, 0.10));
  q.x -= 0.1;
  d = max (d, - PrCylDf (q.zyx, 0.2, 0.05));
  DMINQ (idWhl);
  q = p;
  q.z = abs (q.z);
  q.yz -= vec2 (-1.85, wlBase.y);
  d = PrCylDf (q.zyx, 0.05, tw);
  DMINQ (idAxle);
  return dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  float dMin;
  dMin = dstFar / trSzFac;
  q = (p - carPos[0].xyz) / trSzFac;
  q.xz = Rot2D (q.xz, carPos[0].w);
  dMin = EngDf (q, dMin);
  for (int k = VAR_ZERO + 1; k < N_CAR; k ++) {
    q = (p - carPos[k].xyz) / trSzFac;
    q.xz = Rot2D (q.xz, carPos[k].w);
    dMin = CarDf (q, dMin);
  }
  return dMin * trSzFac;
}

vec4 TrnCol ()
{
  vec4 col4, carCol, carCol2;
  carCol = vec4 (0.1, 0.1, 1., 0.2);
  carCol2 = vec4 (0.7, 1., 0.7, 0.2);
  if (idObj == idEng || idObj == idCar) {
    col4 = mix (carCol2, carCol, smoothstep (0., 0.05, abs (abs (qHit.y + 0.25) - 0.1) - 0.02)) *
       (0.5 + 0.5 * step (-1.15, qHit.y));
  }
  if (idObj == idEng) {
    if (min (max (abs (abs (qHit.x) - 0.5) - 0.4, abs (qHit.y - 1.1) - 0.2),
       max (abs (abs (qHit.z - 1.) - 1.5) - 0.1,  - qHit.y - 1.1)) < 0.)
       col4 = vec4 (0.4, 0.4, 0.1, -1.);
    if (qHit.z > 3.1 && qHit.y < -1.) col4 = carCol;
    if (qHit.z < -2.8 && qHit.y > 0.1) col4 = vec4 (vec3 (0.01), 0.);
  } else if (idObj == idCar) {
    if (min (max (abs (abs (qHit.z) - 1.25) - 1., abs (qHit.y - 0.6) - 0.5),
       length (vec2 (qHit.xy - vec2 (0., 0.6))) - 0.4) < 0.) col4 = vec4 (0.4, 0.4, 0.1, -1.);
  } else if (idObj == idRf) {
    col4 = carCol2;
    if (length (qHit.xy - vec2 (0., -2.35)) > 3.97)
       col4 *= (0.8 + 0.2 * smoothstep (0., 0.05, abs (fract (4. * qHit.x + 0.5) - 0.5)));
  } else if (idObj == idFun) {
    col4 = ((qHit.y > 1.35) ? carCol : carCol2) * (0.5 + 0.5 * step (0.25,
       length (vec2 (qHit.xz - vec2 (0., 2.)))));
  } else if (idObj == idAxle) {
    col4 = vec4 (0.3, 0.3, 0.35, 0.);
  } else if (idObj == idWhl) {
    col4 = vec4 (0.6, 0.6, 0.7, 0.1);
  } else if (idObj == idCpl) {
    col4 = vec4 (0.3, 0.3, 0.3, 0.);
  } else if (idObj == idCrod) {
    col4 = vec4 (0.7, 0.7, 0.1, 0.1);
  } else if (idObj == idLamp) {
    col4 = (length (qHit.xy) < 0.16) ? ((mod (tCur, 2.) < 1.) ? vec4 (1., 1., 0.8, -1.) :
       vec4 (0.8, 0.8, 0.4, 0.)) : carCol2;
  }
  return col4;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

void BConf ()
{
  float h, cxy;
  cMidB = HexToPix (cIdB * hgSizeB);
  h = Hashfv2 (cIdB);
  if (Hashfv2 (17.11 * cIdB) > 0.4) {
    cDir = floor (3. * h) - 1.;
    cType = 3.;
  } else {
    cDir = 2. * floor (2. * h) - 1.;
    cType = 4.;
  }
  cxy = cIdB.x - cIdB.y;
  if (cxy == 0.) cType = 1.;
  else if (abs (cxy) == 1.) cType = mod (cIdB.x, 2.) + 1.;
#if P_LONG
  else if (cxy == -2. || cxy == -3. || cxy == 5.) cType = 2.;
  else if (cxy == 2. || cxy == 3. || cxy == 4.) cType = 1.;
  if (cType <= 2.) {
    if (cType == 1. && (cxy == 1. || cxy == 2. || cxy == 3.)) cDir = 0.;
    else if (cType == 1. && cxy == 4.) cDir = -1.;
    else if (cType == 2. && cxy == 5.) cDir = 1.;
    else cDir = 2. * mod (cIdB.x, 2.) - 1.;
  }
#else
  else if (abs (cxy) == 2. || cxy == -3.) cType = 2.;
  if (cType <= 2.) cDir = 2. * mod (cIdB.x, 2.) - 1.;
#endif
}

float GrndHt (vec2 p)
{
  float f, a, aSum;
  p *= 2.;
  f = 0.;
  a = 1.;
  aSum = 0.;
  for (int j = 0; j < 3; j ++) {
    f += a * Noisefv2 (p);
    aSum += a;
    a *= 0.4;
    p *= 2.5;
  }
  return 0.05 * f / aSum;
}

float BObjDf (vec3 p)
{
  vec3 q, cm3;
  vec2 ws;
  float dMin, d, dt, dWid, eWid, rc, ac, dh, r, h;
  dWid = 0.5;
  eWid = 0.2;
  dMin = dstFar;
  if (cType > 0.) {
    q = p;
    q.xz = (q.xz - cMidB) / hgSizeB;
    cm3 = TruchDist (q.xz);
    dt = hgSizeB * cm3.x;
    rc = cm3.y;
    ac = 18. * cm3.z;
    dh = hgSizeB * HexEdgeDist (q.xz);
    ws = vec2 (0.005, 0.01);
    if (cType <= 2. && rc != 0. && OnTrk (q.xz)) {
      d = PrRoundBox2Df (vec2 (dt - wlBase.x * trSzFac + ws.x, q.y - ws.y), ws, 0.003);
      DMIN (idRail);
    }
    h = gHt + GrndHt (p.xz) - 0.05;
    d = dot (vec2 (dt - dWid, h - q.y), sin (0.13 * pi + vec2 (0.5 * pi, 0.)));
    d = SmoothMin (q.y, SmoothMax (q.y - h, - d, 0.1), 0.1);
    d = SmoothMax (d, - max (PrBox2Df (vec2 (dh, q.y - gHt - 0.05),
       vec2 (eWid, 0.1)), 1.25 - dt), 0.05);
    DMIN (idGrnd);
    r = length (vec2 (dt, q.y + 1.)) - 1.8;
    d = max (abs (abs (r) - 0.1) - 0.025, abs (dh - 0.23) - 0.01);
    DMIN (idBrdg);
    d = max (abs (r + 0.1) - 0.01, abs (dh) - 0.23);
    DMIN (idBrdgP);
    if (rc != 0.) {
      d = min (length (vec2 (dt - dWid + 0.2, q.y - 0.2)) - 0.011,
         max (PrRoundBox2Df (vec2 (dt - dWid + 0.2, (fract (10. * rc * ac + 0.5) - 0.5) / 10.),
         vec2 (0.01, 0.003), 0.001), q.y - 0.2));
      DMIN (idFenc);
    }
  }
  return dMin;
}

float BObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, cIdP;
  float dHit, d, s, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSizeB / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSizeB;
  pM = HexToPix (PixToHex (ro.xz / hgSizeB));
  hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
  s = Minv3 (hv);
  cIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 320; j ++) {
    p = ro + dHit * rd;
    cIdB = PixToHex (p.xz / hgSizeB);
    if (cIdB != cIdP) {
      cIdP = cIdB;
      BConf ();
    }
    d = BObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 BObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = BObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float BObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 40; j ++) {
    p = ro + d * rd;
    cIdB = PixToHex (p.xz / hgSizeB);
    if (cIdB != cIdP) {
      cIdP = cIdB;
      BConf ();
    }
    h = BObjDf (p);
    sh = min (sh, smoothstep (0., 0.02 * d, h));
    d += max (h, 0.01);
    if (sh < 0.05 || d > hgSizeB) break;
  }
  return 0.5 + 0.5 * sh;
}

void SConf ()
{
  vec2 r;
  cMidS = HexToPix (cIdS * hgSizeS);
  r = Hashv2v2 (73. * cIdS + 1.1);
  tOff.xz = 0.2 * sqrt3 * hgSizeS * (0.5 + 0.5 * r.x) * sin (2. * pi * r.y + vec2 (0.5 * pi, 0.));
  tOff.y = r.x + r.y;
}

void SBConf ()
{
  vec2 s, u;
  u = cMidS + tOff.xz;
  cIdB = PixToHex (u / hgSizeB);
  BConf ();
  s = (u - cMidB) / hgSizeB;
  csOcc = (hgSizeB * min (TruchDist (s).x, HexEdgeDist (s)) < 1.1) ? 0. : 1.;
}

float SObjDf (vec3 p)
{
  vec3 q;
  float dMin, d, h;
  dMin = dstFar;
  if (csOcc > 0.) {
    q = p;
    q.xz = (q.xz - cMidS) / hgSizeS;
    q.xz -= tOff.xz;
    h = 0.15 + 0.15 * tOff.y;
    q.y -= h;
    d = PrCylDf (q.xzy, 0.05, h);
    DMIN (idTree);
    q.y -= h + 0.5;
    d = PrConCapsDf (q.xzy, sin (0.08 * pi + vec2 (0.5 * pi, 0.)), 0.17, 0.3);
    DMIN (idLeaf);
  }
  return dMin;
}

float SObjRay (vec3 ro, vec3 rd)
{
  vec3 vri, vf, hv, p;
  vec2 edN[3], pM, cIdP;
  float dHit, d, s, eps;
  if (rd.x == 0.) rd.x = 0.0001;
  if (rd.z == 0.) rd.z = 0.0001;
  eps = 0.001;
  edN[0] = vec2 (1., 0.);
  edN[1] = 0.5 * vec2 (1., sqrt3);
  edN[2] = 0.5 * vec2 (1., - sqrt3);
  for (int k = 0; k < 3; k ++) edN[k] *= sign (dot (edN[k], rd.xz));
  vri = hgSizeS / vec3 (dot (rd.xz, edN[0]), dot (rd.xz, edN[1]), dot (rd.xz, edN[2]));
  vf = 0.5 * sqrt3 - vec3 (dot (ro.xz, edN[0]), dot (ro.xz, edN[1]),
     dot (ro.xz, edN[2])) / hgSizeS;
  pM = HexToPix (PixToHex (ro.xz / hgSizeS));
  hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
  s = Minv3 (hv);
  cIdP = vec2 (-999.);
  dHit = 0.;
  for (int j = VAR_ZERO; j < 220; j ++) {
    p = ro + dHit * rd;
    cIdS = PixToHex (p.xz / hgSizeS);
    if (cIdS != cIdP) {
      cIdP = cIdS;
      SConf ();
    }
    SBConf ();
    d = SObjDf (p);
    if (dHit + d < s) {
      dHit += d;
    } else {
      dHit = s + eps;
      pM += sqrt3 * ((s == hv.x) ? edN[0] : ((s == hv.y) ? edN[1] : edN[2]));
      hv = (vf + vec3 (dot (pM, edN[0]), dot (pM, edN[1]), dot (pM, edN[2]))) * vri;
      s = Minv3 (hv);
    }
    if (d < eps || dHit > dstFar) break;
  }
  if (d >= eps) dHit = dstFar;
  return dHit;
}

vec3 SObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = SObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float SObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 cIdP;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  cIdP = vec2 (-999.);
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    cIdS = PixToHex (p.xz / hgSizeS);
    if (cIdS != cIdP) {
      cIdP = cIdS;
      SConf ();
    }
    SBConf ();
    if (csOcc > 0.) {
      h = SObjDf (p);
      sh = min (sh, smoothstep (0., 0.1 * d, h));
      d += h;
    } else d += 0.1 * hgSizeS;
    if (sh < 0.05 || d > 2. * hgSizeS) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyCol (vec3 rd)
{
  rd.y = abs (rd.y);
  return mix (vec3 (0.3, 0.35, 0.7), vec3 (0.8, 0.8, 0.8),
     clamp (2. * (Fbm2 (2. * rd.xz / rd.y + 0.1 * tCur) - 0.1) * rd.y, 0., 1.));
}

vec4 GroundCol (vec3 p, inout vec2 vf)
{
  vec4 col4, gCol;
  vec3 cm3;
  vec2 w;
  float dt, rc, ac;
  gCol = vec4 (0.6, 0.6, 0.5, 0.);
  col4 = vec4 (0., 0.7, 0., 0.) * (0.6 + 0.4 * Fbm2 (p.xz));
  vf = vec2 (32., 1.);
  w = (p.xz - cMidB) / hgSizeB;
  cm3 = TruchDist (w);
  dt = hgSizeB * cm3.x;
  if (dt > 1.22) col4 = mix (gCol, col4, smoothstep (0., 0.01, hgSizeB * HexEdgeDist (w) - 0.2));
  else if (dt < 0.19) {
    col4 = mix (col4, gCol, smoothstep (0., 0.03, 0.19 - dt));
    rc = cm3.y;
    ac = cm3.z;
    if (cType <= 2. && rc != 0. && OnTrk (w) && dt < 0.16 && step (0.35,
         abs (fract (36. * 6. * rc * ac + 0.5) - 0.5)) > 0.) col4 = vec4 (0.5, 0.4, 0.3, 0.);
  }
  if (trees) {
    cIdB = PixToHex (p.xz / hgSizeB);
    BConf ();
    cIdS = PixToHex (p.xz / hgSizeS);
    SConf ();
    SBConf ();
    if (csOcc > 0. && length ((p.xz - cMidS) / hgSizeS - tOff.xz) < 0.07) col4 *= 0.8;
  }
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, vn, rs;
  vec2 w, vf;
  float dstObj, dstObjB, dstObjS, sh, shGrnd;
  int idObjB, idObjS;
  vf = vec2 (0.);
  if (trees) {
    dstObjS = SObjRay (ro, rd);
    idObjS = idObj;
  } else dstObjS = dstFar;
  dstObjB = BObjRay (ro, rd);
  idObjB = idObj;
  dstObj = ObjRay (ro, rd);
  if (min (dstObjB, dstObjS) < dstObj) {
    if (dstObjB < dstObjS) {
      dstObj = dstObjB;
      idObj = idObjB;
    } else {
      dstObj = dstObjS;
      idObj = idObjS;
    }
  }
  shGrnd = 1.;
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    if (dstObj == dstObjS) {
      cIdS = PixToHex (ro.xz / hgSizeS);
      SConf ();
      SBConf ();
      vn = SObjNf (ro);
    } else if (dstObj == dstObjB) {
      cIdB = PixToHex (ro.xz / hgSizeB);
      BConf ();
      vn = BObjNf (ro);
    } else {
      vn = ObjNf (ro);
    }
    if (idObj == idFenc) {
      col4 = vec4 (0.9, 0.2, 0.3, 0.2);
    } else if (idObj == idBrdg) {
      col4 = vec4 (0.8, 0.3, 0.3, 0.2);
    } else if (idObj == idBrdgP) {
      col4 = vec4 (0.7, 0.7, 0.6, 0.);
      vf = vec2 (32., 1.);
    } else if (idObj == idRail) {
      col4 = vec4 (0.7, 0.7, 0.75, 0.1);
    } else if (idObj == idTree) {
      col4 = vec4 (0.5, 0.3, 0.1, 0.1);
    } else if (idObj == idLeaf) {
      col4 = mix (vec4 (0.4, 0.7, 0.1, 0.), vec4 (0.9, 0.9, 0., 0.1), step (0.7,
         Fbm3 (64. * ro.xzy)));
      vf = vec2 (16., 2.);
    } else if (idObj == idGrnd) {
      col4 = GroundCol (ro, vf);
      for (int k = VAR_ZERO; k < N_CAR; k ++) shGrnd = min (shGrnd, 0.5 +
         0.5 * smoothstep (-0.02, 0.02, PrBox2Df (Rot2D ((ro.xz - carPos[k].xz), carPos[k].w),
         vec2 (1.3, ((k == 0) ? 3.15 : 2.8) - 0.1) * trSzFac)));
    } else if (idObj >= idEng && idObj <= idLamp) {
      col4 = TrnCol ();
    }
    if (col4.a >= 0.) {
      rs = ro + 0.01 * vn;
      sh = min (ObjSShadow (rs, sunDir), BObjSShadow (rs, sunDir));
      sh = min (sh, shGrnd);
      if (trees) sh = min (sh, SObjSShadow (rs, sunDir));
      if (vf.x > 0.) vn = VaryNf (vf.x * ro, vn, vf.y);
      col = col4.rgb * (0.3 + 0.1 * max (dot (vn, sunDir * vec3 (-1., 1., -1.)), 0.) +
         0.7 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
      col = mix (col, SkyCol (rd), smoothstep (0.8, 1., dstObj / dstFar));
    } else {
      col = col4.rgb * (0.2 + 0.8 * max (- dot (rd, vn), 0.));
    }
  } else {
    col = SkyCol (rd);
  }
  return clamp (col, 0., 1.);
}

#define N_WIN  3

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col, vd;
  vec2 canvas, uv, uvv, mMid[N_WIN], ut[N_WIN], mSize, msw, pc[3];
  float el, az, zmFac, asp, sr, cGap, t;
  int vuId, regId;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 10.;
  asp = canvas.x / canvas.y;
  mSize = (1./5.) * vec2 (asp, 1.);
  mMid[0] = (1. - mSize.y) * vec2 (- asp, 1.);
  mMid[1] = (1. - mSize.y) * vec2 (asp, 1.);
  mMid[2] = (1. - mSize.y) * vec2 (asp, -1.);
  for (int k = 0; k < N_WIN; k ++) ut[k] = abs (uv - mMid[k]) - mSize;
  regId = -1;
  if (mPtr.z > 0.) {
    regId = 0;
    for (int k = 0; k < N_WIN; k ++) {
      msw = 2. * mPtr.xy - mMid[k] / vec2 (asp, 1.);
      if (Maxv2 (abs (msw)) < mSize.y) {
        regId = k + 1;
        msw /= 2. * mSize.y;
        break;
      }
    }
    if (regId == 0) msw = mPtr.xy;
  }
  vuId = 0;
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      vuId = k + 1;
      break;
    }
  }
  if (regId > 0 && (vuId == 0 || vuId == regId)) vuId = regId - vuId;
  hgSizeB = 8.;
  hgSizeS = 1.;
  gHt = 0.2;
  trSzFac = 0.1;
  SetPath ();
  trees = true;
  cGap = 2.3 * trSzFac;
  wlBase = vec2 (1.3, 1.1);
  trVel = 0.5;
  az = 0.;
  el = 0.;
  if (mPtr.z > 0. && vuId == regId) {
    az += 2. * pi * msw.x;
    el += 0.5 * pi * msw.y;
  }
  for (int k = VAR_ZERO; k < N_CAR; k ++) {
    t = tCur * trVel - (float (k) + ((k > 0) ? 0.05 : 0.)) * cGap;
    for (int j = VAR_ZERO; j < 3; j ++)
       pc[j] = EvalPPos (t + ((j > 0) ? sign (float (j) - 1.5) * wlBase.y * trSzFac : 0.));
    carPos[k].xz = pc[0];
    carPos[k].y = 2.4 * trSzFac;
    vd.xy = pc[2] - pc[1];
    carPos[k].w = 0.5 * pi - atan (vd.y, vd.x);
  }
  t = tCur * trVel;
  if (vuId == 0 || vuId == 3) {
    ro.xz = EvalPPos (t - float ((vuId == 3) ? -3 : N_CAR + 2) * cGap);
    ro.y = 0.8;
    vd.xy = normalize (EvalPPos (t - float (N_CAR - ((vuId == 3) ? 5 : 0)) * cGap) - ro.xz);
    az += atan (vd.y, - vd.x) - 0.5 * pi;
    el -= 0.06 * pi;
    el = clamp (el, -0.2 * pi, 0.15 * pi);
    zmFac = 3.;
  } else if (vuId == 1) {
    ro = vec3 (0., 40., (-3. * sqrt3 + (2. / tLen) * t) * hgSizeB);
    ro.xz = Rot2D (ro.xz, - pi / 3.);
    ro.xz += 0.01;
#if P_LONG
    ro.x += 1.6 * hgSizeB;
#else
    ro.x -= 1.6 * hgSizeB;
#endif
    az += pi / 3.;
    el -= 0.3 * pi;
    el = clamp (el, -0.4 * pi, -0.2 * pi);
    zmFac = 3.;
  } else if (vuId == 2) {
    ro = vec3 (0., 20., (-1.5 * sqrt3 + (2. / tLen) * t) * hgSizeB);
    ro.xz = Rot2D (ro.xz, - pi / 3.);
    ro.xz += 0.01;
    ro.x -= 1.5 * hgSizeB;
    vd = normalize (carPos[1].xyz - ro);
    az = atan (vd.z, - vd.x) - 0.5 * pi;
    el = asin (vd.y);
    zmFac = 30.;
  }
  vuMat = StdVuMat (el, az);
  dstFar = 16. * hgSizeB;
  sunDir = normalize (vec3 (0., 1.5, -1.));
  sunDir.xz = Rot2D (sunDir.xz, - pi / 3.);
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    uvv = (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) *
       pi)) / zmFac;
    rd = vuMat * normalize (vec3 (2. * tan (0.5 * atan (uvv.x / asp)) * asp, uvv.y, 1.));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  for (int k = 0; k < N_WIN; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 3.) col = vec3 (0.7, 0.7, 0.3);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrConCapsDf (vec3 p, vec2 cs, float r, float h)
{
  float d;
  d = max (dot (vec2 (length (p.xy) - r, p.z), cs), abs (p.z) - h);
  h /= cs.x * cs.x;
  r /= cs.x;
  d = min (d, min (length (vec3 (p.xy, p.z + r * cs.y - h)) - r + h * cs.y,
     length (vec3 (p.xy, p.z + r * cs.y + h)) - r - h * cs.y));
  return d;
}

vec2 PixToHex (vec2 p)
{
  vec3 c, r, dr;
  c.xz = vec2 ((1./sqrt3) * p.x - (1./3.) * p.y, (2./3.) * p.y);
  c.y = - c.x - c.z;
  r = floor (c + 0.5);
  dr = abs (r - c);
  r -= step (dr.yzx, dr) * step (dr.zxy, dr) * dot (r, vec3 (1.));
  return r.xz;
}

vec2 HexToPix (vec2 h)
{
  return vec2 (sqrt3 * (h.x + 0.5 * h.y), (3./2.) * h.y);
}

float HexEdgeDist (vec2 p)
{
  p = abs (p);
  return (sqrt3/2.) - p.x + 0.5 * min (p.x - sqrt3 * p.y, 0.);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

float Minv3 (vec3 p)
{
  return min (p.x, min (p.y, p.z));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b - h * r, a, h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

float Hashfv2 (vec2 p)
{
  return fract (sin (dot (p, vec2 (37., 39.))) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  return fract (sin (dot (p, cHashVA3) + vec4 (0., cHashVA3.xy, cHashVA3.x + cHashVA3.y)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++)
     v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define CLAMP_TO_EDGE

vec4 sumToPoint(ivec2 p) {
    if (p.x <= 0 || p.y <= 0) {
        return vec4(0.0);
    }

    return texelFetch(iChannel0, min(p, ivec2(iResolution.xy)) - 1, 0);
}

vec4 sumOverRect(ivec2 a, ivec2 b) {
#ifdef CLAMP_TO_EDGE
    ivec2 z = ivec2(0);
    ivec2 r = ivec2(iResolution.xy);
    return 
        + (a.x < 0 ? (sumToPoint(ivec2(1, b.y)) - sumToPoint(ivec2(1, a.y))) * float(min(b.x, 0) - a.x) : vec4(0.0))
        + (b.x > r.x ? (sumToPoint(ivec2(r.x, b.y)) - sumToPoint(ivec2(r.x - 1, b.y)) - sumToPoint(ivec2(r.x, a.y)) + sumToPoint(ivec2(r.x - 1, a.y))) * float(b.x - max(a.x, r.x)) : vec4(0.0))
        + (a.y < 0 ? (sumToPoint(ivec2(b.x, 1)) - sumToPoint(ivec2(a.x, 1))) * float(min(b.y, 0) - a.y) : vec4(0.0))
        + (b.y > r.y ? (sumToPoint(ivec2(b.x, r.y)) - sumToPoint(ivec2(b.x, r.y - 1)) - sumToPoint(ivec2(a.x, r.y)) + sumToPoint(ivec2(a.x, r.y - 1))) * float(b.y - max(a.y, r.y)) : vec4(0.0))
        + (a.x < 0 && a.y < 0 ? sumToPoint(ivec2(1)) * float((min(b.x, 0) - a.x) * (min(b.y, 0) - a.y)) : vec4(0.0))
        + (a.x < 0 && b.y > r.y ? (sumToPoint(ivec2(1, r.y)) - sumToPoint(ivec2(1, r.y - 1))) * float((min(b.x, 0) - a.x) * (b.y - max(a.y, r.y))) : vec4(0.0))
        + (b.x > r.x && a.y < 0 ? (sumToPoint(ivec2(r.x, 1)) - sumToPoint(ivec2(r.x - 1, 1))) * float((b.x - max(a.x, r.x)) * (min(b.y, 0) - a.y)) : vec4(0.0))
        + (b.x > r.x && b.y > r.y ? (sumToPoint(r) - sumToPoint(ivec2(r.x - 1, r.y)) - sumToPoint(ivec2(r.x, r.y - 1)) + sumToPoint(r - 1)) * float((b.x - max(a.x, r.x)) * (b.y - max(a.y, r.y))) : vec4(0.0))
        + sumToPoint(clamp(b, z, r))
        - sumToPoint(clamp(ivec2(a.x, b.y), z, r))
        - sumToPoint(clamp(ivec2(b.x, a.y), z, r))
        + sumToPoint(clamp(a, z, r));
#else
    return sumToPoint(b) - sumToPoint(ivec2(a.x, b.y)) - sumToPoint(ivec2(b.x, a.y)) + sumToPoint(a);
#endif
}

vec4 avgOverRect(ivec2 a, ivec2 b) {
    return sumOverRect(a, b) / float((b.x - a.x) * (b.y - a.y));
}

