void mainImage(out vec4 fragColor, vec2 fragCoord) {
    ivec2 p = ivec2(fragCoord);

    if (iFrame == 0) {
        fragColor = pow(texelFetch(iChannel0, p % textureSize(iChannel0, 0), 0), vec4(2.2));
        return;
    }

    int numXSteps = int(ceil(log2(iResolution.x)));
    int numYSteps = int(ceil(log2(iResolution.y)));
    fragColor = texelFetch(iChannel1, p, 0);

    if (iFrame > numXSteps + numYSteps) {
        return;
    }

    ivec2 s = p;

    if (iFrame <= numXSteps) {
        s.x -= 1 << iFrame - 1;
    } else {
        s.y -= 1 << iFrame - numXSteps - 1;
    }

    if (s.x >= 0 && s.y >= 0) {
        fragColor += texelFetch(iChannel1, s, 0);
    }
}

