vec2 conj (vec2 a) { return vec2(a.x,-a.y); }
vec2 cmul (vec2 a, vec2 b) { return a.x*b+a.y*conj(b).yx; }

vec2 nthharmonic(vec2 z, int n){
  vec2 v=vec2(1,0);
  for (int k=0;k<10;k++){ // for n<1024
    if(0<(n&1)) v=cmul(v,z);
    z=cmul(z,z);
    n>>=1;
  }
  return v;
}

