// borrowed hash from https://www.shadertoy.com/view/llGSzw
vec3 hash3( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec3 k = n * uvec3(n,n*16807U,n*48271U);
    return vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pixel = 1.5 / iResolution.y;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0);
    float y = 0.0;
    for (int i = num_lines + 8; i >= 0; --i) {
        vec3 r = hash3(uint(i + 101));
        float f = 12.0 * r.x + 4.0;
        float a = (r.y * 0.25 + 0.05) * 16.0 / (f + 8.0);
        float t = 0.5 * (r.z + 0.1) * (iTime + 2.0);
        float w = sin(uv.x * f + t) * a;
        y = w - y * 0.5;
        if (i < num_lines) {
            float cutoff = float(i) + uv.x * 0.8;
            if (iTime < cutoff * 2.0) continue;
            float thickness = 0.008 * length(vec2(dFdx(y / pixel), 1.0));
            float alpha = 1.0 - smoothstep(thickness, thickness + pixel, abs(uv.y - 0.5 - y));
            vec4 over = vec4(palette(i), alpha);
            col += (over - col) * over.a;
        }
    }
    // Output to screen
    fragColor = col;
}

