#define C2 vec3(255.,195.,0.)/255.

struct noiseSpecs {
  float n1;
  vec3 n1Color;
  float n2;
  vec3 n2Color;
  float n3;
  vec3 n3Color;
  float nFinal;
  vec3 nFinalColor;
};

noiseSpecs getNoise(vec2 uv, float t) {
  noiseSpecs o;
  float tseed = t; //sin(t*.5) ; //floor(t*0.1);
  float seed  = tseed;
  float seed2 = tseed + 373.297;
  float seed3 = tseed + 793.713;
  float seed4 = tseed + 127.139;
  float seed5 = tseed + 929.197;
#ifdef VERTICAL
  uv = vec2(uv.y, uv.x);
#endif
  vec2 roll = -0.05*t*vec2(1.);
  // uv += roll;

  float v = 0.;
  float v1 = 0.;
  float v2 = 0.;
  float v3 = 0.;

  vec2 ouv = uv;
  vec2 nUV = ouv * .9;

  float tmpZoom = 1.;//7.;
  float bv1_turb = ncos(nsimplex(nUV*9., seed) * 50.);
  bv1_turb = bv1_turb*.2 + .8/2.;

  //float bv2_a = nturb2Simplex(uv*4./tmpZoom, seed2);
  float bv2_a_fbm = nfbmCellular(nUV*4./tmpZoom, seed3);
  float bv2_a = ncos(bv2_a_fbm*4.);

  //float bv2_b = nturb2Simplex(uv*.5/tmpZoom, seed3);
  float bv2_b_fbm = nfbmCellular(nUV/tmpZoom, seed2);
  float bv2_b = ncos(bv2_b_fbm*10.);

  float bv2 = sat(bv2_a*.6 + bv2_b*.6);
  bv2 = mix(.15, .45, bv2);

  float bv3_fbm = nfbmCellular(nUV*15./tmpZoom, seed);
  float bv3 = smoothstep(.0, .8, bv3_fbm);

  float final = bv1_turb*0.2 + bv2*0.8;
  final = mix(final*.3, final, bv3);
  final = smoothstep(.0, .5, final);
  final = mix(.15, .45, final);

  float eyeballrad = .17;
  float blackrad = .09;
  vec2 pt1 = vec2(-.12, .0);
  float pt1dist = length(uv - pt1);
  vec2 pt2 = vec2(.12, .0);
  float pt2dist = length(uv - pt2);

  float dist = min(pt1dist, pt2dist);
  final *= (1.-step(eyeballrad, dist)) * (1.-smoothstep(eyeballrad-.1, eyeballrad, dist));

  float turb = nturbSimplex(uv*20., seed*10.);
  final *= mix(step(blackrad, dist+denorm(turb)*.04), 1., smoothstep(blackrad-.07, blackrad, dist));

  o.n1 = bv1_turb;
  o.n2 = bv2;
  o.n3 = bv3;
  o.nFinal = final;

  uv = ouv;
  float c1_fbm = nfbmCellular(uv*4., seed4*20.);
  c1_fbm = smoothstep(.0, 1., c1_fbm);
  vec3 c1 = mix(C1, WHI, .8);

  float c2_fbm = nfbmCellular(uv*2., seed5);
  c2_fbm = smoothstep(.45, .55, c2_fbm);


  //float c2_fbm = nfbmCellular(uv*15., seed4);
  //c2_fbm = smoothstep(.0, .7, c1_fbm);
  vec3 c2 = mix(C2, WHI, .4);
  vec3 c2strong = mix(C2, WHI, .15)*.9;
  vec3 c2weak = mix(C2, WHI, .7)*.9;

  float sideMix = 1.-smoothstep(-.05, .05, uv.x);
  vec3 ca = mix(c2, c2weak, sideMix);
  vec3 cb = mix(c1, c2strong, sideMix);

  o.n1Color = WHI;
  o.n2Color = WHI;
  o.n3Color = WHI;
  o.nFinalColor = mix(ca, cb, c1_fbm);

  return o;
}


