const float n_tau = 6.2831;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_mouse_coords_normalized = iMouse.xy / iResolution.xy; 
    vec2 a_n_pixel_coords_normalized = fragCoord.xy / iResolution.xy; 
    float n_amp = 0.3;
    float n_y = sin(a_n_pixel_coords_normalized.x * n_tau) * n_amp + 0.5;

    float n_distance_to_sine = length(
        a_n_pixel_coords_normalized
        -
        vec2(
            a_n_pixel_coords_normalized.x,
            n_y
        )
    );

    float n_brightness = n_distance_to_sine; 
    // now we can do modulo
    float n_mod_max = 0.1;
    n_brightness = mod(n_brightness, n_mod_max);
    // now we can also normalize it
    n_brightness = n_brightness / n_mod_max;

    fragColor = vec4(n_brightness);

}

