float f_n_distance(
    vec2 a_n_start, 
    vec2 a_n_end
){
    vec2 a_n_delta = vec2(
        a_n_start.x - a_n_end.x, 
        a_n_start.y - a_n_end.y
    );
    float n_distance_square = 
        pow(a_n_delta.x,2.0) + 
        pow(a_n_delta.y,2.0);

    float n_distance_sqrt = sqrt(n_distance_square);

    return n_distance_sqrt;
}

