vec4 f_a_n_blurred(
    float n_scale_x_kernel, 
    float n_scale_y_kernel, 
    float n_x_kernel, 
    float n_y_kernel
){

    vec4 a_n_rgba_sum = vec4(0.0,0.0,0.0,0.0);
    int n_x = 0; 
    int n_y = 0; 
    for(int n_y; n_y < int(n_scale_y_kernel); n_y+=1){
        for(int n_x; n_x < int(n_scale_x_kernel); n_x+=1){
            //int n_x_current = n_x - int(float(n_scale_x_kernel)/2.0);
            //int n_x_current = n_y - int(float(n_scale_y_kernel)/2.0);
            vec2 n_coords_normalized_kernel = vec2(
                float(n_x) + n_x_kernel - float(n_scale_x_kernel)/2.0, 
                float(n_y) + n_y_kernel - float(n_scale_y_kernel)/2.0
            )/iResolution.xy;
            a_n_rgba_sum+=texture(
                iChannel0,
                n_coords_normalized_kernel 

            );
        }
    }
    return a_n_rgba_sum / (n_scale_x_kernel * n_scale_y_kernel);
}
vec4 f_a_n_blurred_ignore_out_of_bounds(
    float n_scale_x_kernel, 
    float n_scale_y_kernel, 
    float n_x_kernel, 
    float n_y_kernel
){

    vec4 a_n_rgba_sum = vec4(0.0,0.0,0.0,0.0);
    int n_x = 0; 
    int n_y = 0; 
    int n_count =0;
    for(int n_y; n_y < int(n_scale_y_kernel); n_y+=1){
        for(int n_x; n_x < int(n_scale_x_kernel); n_x+=1){
            float n_x_target = float(n_x) + n_x_kernel - float(n_scale_x_kernel)/2.0;
            float n_y_target = float(n_y) + n_y_kernel - float(n_scale_y_kernel)/2.0;
            if(
                n_x_target >= 0.0  && n_x_target <= float(iResolution.x
)                &&
                n_y_target >= 0.0  && n_y_target <= float(iResolution.y)

            ){
                //int n_x_current = n_x - int(float(n_scale_x_kernel)/2.0);
                //int n_x_current = n_y - int(float(n_scale_y_kernel)/2.0);
                vec2 n_coords_normalized_kernel = vec2(n_x_target, n_y_target)/iResolution.xy;
                a_n_rgba_sum+=texture(
                    iChannel0,
                    n_coords_normalized_kernel 

                );
                n_count +=1;
            }

        }
    }
    return a_n_rgba_sum / float(n_count);
}
vec4 f_a_n_blurred_ignore_out_of_bounds_gaussian(
    float n_scale_x_kernel, 
    float n_scale_y_kernel, 
    float n_x_kernel, 
    float n_y_kernel
){
    float n_distance_to_kernel_max = f_n_distance(
        0.0, 
        0.0, 
        n_scale_x_kernel, 
        n_scale_y_kernel
    );

    vec2 n_coords_kernel_normalized = vec2(n_x_kernel, n_y_kernel) / iResolution.xy;
    vec4 a_n_rgba_sum = vec4(0.0,0.0,0.0,0.0);
    int n_x = 0; 
    int n_y = 0; 
    int n_count =0;
    for(int n_y; n_y < int(n_scale_y_kernel); n_y+=1){
        for(int n_x; n_x < int(n_scale_x_kernel); n_x+=1){
            float n_x_target = float(n_x) + n_x_kernel - float(n_scale_x_kernel)/2.0;
            float n_y_target = float(n_y) + n_y_kernel - float(n_scale_y_kernel)/2.0;
            if(
                n_x_target >= 0.0  && n_x_target <= float(iResolution.x
)                &&
                n_y_target >= 0.0  && n_y_target <= float(iResolution.y)

            ){
                vec2 n_coords_target_normalized = vec2(n_x_target, n_y_target) / iResolution.xy;
                float n_distance_to_center = f_n_distance(
                    n_coords_target_normalized.x, 
                    n_coords_target_normalized.y, 
                    n_coords_kernel_normalized.x, 
                    n_coords_kernel_normalized.y
                );

                vec2 n_coords_normalized_kernel = vec2(n_x_target, n_y_target)/iResolution.xy;
                a_n_rgba_sum+=(texture(
                    iChannel0,
                    n_coords_normalized_kernel 
                ))*(1.0-(n_distance_to_center/n_distance_to_kernel_max));
                n_count +=1;
            }

        }
    }
    return a_n_rgba_sum / float(n_count);
}
