/* @kishimisu - 2022 

   Playing with reflection and refraction */

#define AA 2

#define PI 3.141592
#define rot(a) mat2(cos(a), -sin(a), sin(a), cos(a))
#define impulse(freq, off) pow(smoothstep(0., .5, sin(off+(iTime-4.5)*freq)*.5+.5), 10.)

float sdBox(vec3 p, vec3 s) {
  vec3 q = abs(p) - s;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0)-.0;
}

// x: distance to box
// y: distance to shape
// z: min absolute distance to scene
vec3 map(vec3 p) {
    float box = sdBox(p, vec3(.9, 1.5, .9)) - .05;

    // animations
    p.xy *= rot(impulse(.3,PI)*3.14); // xy rotation
    p.xz *= rot(impulse(.2,0.)*1.57); // xz rotation
    p = abs(abs(p) - impulse(1.,PI)*.4); // first domain rep
    p = mix(p, abs(p*2. - .3), impulse(.5,0.)); // second domain rep
    float shape = mix(length(p) - .25, sdBox(p, vec3(.25)), impulse(2., 0.))*.5; // box/sphere

    shape = max(box, shape);
    return vec3(box, shape, min(abs(box), shape));
}

vec3 normal(vec3 p) {
    const float h = 0.0001;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ).x + k.yyx*map( p + k.yyx*h ).x + 
                      k.yxy*map( p + k.yxy*h ).x + k.xxx*map( p + k.xxx*h ).x );
}

// refract, or reflect if total internal refraction
vec3 rrefract(vec3 rd, vec3 n, float ior) {
    vec3   r = refract(rd, n, ior);
    return r == vec3(0.) ? reflect(rd, n) : r;
}

void initRayOriginAndDirection(vec2 fragCoord, inout vec3 ro, inout vec3 rd) {
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy*2.-1.;

    float z = iMouse.z > 0. ? 1. : smoothstep(0., 6., iTime);
    ro = vec3(0., 0., 4.2); 
    ro.yz *= rot(iMouse.z > 0. ? m.y*1.5 : -1.5 + mix(0.5, 1.85, z));
    ro.zx *= rot(m.x*2.+iTime*.2);

    vec3 f = normalize(vec3(0.,.2,0.)-ro), r = normalize(cross(vec3(0,1,0), f));
    rd = normalize(f + uv.x*r + uv.y*cross(f,r));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {                  
    vec3 tot; for (int j = 0; j < AA; j++) for (int k = 0; k < AA; k++) { // antialiasing code 
    vec2 o = vec2(float(j), float(k)) / float(AA) - 0.5;

    vec3 ro, rd;
    initRayOriginAndDirection(fragCoord+o/2., ro, rd);

    vec3 bgColor = mix(vec3(.17,.0,.16), vec3(0.,0.,.12), rd.y-.3)*.8;
    vec3 tintColor = vec3(1.,.5,.5) + .07*sin(vec3(1.4,.54,1.23)*iTime*.3);

    vec3 p = ro, color = bgColor;
    float i, t, inside, entry, fresnel;
    const float ior = 1.7;

    for (i = 0.; i < 150.; i++) {
        vec3 d = map(p);

        if (d.y < .001) {
            // sphere hit
            color = tintColor;
            break;
        }

        p += d.z*rd;
        t += d.z;

        if (inside < .5) {
            if (d.x < .001) { 
                // first box hit
                vec3 n = normal(p);
                fresnel = pow(1. - clamp(-dot(rd, n), 0., 1.), 4.);
                rd = rrefract(rd, n, 1./ior);
                p += rd*.04;
                entry = t;
                inside++;
            }
        } else {
            if (d.x > -.001) {
                // inside box bounces
                vec3 n = normal(p);
                rd = reflect(rd, n);
                p += rd*.1;
                inside++;
            }       

        }
        if (inside > 6. || t > 15.) break;
    }

    if (inside > 0.) {
        float att = exp(-(t-entry)*.3);
        color = mix(bgColor*.7, color - vec3(.5,.5,2.)*fresnel, exp(-i*.005) * att);
    }
    tot += color;

} tot /= float(AA*AA); // antialiasing code
  fragColor.rgb = tot;
}

vec3 hsv2rgb(float h, float s, float v) {
    vec3 h3 = mod(6.0 * h + vec3(5.0, 3.0, 1.0), 6.0);
    h3 = min(h3, 4.0 - h3);
    h3 = clamp(h3, 0.0, 1.0);
    return pow(v - v * s * h3, vec3(1.0/2.4));
}

vec3 palette(int i) {
    float f = float(i);
    float h =  mod(1.618033988749894848204586834 * f, 1.0);
    float s = exp(-0.00025 * f) * 0.65 + 0.25;
    float v = 1.0;
    return hsv2rgb(h, s, v);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float r = floor(uv.x * 6.0) / 7.1;
    float t = 3.0 + 12.0 * (0.5 + 0.5 * cos(iTime * r));
    float u = sin(iTime * r) * 8.0;

    int i = int(floor(uv.y * t + u));

    vec3 col = palette(i);
    //col = hsv2rgb(uv.y, uv.x, 1.0);


    // Output to screen
    fragColor = vec4(col,1.0);
}

// ---------------------------------------------------------------------------------------
//	Created by fenix in 2022
//	License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
//  The third in my series of attempts at cloth shaders, see also: 
//
//      Tearable 3D Fishnet       https://www.shadertoy.com/view/NlKBW3
//      Cloth Draping + Shadows   https://www.shadertoy.com/view/cdjGRR
//
//  Texture borrowed from jarble's beautiful:
//
//      Fractal mosaic 30 (music) https://www.shadertoy.com/view/csl3zl
//
//  I got this new rendering method to work, but it has some major limitations. I
//  just barely managed to cobble together (what I hope is) an interesting-looking
//  shader despite them.
//
//  I have not seen a cloth renderer like this before...this is my own idea. The
//  concept is to take the voronoi closest particles as "guesses" to the correct
//  quad to render for a particular pixel. Then we refine that guess by searching
//  over the cloth mesh. This way the voronoi doesn't even need to be very good
//  because as long as the cloth is flat towards the screen, we can find the
//  correct quad with just a few iterations.
//
//  The huge problem is that findQuad fails to find a good quad whenever there
//  are a lot of verties bunched up on one pixel. This happens all the time as the
//  cloth drapes over objects, so I had to greatly limit the amount the balls push
//  into the cloth, and the angle of both the camera and the light. To see the problems,
//  makes the balls bigger with BALL_SIZE or move them deeper into the cloth by
//  hacking the opRepSphere call in the scene function.
//
//  I don't know if there is a way to rescue this rendering approach with a smarter
//  search strategy, or if I should be going in another direction. I know about
//  archee's quadtree method
//
//      curtain and ball          https://www.shadertoy.com/view/MldXWX
//
//  and I like how it doesn't need a voronoi buffer. I suppose I could just copy
//  his approach but I would like to think there is more than one good way to render
//  cloth.
//
//  For fun you can put whatever texture you want (e.g. a video) onto the cloth by
//  modifying the colorMap function.
//
//  Buffer A computes the positions of cloth vertices
//  Buffer B computes nearest particles to each screen pixel from the camera's POV
//  Buffer C computes nearest particles to each screen pixel from the light's POV
//  Buffer D computes the depth of the cloth from the light's perspective, for shadows
//
// ---------------------------------------------------------------------------------------

vec3 light(vec3 n, vec3 pointLightDir, bool shadow)
{
    return vec3(abs(dot(n, FILL_LIGHT_DIR)) *.25) + float(!shadow) * max(0., dot(n, pointLightDir)) * .75;
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

float rayMarch(vec3 ro, vec3 rd, vec4 state)
{
	float d = 0.;

    for(int i = 0; i < MAX_STEPS; i++)
    {
    	vec3 p = ro + rd * d;
        float dS = scene(p, state);
        d += dS;
        if(d > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return d;
}

bool shadowScene(vec3 p, vec3 lightDir, mat4 w2l, vec4 state)
{
    float sd = rayMarch(POINT_LIGHT_POS, -lightDir, state);
    vec3 sp = POINT_LIGHT_POS - lightDir * sd;
    return dot(p - sp, lightDir) < -0.1;
}

bool shadowCloth(vec3 p, vec3 lightDir, mat4 w2l, vec4 state)
{
    vec3 s = screen(p, w2l);
    float sz = texture(iChannel2, s.xy * .5 * vec2(iResolution.y / iResolution.x, 1) + .5).w;

    return sz < s.z;
}

// Featuring: Fractal mosaic 30 (music) by jarble
// https://www.shadertoy.com/view/csl3zl
vec2 triangle_wave(vec2 a)
{
    vec2 a2 = vec2(1.,0.5),
    a1 = a-a2;
    return abs(fract((a1)*(a2.x+a2.y))-.5);
}

vec3 fractalMosaic( in vec2 uv )
{
    uv = uv - .5;
    uv = abs(uv);
    vec3 col = vec3(0.);
    float t1 = 2.;
    vec2 t2 = vec2(0.);

    for(int k = 0; k < 5; k++)
    {    
        uv = abs(.5*sign(uv.y-uv.x)+uv+t2);
        t2 = -triangle_wave(uv-.5*sign(uv.y-uv.x)) ;
        uv = t2-triangle_wave(uv.yx);
        float c1 = abs(uv.x-uv.y);
        col = col.yzx;
        if(uv.y < uv.x) col = vec3(col.yz,c1);
    }

    return min(col,vec3(1.));
}

vec3 colorMap(vec2 uv)
{
    //return uv.xyy; // check uv
    //return texture(iChannel3, vec2(0, 1) + uv * vec2(1, -1)).xyz; // video
    return fractalMosaic(uv)*1.4 + 0.1;
}

bool drawQuad(int id, vec2 bc, fxParticle ul, fxParticle ur, fxParticle ll, fxParticle lr, mat4 w2c, mat4 w2l, vec4 state, vec2 p, vec3 cameraPos, vec3 cameraFwd, inout vec4 fragColor)
{
    vec2 uv = vec2(float(id % g_ClothSideLen) + bc.y, float(id / g_ClothSideLen) - bc.x) / float(g_ClothSideLen);
    fxParticle a, b, c;
    if (bc.x + bc.y > 1.)
    {
        a = ll; b = ul; c = lr;
    }
    else
    {
        a = ur; b = lr; c = ul; bc = 1. - bc;
    }

    vec3 pp = a.pos + bc.x * (b.pos - a.pos) + bc.y * (c.pos - a.pos);
    vec3 ps = screen(pp, w2c);

    if (all(lessThanEqual(bc, vec2(1))) && all(greaterThanEqual(bc, vec2(0)))
        && ps.z < fragColor.w + 0.03)
    {
        vec3 norm = normalize(a.norm * (1. - bc.x - bc.y) + b.norm * bc.x + c.norm * bc.y);
        vec3 color = colorMap(uv);
        //vec3 color = vec3(((id % g_ClothSideLen) % 2) ^ ((id / g_ClothSideLen) % 2)); // checkered

        if (dot(norm, cameraFwd) < 0.) color = color.bgr;
        vec3 lightDir = normalize(POINT_LIGHT_POS - pp);
        bool sh = shadowScene(pp, lightDir, w2l, state);
        fragColor = vec4(color * light(norm, normalize(pp - POINT_LIGHT_POS - vec3(10, 0, 0)), sh), ps.z);
        return true;
    }

    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    initClothExtents(iResolution);

	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    // set up ray for marching
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    vec3 cameraPos = fxCalcCameraPos(state), cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(LOOK_AT, cameraPos, cameraFwd, cameraLeft, cameraUp);
    vec3 rayDir = fxCalcRay(fragCoord, iResolution, cameraFwd, cameraUp, cameraLeft);

    mat4 w2c = fxCalcCameraMat(iResolution, cameraLeft, cameraUp, cameraFwd, cameraPos);

    fragColor = vec4(0, 0, 0, MAX_DIST);

    // march
    float d = rayMarch(cameraPos, rayDir, state);

    // process march results
    vec3 hit = cameraPos + rayDir * d;
    vec3 n = getNormal(hit, state);
    vec3 lightDir = normalize(POINT_LIGHT_POS - hit);

    // set up light matrix
    vec3 lightFwd, lightLeft, lightUp;
    fxCalcCamera(LOOK_AT, POINT_LIGHT_POS, lightFwd, lightLeft, lightUp);
    mat4 w2l = fxCalcCameraMat(iResolution, lightLeft, lightUp, lightFwd, POINT_LIGHT_POS);

    // compute shadows
    bool s = shadowScene(hit, lightDir, w2l, state) || shadowCloth(hit, lightDir, w2l, state);

    // render background color
    fragColor = vec4(light(n, lightDir, s), d);

    // render cloth
    ivec4 old = fxGetClosest(iChannel1, ivec2(fragCoord));      

#define RENDER_ONLY_CLOSEST_NEIGHBOR 1 // more artifacts, but faster

    // find closest of the nearby vertices
    int closest = -1;
    float dist = 1e6;
    for(int i = 0; i < 4; ++i)
    {
        int id = old[i];
        if (id < 0 || id >= g_MaxParticles) continue;
#if RENDER_ONLY_CLOSEST_NEIGHBOR
        fxParticle data = fxGetParticle(id);
        vec3 s = screen(data.pos, w2c);
        if (s.z < dist)
        {
            closest = id;
            dist = s.z;
        }
#else
        fxParticle ul, ur, ll, lr; vec2 bc;
        findQuad(id, bc, ul, ur, ll, lr, w2c, p, cameraPos, cameraFwd, fragColor);
        if (drawQuad(id, bc, ul, ur, ll, lr, w2c, w2l, state, p, cameraPos, cameraFwd, fragColor)) break;
#endif
    }

#if RENDER_ONLY_CLOSEST_NEIGHBOR
    fxParticle ul, ur, ll, lr; vec2 bc;
    findQuad(closest, bc, ul, ur, ll, lr, w2c, p, cameraPos, cameraFwd, fragColor);
    drawQuad(closest, bc, ul, ur, ll, lr, w2c, w2l, state, p, cameraPos, cameraFwd, fragColor);    
#endif

    fragColor.xyz = sqrt(fragColor.xyz);
    fragColor.w = 1.;
    //fragColor = texelFetch(iChannel2, ivec2(fragCoord), 0); //render shadow buffer
}

