const vec3 POINT_LIGHT_POS = vec3(3, 4, 10);
const vec3 FILL_LIGHT_DIR = normalize(vec3(-1, .5, 1));
const vec3 LOOK_AT = vec3(0, .5, 0);
const float FLOOR_Z = -2.;
const float BALL_SIZE = .4;
uvec4 hash(uvec4 x){
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    x = ((x >> 16u) ^ x.yzwx) * 0x45d9f3bu;
    return x;
}

//hashing noise by IQ
float hash( int k ) {
    uint n = uint(k);
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    return uintBitsToFloat( (n>>9U) | 0x3f800000U ) - 1.0;
}

// Integer Hash - II by iq
// https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C

vec3 hash3( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

vec3 fxCalcCameraPos(vec4 state)
{
    return vec3(state.y, 1, state.z + 6.);
}

void fxCalcCamera(in vec3 cameraLookAt, in vec3 cameraPos, out vec3 cameraFwd, out vec3 cameraLeft, out vec3 cameraUp)
{
    cameraFwd  = normalize(cameraLookAt - cameraPos);
    cameraLeft  = -normalize(cross(cameraFwd, vec3(0.0,1.0,0.0)));
    cameraUp   = normalize(cross(cameraLeft, cameraFwd)) * 0.5;
}

mat4 fxCalcCameraMat(vec3 resolution, vec3 cameraLeft, vec3 cameraUp, vec3 cameraFwd, vec3 cameraPos)
{
    return inverse(mat4(vec4(-0.25 * cameraLeft, 0.0),
        vec4(-0.5*cameraUp, 0.0),
        vec4(cameraFwd, 0.0),
        vec4(cameraPos, 1.0)));
}

vec3 fxCalcRay(in vec2 fragCoord, in vec3 iResolution, in vec3 cameraFwd, in vec3 cameraUp, in vec3 cameraLeft)
{
	vec2 screenPos = (fragCoord.xy / iResolution.y) * vec2(0.5, 1.) - vec2(0.25 * iResolution.x / iResolution.y, .5);
	return normalize(cameraFwd - screenPos.x * cameraLeft - screenPos.y * cameraUp);
}

float fxLinePointDist2(in vec2 newPos, in vec2 oldPos, in vec2 fragCoord)
{
    vec2 pDelta = (fragCoord - oldPos);
    vec2 delta = newPos - oldPos;
    float deltaLen2 = dot(delta, delta);

    // Find the closest point on the line segment from old to new
    vec2 closest;
    if (deltaLen2 > 0.0000001)
    {
        float deltaInvSqrt = inversesqrt(deltaLen2);
        vec2 deltaNorm = delta * deltaInvSqrt;
        closest = oldPos + deltaNorm * max(0.0, min(1.0 / deltaInvSqrt, dot(deltaNorm, pDelta)));
    }
    else
    {
        closest = oldPos;
    }

    // Distance to closest point on line segment
    vec2 closestDelta = closest - fragCoord;
    return dot(closestDelta, closestDelta);
}

//returns the ids of the four closest particles from the input
ivec4 fxGetClosest(sampler2D sampler, ivec2 xy)
{
    return ivec4(texelFetch(sampler, xy, 0));
}

#define POS 0
#define PREV 1
#define NORM 2
#define NUM_PARTICLE_DATA_TYPES 3

//returns the location of the particle within the particle buffer corresponding with the input id 
ivec2 fxLocFromIDInternal(int width, int id, int dataType)
{
    int index = id * NUM_PARTICLE_DATA_TYPES + dataType;
    return ivec2( index % width, index / width);
}

#define fxLocFromID(X, Y) fxLocFromIDInternal(int(iResolution.x), X, Y)

struct fxParticle
{
    vec3 pos;
    vec3 prev;
    vec3 norm;
    bool pinned;
};

//get the particle corresponding to the input id
fxParticle fxGetParticleInternal(sampler2D sampler, int resolutionWidth, int id)
{
    vec4 particleData0 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, POS), 0);
    vec4 particleData1 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, PREV), 0);
    vec4 particleData2 = texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, NORM), 0);

    fxParticle particle;
    particle.pos = particleData0.xyz;
    particle.pinned = particleData0.w != 0.;
    particle.prev = particleData1.xyz;
    particle.norm = particleData2.xyz;

    return particle;
}

vec4 fxSaveParticle(fxParticle p, int dataType)
{    
    switch(dataType)
    {
    case POS:  
        return vec4(p.pos, p.pinned ? 1. : 0.);
    case PREV:  
        return vec4(p.prev, 0);
    case NORM:  
        return vec4(p.norm, 0);
    }
}

#define fxGetParticle(X) fxGetParticleInternal(iChannel0, int(iResolution.x), X)

vec4 fxGetParticleDataInternal(sampler2D sampler, int resolutionWidth, int id, int dataType)
{
    return texelFetch(sampler, fxLocFromIDInternal(resolutionWidth, id, dataType), 0);
}

#define fxGetParticleData(X, Y) fxGetParticleDataInternal(iChannel0, int(iResolution.x), X, Y)

#define keyDown(ascii)    ( texelFetch(iChannel3,ivec2(ascii,0),0).x > 0.)

#define KEY_SPACE 32

const float SIDE_LEN = 4.;

// global variables, initialized via computeClothSide
int g_ClothSideLen = 0; // how many particles along each side of the square
int g_MaxParticles = 0; // how many particles, total

// computes the size of the cloth grid relative to the current resolution
void initClothExtents(vec3 res)
{
    float particleUse = 0.5 * (3. - 0.6 * smoothstep(1000., 1200., res.y));
    g_ClothSideLen = int(sqrt(res.x * res.y / float(NUM_PARTICLE_DATA_TYPES)) * particleUse);
    g_ClothSideLen = min(80, g_ClothSideLen);
    g_MaxParticles = g_ClothSideLen * g_ClothSideLen;
}

// These functions compute the neighbors from each particle, and -1 if there is
// no neighbor in that direction. Crucially, they must continue to return -1 if
// -1 is passed in, since we do not terminate the loop right away.

int above(int i)
{
    return i >= 0 && i >= g_ClothSideLen ? i - g_ClothSideLen : -1;
}

int below(int i)
{
    return i >= 0 && i < (g_ClothSideLen * (g_ClothSideLen - 1)) ? i + g_ClothSideLen : -1;
}

int left(int i)
{
    return i >= 0 && (i % g_ClothSideLen) != 0 ? i - 1 : -1;
}

int right(int i)
{
    return i >= 0 && (i % g_ClothSideLen) != g_ClothSideLen - 1 ? i + 1 : -1;
}

vec3 screen(vec3 p, mat4 w2c)
{
    vec3 s = (w2c * vec4(p,1.0)).xyz;
    s.xy = s.xy / s.z;
    return s;
}

void insertion_sort(inout ivec4 i, inout vec4 d, int i_, float d_)
{	
    if(any(equal(ivec4(i_),i))) return;
    if     (d_ < d[0])             
        i = ivec4(i_,i.xyz),    d = vec4(d_,d.xyz);
    else if(d_ < d[1])             
        i = ivec4(i.x,i_,i.yz), d = vec4(d.x,d_,d.yz);
    else if(d_ < d[2])            
        i = ivec4(i.xy,i_,i.z), d = vec4(d.xy,d_,d.z);
    else if(d_ < d[3])           
        i = ivec4(i.xyz,i_),    d = vec4(d.xyz,d_);
}

float distance2Particle(sampler2D sampler, int resolutionWidth, int id, float zBias, vec2 fragCoord, mat4 w2cNew)
{
    if (id==-1) return 1e20;
    vec4 worldPos = fxGetParticleDataInternal(sampler, resolutionWidth, id, POS);
    if (worldPos.w != 0.) return 1e20;
    vec3 screenPos = (w2cNew * vec4(worldPos.xyz,1.0)).xyz;
    screenPos.xy = screenPos.xy / screenPos.z;
    vec2 delta = (screenPos.xy)-fragCoord;
    return dot(delta, delta) + zBias*screenPos.z;
}

void consider(sampler2D sampler, int resolutionWidth, int nid, float zBias, vec2 p, mat4 w2c, inout ivec4 new, inout vec4 dis)
{
    if (nid >= 0 && nid < g_MaxParticles)
    {
        float dis2 = distance2Particle(sampler, resolutionWidth, nid, zBias, p, w2c);
        insertion_sort( new, dis, nid, dis2 );
    }
}

void voronoiInternal(sampler2D particles, vec3 res, int frame, out vec4 fragColor, vec2 fragCoord, vec3 cameraPos, sampler2D self, float zBias, uint searchRange, uint searchCount)
{
   	ivec2 iFragCoord = ivec2(fragCoord);
    initClothExtents(res);

	vec2 p = (2.*fragCoord - res.xy) / res.y;

    vec3 cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(LOOK_AT, cameraPos, cameraFwd, cameraLeft, cameraUp);

    mat4 w2c = fxCalcCameraMat(res, cameraLeft, cameraUp, cameraFwd, cameraPos);

    ivec4 new = ivec4(-1);
    vec4 dis = vec4(1e6);

    ivec4 old = fxGetClosest(self, iFragCoord);      
    for (int j = 0; j < 4; j++)
    {
        int id = old[j];
        consider(particles, int(res.x), id, zBias, p, w2c, new, dis);

        // randomly check one of the neighbors of the particle, it's likely to be of interest
        int nid;
        switch((frame + int(j)) % 4)
        {
            case 0: nid = above(id); break;
            case 1: nid = below(id); break;
            case 2: nid = left(id); break;
            case 3: nid = right(id); break;
        }

        consider(particles, int(res.x), nid, zBias, p, w2c, new, dis);
    }

    for (uint i = 0u; i < searchCount; ++i)
    {
        uvec4 h0 = hash(uvec4(fragCoord, frame, res.x) * i);

        ivec4 old = fxGetClosest(self, iFragCoord + ivec2( h0.xy % searchRange - searchRange / 2u));      
        consider(particles, int(res.x), old[0], zBias, p, w2c, new, dis);
    }

    int searchIterations = 1;
    if (frame < 5)
    {
        searchIterations = 10;
    }
    for (int k = 0; k < searchIterations; k++)
    {
        float h = hash3(uvec3(fragCoord, k)).x;

        int id = int(h*float(g_MaxParticles));
        consider(particles, int(res.x), id, zBias, p, w2c, new, dis);
    }

    fragColor = vec4(new);
}

#define voronoi(A, B, C, D, E, F, G) voronoiInternal(iChannel0, iResolution, iFrame, A, B, C, D, E, F, G)

vec2 bary(vec2 p, vec2 a, vec2 b, vec2 c)
{
    vec2 v0 = b - a, v1 = c - a, v2 = p - a;
    float d00 = dot(v0, v0);
    float d01 = dot(v0, v1);
    float d11 = dot(v1, v1);
    float d20 = dot(v2, v0);
    float d21 = dot(v2, v1);
    float denom = d00 * d11 - d01 * d01;
    return vec2((d11 * d20 - d01 * d21) / denom,
                (d00 * d21 - d01 * d20) / denom);
}

vec2 perp(vec2 v)
{
    return vec2(v.y, -v.x);
}

vec2 baryQuad(vec2 p, vec2 ul, vec2 ur, vec2 ll, vec2 lr)
{
    vec2 bc = bary(p, ll, ul, lr);
    if (bc.x + bc.y > 1.) bc = 1. - bary(p, ur, lr, ul);
    return bc;
}

void findQuadInternal(sampler2D sampler, vec3 res, inout int id, out vec2 bc, out fxParticle ul, out fxParticle ur, out fxParticle ll, out fxParticle lr, mat4 w2c, vec2 p, vec3 cameraPos, vec3 cameraFwd, inout vec4 fragColor)
{
    if (id < 0) return;
    // move away from edge
    if (id < g_ClothSideLen) id += g_ClothSideLen;
    if ((id % g_ClothSideLen) == g_ClothSideLen - 1) id -= 1;

    ll = fxGetParticleInternal(sampler, int(res.x), id);
    ul = fxGetParticleInternal(sampler, int(res.x), above(id));
    lr = fxGetParticleInternal(sampler, int(res.x), right(id));
    ur = fxGetParticleInternal(sampler, int(res.x), above(right(id)));

    for (int i = 0; i < 10; ++i)
    {
        vec3 lls = screen(ll.pos, w2c);
        vec3 uls = screen(ul.pos, w2c);
        vec3 lrs = screen(lr.pos, w2c);
        vec3 urs = screen(ur.pos, w2c);

        bc = baryQuad(p, uls.xy, urs.xy, lls.xy, lrs.xy);

        vec2 uv = vec2(float(id % g_ClothSideLen) + bc.y, float(id / g_ClothSideLen) - bc.x) / float(g_ClothSideLen);
        if (any(lessThan(uv, vec2(0))) || any(greaterThan(uv, vec2(1))))
        {
            id = -1; // off the edge of the cloth
            return;
        }

        if (all(lessThan(bc, vec2(1))) && all(greaterThanEqual(bc, vec2(0))))
        {
            break; // found the correct quad that surrounds our pixel
        }

        if (bc.x < 0.)
        {
            id = below(id);
            ur = lr;
            ul = ll;
            ll = fxGetParticleInternal(sampler, int(res.x), id);
            lr = fxGetParticleInternal(sampler, int(res.x), right(id));
        }
        else if (bc.x >= 1.)
        {
            id = above(id);
            lr = ur;
            ll = ul;
            ul = fxGetParticleInternal(sampler, int(res.x), above(id));
            ur = fxGetParticleInternal(sampler, int(res.x), above(right(id)));            
        }

        if (bc.y < 0.)
        {
            id = left(id);
            ur = ul;
            lr = ll;
