}

float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float opRepSphere( in vec3 p, in vec3 c, float s )
{
    vec3 q = mod(p+0.5*c,c)-0.5*c;
    return sdSphere( q, s );
}

float scene(vec3 p, vec4 state)
{
    float d = opRepSphere(p + vec3(abs(state.w) * 0.01 + .5, 0, -.15), vec3(1.5, 0, 0), BALL_SIZE); // balls

    d = min(d, sdBox(p + vec3(0,0,6), vec3(100.,100.,1.))); // wall
    d = min(d, sdBox(p + vec3(0,1.-FLOOR_Z,0), vec3(100.,1.,100.))); // floor

    return d;
}

vec3 getNormal(vec3 p, vec4 state)
{
    vec2 e = vec2(.001, 0);
    vec3 n = scene(p, state) - 
        vec3(scene(p - e.xyy, state), scene(p - e.yxy, state),scene(p - e.yyx, state));

    return normalize(n);
}

