// ---------------------------------------------------------------------------------------
// Computes the position of each cloth vertex.
// ---------------------------------------------------------------------------------------

const vec3 GRAVITY = vec3(0,-0.1,0);
const float COMPRESSION_RESIST = .00003; // stiffness
const float OVERRELAX = .75;

void constraint(inout int nid, inout fxParticle p, float edgeLen)
{
    if (nid < 0) return;

    fxParticle n = fxGetParticle(nid);

    vec3 deltaPos = n.pos - p.pos;
    float len = length(deltaPos);
    vec3 dir = deltaPos / len;

    float error = len - edgeLen * OVERRELAX;

    error *= error < 0. ? COMPRESSION_RESIST : 1.;

    float f = .99;
    p.pos += dir * error * f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    initClothExtents(iResolution);

    ivec2 iFragCoord = ivec2(fragCoord);
    int index = iFragCoord.x + iFragCoord.y*int(iResolution.x);
    int id = index / NUM_PARTICLE_DATA_TYPES;
    int dataType = index - id * NUM_PARTICLE_DATA_TYPES;
    if(id>=g_MaxParticles) return;

    vec4 state = texelFetch(iChannel1, ivec2(0), 0);

    fxParticle p = fxGetParticle(id);

    if (dataType == NORM)
    {
        // compute cloth normals
        ivec4 ids;

        ids.x = above(id);
        ids.y = right(id);
        ids.z = below(id);
        ids.w = left(id);

        fxParticle ps[4];
        for (int i = 0; i < 4; ++i) ps[i] = fxGetParticle(ids[i]);

        float count = 0.;
        vec3 norm = vec3(0);
        for (int i = 0; i < 4; ++i)
        {
            if (ids[i] >= 0 && ids[(i + 1) % 4] >= 0)
            {
                norm += normalize(cross(ps[i].pos - p.pos, ps[(i + 1) % 4].pos - p.pos));
                count += 1.;
            }
        }

        fragColor = vec4(normalize(norm / count), 0);
        return;
    }

    if (!p.pinned)
    {
        p.prev = p.pos;
        p.pos += p.pos - p.prev + GRAVITY; // verlet

        // edge constraints
        int a, b, l, r;
        a = b = l = r = id;

        float EDGE_LEN = SIDE_LEN / float(g_ClothSideLen);
        float CARDINAL_ITERATIONS = 45.;
        for (float i = 1.; i < CARDINAL_ITERATIONS; ++i)
        {
            a = above(a);
            b = below(b);
            r = right(r);
            l = left(l);

            float sLen = EDGE_LEN * i;
            constraint(a, p, sLen);
            constraint(b, p, sLen);
            constraint(r, p, sLen);
            constraint(l, p, sLen);
        }

    #define DIAGONAL_CONSTRAINTS 1

    #if DIAGONAL_CONSTRAINTS
        int al, ar, bl, br;
        al = ar = bl = br = id;

        float DIAGONAL_ITERATIONS = 35.;
        for (float i = 1.; i < DIAGONAL_ITERATIONS; ++i)
        {
            ar = above(right(ar));
            al = above(left(al));
            br = below(right(br));
            bl = below(left(bl));

            float dLen = EDGE_LEN * i * sqrt(2.);
            constraint(al, p, dLen);
            constraint(ar, p, dLen);
            constraint(bl, p, dLen);
            constraint(br, p, dLen);
        }
    #endif // DIAGONAL_CONSTRAINTS

        // collision with scene
        p.pos -= min(0., scene(p.pos, state)) * getNormal(p.pos, state);
    }

    // reset
    if (iFrame == 0 || state.x < 0.)
    {   
        float x = float(id % g_ClothSideLen) / float(g_ClothSideLen) - 0.5;
        float y = .7 - float(id) / float(g_ClothSideLen * g_ClothSideLen);
        p.pos = vec3(x, y, 0.0) * SIDE_LEN / sqrt(2.);
        p.prev = p.pos;
        p.pinned = id < g_ClothSideLen;
    }

    fragColor = fxSaveParticle(p, dataType);
}

