// ---------------------------------------------------------------------------------------
// Manages state, computes voronoi for particle rendering.
// ---------------------------------------------------------------------------------------

vec4 updateState()
{
    // Reset if resolution changes
    vec4 state = texelFetch(iChannel1, ivec2(0), 0);

    ivec4 topMiddleClosest = fxGetClosest(iChannel1, ivec2(iResolution.x * .5, iResolution.y - 10.));
    if (topMiddleClosest[0] >= 0)
    {
        // Reset if cloth falls on the floor
        fxParticle test = fxGetParticle(topMiddleClosest[0]);
        if (test.pos.y <= FLOOR_Z) state.x = 0.;
    }

    if (iFrame == 0 ||
        iResolution.x * iResolution.y != abs(state.x) ||
        keyDown(KEY_SPACE))
    {
        state = vec4(-iResolution.x * iResolution.y, 0.0, 0.0, 0.0);
    }
    else
    {
        state.x = abs(state.x);
    }

    if (iMouse.z > 0.)
    {
        state.yz = vec2(3, 0) + (vec2(-3, 7) * iMouse.xy - iResolution.xy ) / iResolution.y;
        state.w = -abs(state.w);
    }

    if (state.w >= 0.)
    {
        state.w++;
        state.z = 1.5 * sin(.005 * state.w);
        state.y = 3.2 * cos(.0025 * state.w);
    }
    else
    {
        state.w--;
    }

    return state;
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    if (ivec2(fragCoord) == ivec2(0))
    {
        fragColor = updateState();
        return;
    }

    vec4 state = texelFetch(iChannel1, ivec2(0), 0);
    voronoi(fragColor, fragCoord, fxCalcCameraPos(state), iChannel1, 0.01, 15u, 4u);
}

