// ---------------------------------------------------------------------------------------
// Renders depth into w component from the light source for shadow computation.
// ---------------------------------------------------------------------------------------

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    initClothExtents(iResolution);

    // pixel
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 cameraPos = POINT_LIGHT_POS, cameraFwd, cameraLeft, cameraUp;
    fxCalcCamera(LOOK_AT, cameraPos, cameraFwd, cameraLeft, cameraUp);

    // camera-to-world and world-to-camera transform
    mat4 w2c = fxCalcCameraMat(iResolution, cameraLeft, cameraUp, cameraFwd, cameraPos);

    fragColor = vec4(0, 0, 0, 1e6);

    ivec4 old = fxGetClosest(iChannel2, ivec2(fragCoord));      

    for (int j = 0; j < 4; j++)
    {
        int id = old[j];
        if (id < 0 || id >= g_MaxParticles) continue;
        fxParticle ul, ur, ll, lr; vec2 bc;
        findQuad(id, bc, ul, ur, ll, lr, w2c, p, POINT_LIGHT_POS, cameraFwd, fragColor);

        fxParticle a, b, c;
        if (bc.x + bc.y > 1.)
        {
            a = ll; b = ul; c = lr;
        }
        else
        {
            a = ur; b = lr; c = ul;
        }

        vec3 pp = a.pos + bc.x * (b.pos - a.pos) + bc.y * (c.pos - a.pos);
        vec3 ps = screen(pp, w2c);

        vec2 uv = vec2(float(id % g_ClothSideLen) + bc.y, float(id / g_ClothSideLen) - bc.x) / float(g_ClothSideLen);
        if (any(lessThan(uv, vec2(0))) || any(greaterThan(uv, vec2(1))))
        {
            continue;// off the edge of the cloth
        }

        if (all(lessThanEqual(bc, vec2(1))) && all(greaterThanEqual(bc, vec2(0)))
            && ps.z < fragColor.w + .01)
        {
            fragColor = vec4(1, 1, 1, ps.z);
            return;
        }
    }
}

