// CC0: Another windows terminal shader
//  Created this based on an old shader as a background in windows terminal

#define TIME        iTime
#define RESOLUTION  iResolution
#define PI          3.141592654
#define TAU         (2.0*PI)
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}
// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

const mat2 rot0 = ROT(0.0);
mat2 g_rot0 = rot0;
mat2 g_rot1 = rot0;

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(vec3 t) {
  return mix(1.055*pow(t, vec3(1./2.4)) - 0.055, 12.92*t, step(t, vec3(0.0031308)));
}

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

float apolloian(vec3 p, float s) {
  float scale = 1.0;
  for(int i=0; i < 5; ++i) {
    p = -1.0 + 2.0*fract(0.5*p+0.5);
    float r2 = dot(p,p);
    float k  = s/r2;
    p       *= k;
    scale   *= k;
  }

  vec3 ap = abs(p/scale);  
  float d = length(ap.xy);
  d = min(d, ap.z);

  return d;
}


float df(vec2 p) {
  float fz = mix(0.75, 1., smoothstep(-0.9, 0.9, cos(TAU*TIME/300.0)));
  float z = 1.55*fz;
  p /= z;
  vec3 p3 = vec3(p,0.1);
  p3.xz*=g_rot0;
  p3.yz*=g_rot1;
  float d = apolloian(p3, 1.0/fz);
  d *= z;
  return d;
}

vec3 effect(vec2 p, vec2 pp) {
  g_rot0 = ROT(0.1*TIME); 
  g_rot1 = ROT(0.123*TIME);

  float aa = 2.0/RESOLUTION.y;

  float d = df(p);
  const vec3 bcol0 = HSV2RGB(vec3(0.55, 0.85, 0.85));
  const vec3 bcol1 = HSV2RGB(vec3(0.33, 0.85, 0.025));
  vec3 col = 0.1*bcol0;
  col += bcol1/sqrt(abs(d));
  col += bcol0*smoothstep(aa, -aa, (d-0.001));

  col *= smoothstep(1.5, 0.5, length(pp));

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  vec2 pp = p;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  vec3 col = effect(p, pp);
  col = aces_approx(col);
  col = sqrt(col);
  fragColor = vec4(col, 1.0);
}

// StarHUD - https://www.twitch.tv/videos/1642855178

#define PI 3.141519
#define TAU 6.283185

vec2 rot(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c)*v;
}

// mercury sdf
// Repeat around the origin by a fixed angle.
// For easier use, num of repetitions is use to specify the angle.
float pModPolar(inout vec2 p, float repetitions)
{
	float angle = (2.0*PI/repetitions);
	float a = atan(p.x, p.y) + angle/2.; // Y,X
	float r = length(p);
	float c = floor(a/angle);
	a = mod(a,angle) - angle/2.;
	p = vec2(cos(a), sin(a))*r;
	// For an odd number of repetitions, fix cell index of the cell in -x direction
	// (cell index would be e.g. -5 and 5 in the two halves of the cell):
	if (abs(c) >= (repetitions/2.0)) c = abs(c);
	return c;
}

float sdfStar5( in vec2 p )
{
    // repeat domain 5x
    const vec2 k1 = vec2(0.809016994375, -0.587785252292); // pi/5
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;

    // draw triangle
    const vec2 k3 = vec2(0.951056516295,  0.309016994375); // pi/10
    return dot( vec2(abs(p.x)-0.3,p.y), k3);
}

float SDstar5(in vec2 p, in float r)
{
    //return sdfStar5(p/r)*r;


    // these 4 lines can be precomputed for a given shape
    const float an = 3.141593/5.0; // 5sides
    const float en = 3.141593/3.0; // angle
    const vec2  acs = vec2(cos(an),sin(an));
    const vec2  ecs = vec2(cos(en),sin(en)); // ecs=vec2(0,1) and simplify, for regular polygon,

    // reduce to first sector
    float bn = mod(atan(p.x,p.y),2.0*an) - an;
    p = length(p)*vec2(cos(bn),abs(sin(bn)));

    // line sdf
    p -= r*acs;
    p += ecs*clamp( -dot(p,ecs), 0.0, r*acs.y/ecs.y);
    return length(p)*sign(p.x);
}


float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    p.y = -p.y;

    //float starcount = 38.0;               // example stars collected (pass this in...)

    float t = iTime*0.75;

    float starcount = hash11(floor((27.0+t)/3.0));
    starcount = floor(starcount*50.0);      // randomized stars collected


    // fill amount (0.0 to 1.0)
    float fill = mod(t,3.0)-1.0;  // example fill value
    //float fill = 0.5+sin(t)*0.5;

    vec3 col1 = vec3(0.6,0.3,1.0)*0.7;    // purple
    vec3 col2 = vec3(0.2,0.2,0.2);        // grey
    vec3 col3 = vec3(1.0,1.0,1.0);        // white

    float totalstars = 50.0;
    float c = pModPolar(p,totalstars)+(totalstars*0.5);
    float fade = 0.0;
    float rad = 0.015;
    float ang = -(PI*0.5);
    //float ang = (c*(-TAU/totalstars))-(PI*0.5);

    if (c>(totalstars-starcount))
    {
        fade = -1.0 + (c*(1.0/totalstars));
        fade+=(fill*2.0);
        fade = clamp(fade,0.0,1.0);
        float fade2 = smoothstep(0.0,0.25,fade); // 0.125
        rad = mix(rad,0.022,fade2);
        ang = mix(ang,ang-(TAU/5.0),fade2);
        float fade3 = smoothstep(0.0,0.25,fade-0.1);
        col2 = mix(col2,col3,fade3);
    }

    // render star
    p = rot(p+vec2(-0.4,0.0),ang);
    float cc = 1.0-smoothstep(0.0,0.005,SDstar5(p,rad)); // starradius=0.02
    vec3 col = mix(col2*cc,col1*cc,fade);
    fragColor = vec4(col, 1.0);
}

float sdTorus(vec3 p, float r1, float r2)
{
    float l = length(p.xy)-r1;
    return length(vec2(l,p.z))-r2;
}

float sdf(vec3 p) {return sdTorus(p,3.0,1.0);}

float rayMarching(vec3 raySource, vec3 direction)
{
    float d;
    for (int i=0; i<100; i++)
    {
        float len = sdf(raySource+direction*d);
        if (len<0.001 || d>30.0) break;
        d += len;
    }
    return d;
}

vec3 getNormal(vec3 p)
{
    vec2 e = vec2(0.0001,0);
    float d = sdf(p);
    float pdx=sdf(p+e.xyy)-d;
    float pdy=sdf(p+e.yxy)-d;
    float pdz=sdf(p+e.yyx)-d;
    return normalize(vec3(pdx,pdy,pdz));

}

mat2 rotateMatrix(float theta)
{
    return mat2(cos(theta),-sin(theta),
                sin(theta),cos(theta));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y * 4.0;

    vec3 col = vec3(0.75);
    vec3 raySource = vec3(0,0,20);
    raySource.xz *= rotateMatrix(iTime);

    vec3 screen = vec3(uv,1.0);
    screen.xz *= rotateMatrix(iTime);

    vec3 direction = normalize(screen-raySource);

    float d = rayMarching(raySource, direction );
    if(d<30.0)
    {
        vec3 p = vec3(raySource+direction*d);
        vec3 N = getNormal(p);
        vec3 light =vec3(5,0,5);
         light.xz *= rotateMatrix(iTime);

        vec3 L = normalize(light-p);

        float diffuse = dot(N,L);
        // Lambert
        // diffuse = max(0,diffuse);
        // Half Lambert
        diffuse = diffuse * 0.5 + 0.5;


        // Phong
        vec3 R = normalize(2.0*N*dot(N,L)-L);
        vec3 V = -direction;
        // vec3 specular = 0.1*vec3(1)*pow(dot(R,V),8.0);

        // Blinn
        vec3 H = normalize(V+L);
        vec3 specular = 0.9*vec3(1)*pow(dot(H,N),5.0);


        vec3 ambient = vec3(0.1,0.1,0.3)*1.5;

        col = vec3(1,0,1);
        col = ambient + 0.5*col*diffuse + specular;
    }

    // Time varying pixel color
    // col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}

/**
