#define PI    3.14159265358
#define PI2   6.28318530718

mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 a){ return fract(sin(dot(a,vec2(22.34,35.34)))*483434.);}

//@iq
float opx(in float d, in float z, in float h){
    vec2 w = vec2( d, abs(z) - h );
  	return min(max(w.x, w.y), 0.) + length(max(w, 0.));
}

float glow=0.,t12=0.;

const float size = 5.25;
const float hlf =size/2.;

void sep(inout vec3 p){
	if (p.x + p.y<0.) p.xy = -p.yx;
	if (p.x + p.z<0.) p.xz = -p.zx;
	if (p.y + p.z<0.) p.zy = -p.yz;
}

vec2 map(vec3 p, float sq) {
    vec2 res = vec2(1e5,0.);
    p.y+=3.;
    p.z-=t12;
    vec3 o=p;
    o.y+= .5*sin(T*.21+o.z*.15)+.7*cos(T*.12+o.x*.3);

    vec2 id = floor((p.xz+hlf)/size);
    p.xz=mod(p.xz+hlf,size)-hlf;

    float hs = 2.50*sin(id.x+id.y*2.+T*.3);
    float ht = 2.25*sin(id.x*2.+id.y*1.3+T*1.3);

    float ss = hash21(id);
    p.y+= ht;
    vec3 q=p;

    float pis=hs*PI2;
    p.zy*=rot(pis+(T*.04));
    p.xy*=rot(pis-(T*.05));

    if(ss>.25) sep(p);

    float fs = hash21(id+vec2(22.));
    if(fs>.5) { p=abs(p.zyx); } else if(fs>.75) p=abs(p.yzx);

    float r = length(p.xy)-1.45;
    float d = opx(abs(r)-.1,abs(p.z)-.25,.05)/1.25;

    if(d<res.x) res=vec2(d,1.);

    float d2 = length(q)-(1.15-ss);  

    if(d2<res.x) res=vec2(d2,2.);

    float ms=hash21(id+floor(T*1.5));
    if(sq==1.&&ms>.825) glow+=.002/(.0025+d2*d2);

    return res;
}

vec3 normal(vec3 p, float t){
    vec2 e=vec2(t*1e-3,0.);
    float d = map(p,0.).x;
    vec3 n = d-vec3(
        map(p-e.xyy,0.).x,
        map(p-e.yxy,0.).x,
        map(p-e.yyx,0.).x
    );
    return normalize(n);
}

vec3 shade(vec3 p, vec3 n, vec3 ro, float m) {
    vec3 l = normalize(vec3(-2,15,-10)-p);
    float diff = clamp(dot(n,l),.1,1.);

    float shdw = 1.;
    for( float t=.01;t<18.; ) {
        float h = map(p + l*t,0.).x;
        if( h<1e-4 ) { shdw = 0.; break; }
        shdw = min(shdw, 18.*h/t);
        t += h;
        if( shdw<1e-4 || t>18. ) break;
    }
    diff = mix(diff,diff*shdw,.85);

    float spec = .15 * pow(max(dot(normalize(p-ro),reflect(l,n)),0.),24.);

    vec3 h = vec3(0);
    if(m==1.) h = vec3(.05);
    if(m==2.) h = vec3(.75);

    return h * diff+spec;   
}
void mainImage( out vec4 fragColor, in vec2 F )
{

    t12=(T*14.);
	vec2 uv = (2.*F.xy - R.xy)/max(R.x,R.y);
    vec2 vv=uv;

	vec3 C = vec3(0);

    vec3 ro = vec3(0,-1.,12),rd = normalize(vec3(uv, -1.0));

    const float dof = 0.001;
    const float dofdist = 1.0/10.;

    vec2 off=vec2(-.05,.05);
    ro.xy+=off*dof*smoothstep(0.,1.,length(uv))*.01;

    mat2 rx =rot(-.60-.46*sin(T*.4));
    mat2 ry =rot(-.178*sin(T*.2)+pow(1.95,cos(T*.2)*PI));

    ro.zy*=rx;ro.xz*=ry;
    rd.zy*=rx;rd.xz*=ry;

    float fa=0.,sglow=0.;
    for(int k=0;k<2;k++){

        vec3 p = ro;
        float d = 0., m = 0.;
        vec3 RC = vec3(0);

        for(int i=0;i<80;i++){

            //modified jitter/dof 
            //inspiration @Nusan https://www.shadertoy.com/view/3sXyRN
            if(mod(float(i),2.)<1.){
                off= texture(iChannel1,F.xy/1024.).rg*2.-1.;
                vec2 focus = off*dof;
                ro.xy+= focus*(d)*.01;
                rd.xy+= focus*(d*d)*dofdist*.012;
            }

            p=ro+d*rd;

            vec2 ray = map(p,1.);
            m=ray.y;
            d+= ray.x*.55;
            if(ray.x<d*1e-4||d>50.)break;
        }

        if(k==0) {fa=d;sglow=glow;}

        if(d<40.) {
            vec3 n = normal(p,d);
            RC += shade(p, n, ro, m);
            ro = p+n*.001;
            rd = reflect(rd,n);
        }

        if(k>0) RC *=.3;
        C += RC;

    }

    C=mix(C,vec3(.1),1.-exp(-.00003*fa*fa*fa));
    C=mix(C,vec3(sglow,sglow*.5,sglow*.1),clamp(sglow,0.,.9));
    C=clamp(C,vec3(0),vec3(1));
	fragColor = vec4(pow(C,vec3(.4545)),1.);
}

float sphereSDF(in vec3 p)
{
    return length(p)-1.0;
}

float sdf(vec3 p){return sphereSDF(p);}

float RayMarching(in vec3 source, in vec3 direction)
{
    float distance=0.0;
    for (int i=0; i<100; i++)
    {
        vec3 end = source+direction*distance;
        float len = sdf(end);
        if (len<0.001 || distance>10.0) break;
        distance += len;     
    }
    return distance;
}

vec3 GetNormal(vec3 p)
{
    float d = sdf(p);
    vec2 e = vec2(0.001,0.0);
    float pdx = sdf(p+e.xyy)-d;
    float pdy = sdf(p+e.yxy)-d;
    float pdz = sdf(p+e.yyx)-d;
    return normalize(vec3(pdx,pdy,pdz));
}

mat2 RotateMatrix(float theta)
{
    return mat2(cos(theta),-sin(theta),
                sin(theta),cos(theta));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.75);

    vec3 source = vec3(0,0,3);
    vec3 direction = normalize(vec3(uv,0.5)-source);

    float d = RayMarching(source, direction);

    col = vec3(d/15.0);
    if (d < 10.0)
    {
        vec3 p = source + direction*d;
        vec3 n = GetNormal(p);
        vec3 light = vec3(3,1,3);
        light.xz *= RotateMatrix(iTime*5.0);
        float diffuse = dot(n, normalize(light-p));

        // Time varying pixel color
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        col = col * (diffuse*0.5+0.5);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

