F3
col = F3(.41,.43,.41);

B1
hitCuboid(Camera cam, Cuboid cub, inout Hit f, inout Hit b) {


    F3
    ray = normalize(cam.dir * cam.ray),
    dst = cam.pos - cub.pos;

    // check whether ray hits bounding spheroid
    F1
    rd = dot(ray, dst);

    if(dot(cub.ext, cub.ext) - dot(dst, dst) + rd * rd < 0.) RF

    Hit[2]
    hit = Hit[2](
        Hit(-1, 1e7, _.yy, _.yyy),
        Hit(-1, 1e7, _.yy, _.yyy));

    I1
    i = -1;

    M3
    xyz = M3(cub.dir[X] * cub.ext.x, cub.dir[Y] * cub.ext.y, cub.dir[Z] * cub.ext.z),
    xyz_,
    _zyx;

    F3
    sol;

    F2
    sol_uv;

    for(I1 dim = 0; dim < 3; ++ dim) {

        for(F1 sol_f = -1.; sol_f < 2.; sol_f += 2.) { 

            xyz_      = xyz;
            xyz_[dim] = -ray;
            _zyx      = inverse(xyz_);

            sol       = _zyx * (dst + sol_f * xyz[dim]);
            sol_uv    = F2(sol[(dim + 1) % 3], sol[(dim + 2) % 3]);

            if(i < 1 && 0. < sol[dim] && all(lessThanEqual(abs(sol_uv), _.ww))) {

                ++ i;
                hit[i].id   = 2 * dim + I1(.5 + .5 * sol_f);
                hit[i].dist = sol[dim];
                hit[i].uv   = sol_uv;
                hit[i].norm = -sol_f * cub.dir[dim];
            }
        }
    }

    if(hit[0].dist < hit[1].dist) {

        f = hit[0];
        b  = hit[1];
    } else {

        f = hit[1];
        b  = hit[0];
    }

    RE 1 == i;
}

