// rotate vector u around vector axe by angle alpha
F3
rot(F3 u, F1 alpha, F3 axe) {

    axe = normalize(axe);

    F3
    z = dot(u, axe) * axe,
    x = u - z,
    y = cross(axe, x);

    RE cos(alpha) * x + sin(alpha) * y + z;
}

// rotate coordinate system xyz around vector axe by angle alpha
M3
rot(M3 xyz, F1 alpha, F3 axe) {

    axe = normalize(axe);

    xyz[X] = rot(xyz[X], alpha, axe);
    xyz[Y] = rot(xyz[Y], alpha, axe);
    xyz[Z] = rot(xyz[Z], alpha, axe);

    RE xyz;
}

