VO
mainImage(out F4 o, F2 f) {

    // set globel input I
    I = I2(f);

    // read color from Buffer A and store to globel output O
    O = rd(I);

    // use first 16 pixel of the first row of Buffer A as RAM
    // that means if where not inside the RAM coords, we are done here and return fragmentColor o = O.
    if(15 < I.x || 0 < I.y) {

        o = O;

        RE;
    }

    // if the first frame is rendered
    // Buffer A is undefined - RAM is not set
    // init RAM
    if(iFrame < 1) {

        // init RAM and return o = O
        wt(MOUSE,   F4(-1));
        wt(DIR + 0, F4(ID[X], 0));
        wt(DIR + 1, F4(ID[Y], 0));
        wt(DIR + 2, F4(ID[Z], 0));

        o = O;

        RE;
    }

    // if mouse button is not pressed
    // write -1,-1,-1,-1 to RAM at address MOUSE and return o = O
    if(iMouse.z <= 0.) {

        wt(MOUSE, F4(-1));

        o = O;

        RE;
    }

    // read iMouse from Buffer A at address MOUSE and store it in m
    F4
    m = rd(MOUSE);

    // if mouse button is pressed and one of the coords has changed
    if(m.z == iMouse.z && (iMouse.y != m.y || iMouse.x != m.x)) {

        // create from mouse coord difference a rotation axis
        F4
        axis = F4(normalize(F3(m.y - iMouse.y, iMouse.x - m.x, 0)),  .01 * length(iMouse.yx - m.yx));

        // read the coords system of the cube
        M3
        dir = M3(
            rd(DIR + X).xyz,
            rd(DIR + Y).xyz,
            rd(DIR + Z).xyz
        );

        // rotate the cubes coords system by angle axis.w around axis.xyz
        dir = rot(dir, axis.w, axis.xyz);

        // write new cubes 3d coords to RAM as 3 x vec4
        wt(DIR + X, F4(dir[X], 0));
        wt(DIR + Y, F4(dir[Y], 0));
        wt(DIR + Z, F4(dir[Z], 0));
    }

    // write current mouse state to RAM at address MOUSE
    wt(MOUSE, iMouse);

    // return o = O
    o = O;
}

