// simulation
void mainImage( out vec4 fragColor, vec2 fragCoord ){
    ivec2 iFragCoord = ivec2(fragCoord);

    int id = iFragCoord.x + iFragCoord.y*int(R.x);
    if(id>=PARTICLES) return; 

    vec4 prev = texelFetch( ch0, ivec2(fragCoord), 0 );
    vec2 pos = prev.xy;
    vec2 vel = prev.zw;

    // reset pattern
    if(iFrame==0 || keyDown(KEY_SPACEBAR)){
        float h1 = hash(id);
        float h2 = hash(int(h1*41343.));
        pos = vec2(h1,h2)*R.xy;
    }

    float timer = fract(TIMER);
    int cycle = int(TIMER);

    vec2 force = vec2(0);

    if(iMouse.z > 0.){
        vec2 delta = pos-iMouse.xy;
        float dis = length(delta);
        force -= delta/(dis*dis*dis) * 100.;
    } else {
        vec2 delta = pos-R.xy/2.;
        float dis = length(delta);
        vec2 p1 = delta-vec2(R.x/4.,0.);
        vec2 p2 = delta+vec2(R.x/4.,0.);
        float d1 = length(p1);
        float d2 = length(p2);

        if(timer<0.007||timer>.66) {
            // force -= delta/(dis*dis*dis) * 100.;
            force -= p1/(d1*d1*d1) * 100.;
            force -= p2/(d2*d2*d2) * 100.;
        }
    }

    // set boid values for the current cycle
    //   min-max set in COMMON
    float seperation = mix(
        seperation_min,
        seperation_max,
        hash(cycle    ));
    float cohesion = mix(
        cohesion_min,
        cohesion_max,
        hash(cycle+100));
    float alignment = mix(
        alignment_min,
        alignment_max,
        hash(cycle+200));
    float speed = mix(
        speed_min,
        speed_max,
        hash(cycle+100));

    ivec4 closest = getClosest(ivec2(pos));
    for(int i = 0; i < 4; i++){
        int cid = closest[i];
        if(cid==id) continue;

        vec4 part = getParticle(cid);
        vec2 delta = part.xy-pos;
        float dis = length(delta);
        force += -delta /(dis*dis*dis) * seperation;

        force += delta * cohesion;

        force += (part.zw-vel)/(dis+1.) * alignment;
    }

    vec2 dis = max(R.xy-pos, vec2(1));
    force -= 1./(dis*dis)*20.;
         dis = max(pos, vec2(1));
    force += 1./(dis*dis)*20.;

    vel += force;

    vel = normalize(vel)* speed; 

    pos += vel;

    fragColor = vec4(pos,vel);
}

