vec2 ratioZoom(in vec2 uv, in vec2 R, in float k) {
    uv -= 0.5;
    uv *= vec2(1.,R.x/R.y);
    uv *= 1.-k;
    uv /= vec2(1.,R.x/R.y);
    uv += 0.5;
    return uv;
}

void mainImage( out vec4 fragColor, vec2 fragCoord ){
    fragColor = vec4(0);

    float timer = fract(TIMER);
    int cycle = int(TIMER);

    ivec4 ids = getClosest(ivec2(fragCoord));

    const int glow = 6;
    float dots = 0.;
    for(int i = 0; i < glow; i++){
        int id = ids[i];
        vec2 pos = getParticle(id).xy;
        float d = distance(fragCoord,pos);
        dots += 1./(pow(d+.5,2.)); // draw dots
    }
    // feedback uvs
    float zoomer = pow(sin(timer*PI+PI/2.),5.);
    zoomer *= 0.002;
    vec2 luv = fragCoord/R.xy;
    luv = ratioZoom(luv,R.xy,zoomer);
    vec4 last = texture(iChannel2,luv);
    float b = mix(dots,last.r,.99);
    b = clamp(b,0.,1.);
    b = mix(b,dots,dots*dots*dots);
    fragColor = vec4(vec3(b),1.);
}

