////////////////////////////////////////////////////////////////////////////////
//
// Metakoch II, mla, 2022.
//
// Koch curves but with an arbitrary path, set by the mouse (drag the three inner
// red points). There is a counter limiting the number iterations eg. for
// heavily overlapping curves - place where this happens are coloured a lurid green.
// Draws two copies of the curve, rotated by 180, filling the region in between.
//
// Code should generalize to more than 4 segments, or using a diffent path at
// each level of recursion - I've not experimented with this.
//
// Controls:
//
// <mouse>: set path
// <up>/<down>: zoom
// 'c': hide controls
// 'a': adaptive mode where we iterate down to a minimum segment length
// 'x': show segments actually drawn around mouse position
// 'f': fill shape
// 'm': show moving point
// 'p': progressive depth
//
////////////////////////////////////////////////////////////////////////////////


const int MAXDEPTH = 16;       // Maximum stack depth
const vec2 initp = vec2(-1,0); // Initial drawing point

vec2 p = initp;                // The drawing point
vec2 point = vec2(0);          // The moving point
bool gotpoint = false;
float plen = 0.0;              // Cumulative path length
int wind = 0; // Compute winding number here

void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.x <= 0.0) {
    if (r.x > 0.0 && t > 0.0) wind++;
  } else {
    if (r.x <= 0.0 && t < 0.0) wind--;
  }
}

float drawsegment(vec2 pos, float seglen, float theta, float t) {
  vec2 seg = seglen*vec2(cos(theta),sin(theta));
  if (t < plen+seglen && !gotpoint) {
    gotpoint = true;
    point = p + (t-plen)/seglen*seg;
  }
  vec2 q = p + seg;
  float d = segment(pos,p,q);
  polywind(pos,p,q);
  plen += seglen;
  p = q;
  return d;
}

// Map screen coordinates
vec2 map(vec2 pos) {
  pos = scale*(2.0*pos - iResolution.xy)/iResolution.y;
  return pos;
}

// This should get something from a buffer so we can set all three points
vec2 getpoint(int i) {
  vec2 p = getselection(i-1);
  p *= 0.5;
  p.x += 0.5;
  return p;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  int maxdepth = 8;
  bool adaptive = key(CHAR_A);
  if (adaptive) maxdepth = MAXDEPTH;
  else if (key(CHAR_P)) maxdepth = 1+int(iTime)%maxdepth;
  scale = exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  vec2 pos = map(fragCoord);
  vec2 pos2 = pos;
  if (key(CHAR_X)) pos2 = map(iMouse.xy);
  float pwidth = fwidth(pos.x);
  vec2 program[5];
  float radius = 0.0;
  float pathlen = 0.0;
  {
    #define C(a,b) (vec2(a,b))
    vec2 p[] =
      vec2[](vec2(0,0),
             getpoint(1),
             getpoint(2),
             getpoint(3),
             vec2(1,0));
    float theta = 0.0;
    float seglen = 1.0;
    for (int i = 0; i < 4; i++) {
      radius = max(radius,distance(vec2(0.5,0),p[i]));
      assert(p[i] != p[i+1]);
      float theta1 = arg(p[i+1]-p[i]);
      float seglen1 = length(p[i+1]-p[i]);
      pathlen += seglen1;
      program[i] = C(seglen1/seglen,theta1-theta);
      theta = theta1;
      seglen = seglen1;
    }
    program[4] = C(1.0/seglen,-theta);
    #undef C
  }
  int stack[MAXDEPTH];
  float d = 1e8;
  float seglen = 2.0;
  float theta = 0.0;    // Cumulative angle
  float t = mod(iTime,2.0*seglen*pow(pathlen,float(maxdepth))); // Moving point
  for (int i = 0; i < 2; i++,theta += PI) {
  int depth = 0;
  stack[depth++] = 0;
  int check = 1000;
  while (depth > 0 && check-- > 0) {
    assert(check > 1);
    vec2 instr = program[stack[depth-1]++];
    float lenfact = instr[0];
    float angle = instr[1];
    theta += angle; // Apply turn
    seglen *= lenfact;
    if (stack[depth-1] == program.length()) {
      depth--;    // Terminate level & unwind
    } else if (depth == maxdepth || seglen < 3.0*pwidth) {
      d = min(d,drawsegment(pos,seglen,theta,t)); // Segment
    } else {
      vec2 seg = seglen*vec2(cos(theta),sin(theta));
      vec2 next = p+seg; // Where we end up
      if (distance(pos2,p+0.5*seg) > seglen) {
        polywind(pos,p,next);
        // Show actuall path taken to mouse position.
        if (key(CHAR_X)) d = min(d,segment(pos,p,next));
        p = next;
        plen += seglen*pow(pathlen,float(maxdepth-depth));
      } else {
        stack[depth++] = 0;  // Recurse
      }
    }
  }
  }
  polywind(pos,p,initp);
  float px = fwidth(pos.x);
  vec3 col = vec3(1,1,0.8);
  if (!key(CHAR_F) && (wind&1) != 0) {
     col = texture(iChannel0,pos).xyz;
     if (d > 1.0) col *= 0.75; 
  }
  col *= vec3(smoothstep(0.0,px,d));
  if (key(CHAR_M) && gotpoint) col = mix(vec3(1,0,0), col,smoothstep(0.0,px,distance(pos,point)-0.01));
  if (key(CHAR_R)) col = mix(vec3(1,0,0), col,smoothstep(0.0,px,abs(distance(pos,vec2(0))-2.0*radius)));
  if (!key(CHAR_C)) {
    float px = fwidth(pos.x);
    vec2 points[] = vec2[](vec2(-1,0),
                           getselection(0),
                           getselection(1),
                           getselection(2),
                           vec2(1,0));
    float d = 1e8, d1 = 1e8;
    for (int i = 0; i < 5; i++) {
      d1 = min(d1,distance(pos,points[i]));
      if (i < 4) d = min(d,segment(pos,points[i],points[i+1]));
    }
    col = mix(vec3(0),col,smoothstep(0.0,px,d-0.008));
    col = mix(vec3(1,0,0),col,smoothstep(0.0,px,d1-0.025));
  }
  col = pow(col,vec3(0.4545));
  if (alert) col.g = 1.0;
  fragColor = vec4(col,1);
}

