#define T (iTime)

float sid=0.0;
vec3 c0=vec3(0);

mat2 rot(float a)
{
	float c=cos(a), s=sin(a);
	return mat2(c,s,-s,c);
}

float dfo(vec3 p, float id)
{
	p.xy*=rot(p.z*sin(T/4.0+id)*(.01+id*0.1));
	p.z+=T;
	float s = 1.0/id/id;
	vec3 p2 = mod(p, s+s)-s;
	p+=sin(p.yzx+T);
	return length(p2)-s*0.5;
}

float df(vec3 p)
{
	float d=dfo(p,0.0);
	for (float i=.0; i<4.0; i+=1.)
	{
		d=max(d-.2, min(d, dfo(p, i)));
	}
	return d;
}

vec3 nf(vec3 p)
{
	vec2 e=vec2(0,0.5e-4);
	return normalize(vec3(
		df(p+e.yxx)-df(p-e.yxx), 
		df(p+e.xyx)-df(p-e.xyx), 
		df(p+e.xxy)-df(p-e.xxy)));
}

vec3 surf(vec3 pos)
{
	vec3 norm = nf(pos);
	vec3 emc = c0;
	vec3 shr = vec3(0);
	float pat = 0.0;
	float q = 0.0;
	q += smoothstep(0.9,0.98,sin((pos.z*0.5+T*3.14159)));
	pat += smoothstep(0.97-q,0.98,sin(94.0*df(pos*1.0+1.0)));
	pat += smoothstep(0.92,0.98,sin(94.0*df(pos*4.0+1.0)))*0.2;
	shr=emc * pat * (1.0+q*2.0) * max(.0,sin(pos.z*0.5+T));
	return shr;
  }

vec3 eval_surface(vec3 pos, vec3 dir)
{
	return surf(pos);
}

vec3 frame(in vec2 coord, int id)
{
	vec4 color = vec4(1.0);
	vec2 uv = (coord-iResolution.xy/2.0)/iResolution.yy;

	vec2 quv = uv-mod(uv,vec2(0.1));
	float q=fract(dot(quv, vec2(2.412,95.313))*31.3513);
	sid = T*0.3+q*0.25;
	sid -= fract(sid);
	sid = sin(sid*4123.0);

	c0 = mix(vec3(0.9,9,1.1), vec3(9,0.9,1.1), abs(sin(sid*4.04+uv.y*0.5)));
	c0 = mix(c0, vec3(1.5,.9,6), abs(sin(sid*9.53+uv.y*0.5)));

	vec3 pos = vec3(sin(sid)*0.35,cos(sid*4.0)*0.35,-4.0);
	vec3 dir = normalize(vec3(uv.xy, +1));
	float dt=0.01;
	float dist;

	dir.xy*=rot(sid);
	dir.xz*=rot(sin(sid*53.0)/4.0);
	dir.yz*=rot(sin(sid*9.0)/4.0);

	for (int i=0; i<150; i+=1){
	  dist = df(pos+dir*dt);
	  dt += dist;
	  if (dist<1e-4) break;
	}

	vec3 pos2 = pos+dir*dt;

	vec3 col = vec3(0);
	if (dist < 1e-3)
	{
	  col = surf(pos2);
	}

	vec3 dir2 = reflect(dir, nf(pos2));
	float dt2 = 1e-3;
	float dist2 = 0.0;
	for (int i=0; i<100; i+= 1){
	  dist=df(pos2+dir2*dt2);
	  dt2+=dist;
	  if (dist<1e-4) break;

	}
	vec3 pos3 = pos2+dir*dt2;
	vec3 col2 = c0;
	if (dist < 1e-3)
	{
	  col2 = surf(pos3);
	}
	col2 += mix(c0*4.0,col2,1.0/(1.0+dt2*dt2*.5e-3));

	float fres = pow(1.0+dot(dir, nf(pos2)),2.0);
	fres = clamp(fres,0.0,1.0);

	col=mix(col, col2, fres);
	col=mix(c0*4.0,col,1.0/(1.0+dt*dt*.5e-3));

	return vec3(col);
}

void mainImage(out vec4 color, in vec2 coord){

	color = vec4(0);

	for (int i=0; i<1; i+=1)
	{
		color.xyz += frame(coord, i);
	}

	float vignette = 1.0-pow(length((coord.xy-.5*iResolution.xy)/iResolution.yy),2.0);
	color.xyz *= clamp(vignette, 0.0, 1.0);
	color.xyz = 1.2*vec3(1.35,1.3,1.5)*color.xyz/(1.0+color.xyz);
	vec3 inv_gamma = vec3(1.0/2.2);
	color.xyz = pow(color.xyz, inv_gamma);
	color.xyz = clamp(color.xyz, 0.0, 1.0);
}

float strength = 0.02;
float speed = 0.2;
int grains = 2;
float fgrains; 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Some convenient variables
    fgrains = float(grains);
    vec2 uv = fragCoord/iResolution.xy;

    // Flow from channel 0s RG channels between -1 and 1
    vec2 flow = (texture(iChannel0,uv).rb-0.5)*2.;

    // Original phase saw tooth wave
    float phase = fract(iTime*speed);
    vec2 uv1 = uv + (flow*phase*strength);

    // Triangle wave that determines mixture 
    float flow_mix = 1.-abs((fgrains*phase - (0.5*fgrains))/(0.5*fgrains));

    //Initialise color with first term
    vec4 color = texture(iChannel1, uv1)*flow_mix;

    for(int i=1;i<grains;i++){
        // Calculate next sawtooth wave with offset
        float phase_tmp = fract(phase + 1./fgrains);
        vec2 uv2 = uv + (flow*phase_tmp*strength);

        phase = phase_tmp;
        flow_mix = 1.-abs(2.*phase - 1.);
        color += texture(iChannel1, uv2)*flow_mix;
    }

    // Output to screen
    fragColor = color;
}

mat2 rot(float a) {
  return mat2(cos(a), -sin(a),sin(a), cos(a));
}

float wing2(vec2 uv) {
  uv.x = abs(uv.x);
  return length(uv + sin(uv.y)*2.)-1.;
}

float wing(vec3 p) {
  p.x = abs(p.x);
  p.xz *= rot(sin(iTime*20.)/5.);
  return max(abs(p.z)-0.01,wing2(p.xy));
}


float flutter(vec3 p) {
    p.y += sin(iTime);
  p.x += sin(iTime*2.)*2.;
  p.yz *= rot (1.2+sin(iTime*2.)*0.2);
  p.xy *= rot(sin(iTime*2.+0.5));
  float bod=length(p/vec3(0.3,1.,0.3)+vec3(0.,0.,0.3))-1.;
  bod /= 3.;
  float hed = length(p-vec3(0.,1.,0.)) -0.2;
  float flutter = min(min(wing(p/3.),bod),hed);
  return flutter;
}

float flh = 3.5;
float twgl = 100.;

float twimst(vec3 p) {
  p.yz *= rot(iTime+p.x/10.);

  float twgls = max(abs(p.y)-0.4,abs(p.z)-0.4);
  twgl = min(twgl,twgls);
  return twgls;
}

float flugl = 100.;

float map(vec3 p) {

  float flut = flutter(p);
  flugl = min(flugl,flut);
  float flo = p.y+flh;

  float muun = length(p - vec3(19.,12.,25.))-1.;

  float twi=twimst(p-vec3(0.,0.,20.));

  return min(min(min(flut, flo), muun),twi);
}



vec3 skydome(vec3 rd) {
  return vec3(vec2(-rd.y),rd.y);
}

vec3 gn(vec3 p) {
  vec2 e=vec2(0.001,0);
  return normalize(map(p) - vec3(map(p-e.xyy), map(p-e.yxy), map(p-e.yyx)));
}

vec3 wh = vec3(1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.-1.;
    uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 ro=vec3(0,0,-8), rd=normalize(vec3(uv,1));

  rd.x += max(0.,sin(uv.y*4.))*0.003;
  rd.y += max(0.,cos(uv.x*4.))*0.003;

  float d,t=0.;

  for(int i=0; i < 1000; ++i ) {
    d = map(ro+rd*t);
    t+=d;
    if (d < 0.01) {
      vec3 p = ro + rd * t;

      if (p.y > 0.3-flh)
        break;
      else {
        wh = vec3(0.5,0.5,1);
        vec3 n = gn(p);
        n.x += sin(iTime*4.+30.*p.z)/50.;
        n.y += cos(iTime*4.+30.*p.z)/50.;
        rd = reflect(rd,n);
        ro = p;
        t = 0.2;
      }
    }
  }


  vec3 col = skydome(rd);

  vec3 ld = normalize(vec3(3,4,-10));

  vec3 p = ro + rd * t;

  if (d < 0.01) {
    vec3 n = gn(ro+rd*t);
    if (p.y > 3.) {
      col = vec3(1.);
    } else {
      if (p.z > 3.) {
        col = vec3(0,0.4,0);
      } else {
        col = vec3(0.3,0,0);
      }
      col += pow(max(dot(reflect(-ld,n),rd),0.),30.);
      col += dot(n,ld);
    }
  } else {

    col += vec3(0.3,0.3,0.)* 0.1/pow(twgl,2.);
    col += vec3(0.6,0.,0.4)* 0.1/flugl;
 }

  col *= wh;
    // Output to screen
    fragColor = vec4(col,1.0);
}

