vec3 glow = vec3(0.);

float box(vec3 p, vec3 s) {
  p = abs(p)-s;
  return max(p.x, max(p.y, p.z));
}

vec3 calcglow(float m) {
  return .003*vec3(1.,1.2,1.4) / abs(m-.05*vec3(1.3, 1.2, .9));
}

float mycube(vec3 p) {
  vec3 p2 = p;
  float m = INF;
  vec3 s = vec3(.2 + .02*smoothstep(.6, 1., fract(beat)));
  float gi = floor(4.*fract(beat));
  for(float i=0.;i<4.;++i) {
