    glow += calcglow(m);
  } else if(h < .2) {
    m = box(p, vec3(.3));
    glow += calcglow(m);
  } else if(h < .3) {
    p = abs(p)-.2;
    p = abs(p)-.1;
    m = length(p)-.05;
    glow += calcglow(m);
