    float d = distance(coord, floored);

    vec4 color = vec4(.5, 1, .4, 1);

    if (mod(floored.x, 2.0) == mod(floored.y, 2.0))
        color = vec4(.4, .6, .1, 1);


    if (d > 0.5)
        return vec4(1, 1, 1, 1) / (d * 10.0);
    else
        return (1.0 - color * d * d * 4.0);

}

vec2 convert(vec2 coord)
{

    float d = length(coord);
    float a = atan(coord.y, coord.x);
    //a += pow(d, 1.0 + iTime * sin(iTime / 100.0));

    float cosa = cos(a);
    float sina = sin(a);


    vec2 converted = vec2(cosa + (sina * cos(iTime)), sina + (cosa * sin(iTime))) / (d / iTime);
    converted = coord * d + converted * (1.0 - d);

    vec2 other = coord / (d * iTime);


    return converted * d + other * (1.0 - d);


}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x / iResolution.y;

    fragColor = checkerboard(convert(uv), vec2(1, 1) / (1.0 + sin(iTime / 100.0)));
}

#define MAX_STEPS 100
#define MAX_DIST 100.0
#define SURFACE_DIST 0.01
#define BLEND_UNITS 1.6667

//smooth union function (thank you, inigo!)
float opSmoothUnion (float d1, float d2, float k ) {
    float h = clamp(0.5 + 0.5*(d2-d1)/k, 0., 1.);
    return mix( d2, d1, h ) - k*h*(1.-h);
}

//sdfs
float GetDist(vec3 p) {
    vec4 s1 = vec4(0, 1, 5, 1);
    vec4 s2 = vec4(.25 * tan(iTime), 2, 5, 1);
    vec4 s3 = vec4(0, 2, cos(iTime) + 5., 1);

    float sphereDist1 = length(p-s1.xyz)-s1.w;
    float sphereDist2 = length(p-s2.xyz)-s2.w;
    float sphereDist3 = length(p-s3.xyz)-s3.w;
    float sphereDist = opSmoothUnion(
        sphereDist1, sphereDist2, BLEND_UNITS
    );
    sphereDist = opSmoothUnion(
        sphereDist, sphereDist3, BLEND_UNITS
    );

    float planeDist = opSmoothUnion(sphereDist, p.y, BLEND_UNITS);
    float d = min(sphereDist, planeDist);
    return d;
}

//raymarcher
float RayMarch (vec3 ro, vec3 rd) {
    float dO = 0.;

    for (int i=0; i<MAX_STEPS; ++i) {
        vec3 p = ro + dO*rd;
        float dS = GetDist(p);
        dO += dS;
        if (dS<SURFACE_DIST || dO>MAX_DIST) break;
    }
    return dO;
}

//normals
vec3 Normalfy (vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    //calculates normals by swizzling
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx)
    );
    return normalize(n);
}

//phong
float Phongify(vec3 p) {
    vec3 lightPos = vec3(3, 5, -1);
    vec3 l = normalize(lightPos-p);
    vec3 n = Normalfy(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p+n*SURFACE_DIST*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // zero uvs are in the middle
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0.);

    vec3 ro = vec3(.25 * tan(iTime), 1.2, -2.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    vec3 bl = vec3(sin(iTime) * 255., cos(iTime) * 255., 0);

    float dif = Phongify(p);
    col = vec3(dif);
    col = mix(col, bl.yzx, 0.001);

    fragColor = vec4(col,1.0);
}

vec4 lerp(vec4 colorone, vec4 colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}

vec4 power(vec4 color, float exponent)
{
    return vec4(pow(color.x, exponent), pow(color.y, exponent), pow(color.z, exponent), pow(color.w, exponent));
}

vec4 checkerboard(vec2 coord)
{

    float l = length(coord);

    coord *= iTime;

    coord = floor(coord);

    vec2 wrap = mod((mod(coord, 255.0) + 255.0), 255.0) / 255.0;

    vec4 color = vec4(wrap.y, (wrap.x + wrap.y) / 2.0, wrap.x*.9, 1);

    if (mod(coord.x, 2.0) == mod(coord.y, 2.0))
        color = lerp(color * color, 1.0 - color, cos(iTime / 10.0));


    return color;//smoothstep(0.0, 1.0, lerp(color, power(color, iTime / 10.0), l / 10.0));
}

float PI = 3.1415926;

vec2 src(vec2 dst)
{
    float d = length(dst);
    float angle = atan(dst.y, dst.x);
    //angle += d;
    //angle += pow(d, 1.0 + iTime / 1000.0);
    //angle += d * iTime * 0.01;
    angle += 0.01 * d * sin(iTime / 50.0);
    angle -= d * d * d * iTime * 0.00005 / (d * sin(iTime / 30.0) + 0.25);
    float cos = cos(angle);
    float sin = sin(angle);


    return vec2(cos, sin) / pow(d, iTime / 100.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.0;
    uv -= 1.0;
    uv.x *= iResolution.x / iResolution.y;
    fragColor = smoothstep(0.0, 1.0, checkerboard((iTime / -10.0) * src(uv * (iTime / 100.0))));
}

