#define R iResolution
#define T iTime

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21(vec2 a){return fract(sin(dot(a,vec2(32.232,21.232)))*2323.23);}
float smp(float a){return texture(iChannel0,vec2(a,.1)).x;}
vec3 gid,sid;
float glow =0.;
vec2 map(vec3 p, float a) {
    vec2 res=vec2(1e5,0.);
    vec3 o=p,i=p;
    float k=5./dot(o,o);
    o*=k;

    float tt = .5+.5*sin(T*.2);
    p=mix(p,o,tt);

    p.z-=T*1.5;

    vec3 id = floor((p+1.)/2.);
    float hs=hash21(id.xy+id.z);gid=id;
    float ss = (smp(mod(id.z*.05,1.))*.25)+.001;
    p=mod(p+1.,2.)-1.;

    vec2 q=vec2(length(p.xy)-.5,p.z);
    float d = length(q)-ss;

    vec3 w=p;

    float dt = length(abs(w.xy)-.5)-.075;
    dt=min(length(abs(w.xz)-.5)-.075,dt);
    dt=min(length(abs(w.yz)-.5)-.075,dt);

    float tm = .75+.25*cos(T*.2);
    dt=mix(1.,dt,tm);
    if(dt<res.x) res=vec2(dt,1.);
    if(d<res.x) res=vec2(d,1.);

    bool check=id.x==0.&&id.y==0.;
    float l = length(p)-.05;
    if(l<res.x&&hs>.6&&!check) res=vec2(l,1.);
    if(a==1.&&hs>.6&&!check) glow += .002/(.0165+l*l);

    float sx = .75+.5*sin(T*.33);
    float sp =length(i)-(.5+sx);
    if(sp<res.x) res=vec2(sp,1.);

    float ml= 1./k;
    res.x=mix(res.x,res.x*(ml/1.),tt);
    return res;
}

vec3 normal(vec3 p, float t){
    vec2 e=vec2(t*1e-3,0.);
    float d = map(p,0.).x;
    vec3 n=d-vec3(
    map(p-e.xyy,0.).x,
    map(p-e.yxy,0.).x,
    map(p-e.yyx,0.).x
    );
    return normalize(n);
}

vec3 hue(float a){return .45+.45*sin(6.*a+vec3(1,.5,.2)*vec3(2.75,1.2,.4));}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (2.*fragCoord-R.xy)/max(R.x,R.y);
    uv*=rot(T*.4);
    vec3 C = vec3(0),ro=vec3(0,0,5.),rd=normalize(vec3(uv,-1.));

    float tt = floor(T*.4);
    float xt= 1.015*sin(uv.x*45.+T*22.);float yt= .015*sin(uv.y*45.+T*22.);

    float hs=hash21(vec2(tt,25.));
    if(uv.x>yt&&hs>.7&&tt>12.){
        ro.xz*=rot(30.+T*.2);
        rd.xz*=rot(30.+T*.2);
    }

    hs=hash21(vec2(15,tt));
    if(uv.y>xt&&hs>.5&&tt>18.){
        ro.yz*=rot(50.+T*.1);
        rd.yz*=rot(50.+T*.1);
    }

    vec3 p = ro;
    float d=0.,m=0.;

    for(int i=0;i<132;i++){
        p=ro+d*rd;
        vec2 ray=map(p,1.);
        m=ray.y;
        ray.x=max(abs(ray.x),1e-4);
        d+=abs(ray.x*.45);
    }
    sid=gid;
    if(d<25.){
        vec3 n=normal(p,d);
        vec3 l=normalize(vec3(1,5,-5));
        float diff=clamp(dot(n,l),0.,1.);
        vec3 h = hue(n.x+n.y+n.z);

        C+=diff*h;

    }
    float ff = .5+.5*sin(T*.5);
    vec3 clr=mix(vec3(.1),hue(2.445+(uv.y+T)*.2),ff);
    C=mix(C,clr,1.-exp(-.0095*d*d*d));
    C = mix(C,vec3(glow),clamp(glow,0.,1.));

    C = pow(C,vec3(.4545));
    fragColor = vec4(C,1.0);
}

//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

float sh1,sh2,sh3;
float time ;

float fac(vec3 p) {
  p = asin(sin(p));
  return dot(normalize(vec3(1)),p);
}

float noise(vec3 p, float dir) {
  float f1 = fac(erot(p, normalize(vec3(1,2,3)),1.4+sh1*dir));
  float f2 = fac(erot(p, normalize(vec3(-1,3,2)),2.4+sh2*dir));
  float f3 = fac(erot(p, normalize(vec3(1,-2,1)),.4+sh3*dir));

  return (f1 + f2)/sqrt(2.);
  return (f1 + f2 + f3)/2.;
}

float ball(vec3 p, float w, float t, float dir) {
  vec3 p2 = normalize(p);
  float f = noise(p2*7.*w, dir)/7./w;
  return length(vec2(f,length(p)-w))-t;
}

float scene(vec3 p) {
  float b1 = ball(p,1.,.1,-1.);
  float b2 = ball(p,1.5,.03,1.);
  float b3 = ball(p,2.,.02,-1.);
  float b4 = ball(p/2.,2.,.01,1.)*2.;
  return mix(min(min(b1,b2),min(b4,b3)), mix(noise(p*50., 1.)/50., noise(p*10.,-1.)/10., .5), .2);
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.001);
  return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

#define FK(x) floatBitsToInt(x*x/7.)^floatBitsToInt(x)
float hash(float a, float b) {
  int x = FK(a), y = FK(b);
  return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float fakefft(float k) {
float mul = fract(sin(k*564.)*353.);
    return pow(1.-k,3.) * (.5+.5*sin(iTime*3.*sqrt(k+1.)*(4.4544+mul)))*1.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  vec2 uv2 = floor(uv*4.)/4.;
  float hs = hash(uv2.x,uv2.y);
  time = iTime + (hash(hs,54.)+hash(hs,456.)+hash(hs,234.)+hs)*(.2*(sin(iTime*.5)*.5+.5));

  float bar = floor(fragCoord.x / iResolution.x*20.)/20.;
  bar = abs(bar-.5);
  bool inbar = false;
  if (fakefft(bar)*(20.+bar*bar*600.)/100. > 1.-abs(fragCoord.y / iResolution.y-.5)*2.) {
    inbar = true;
  }

  float it = iTime*.3;
  sh1 = sin(it);
  sh2 = cos(it*1.34235);
  sh3 = sin(it*2.15335);

  float off1,off2,off3,off4,off5;

  float tt = floor(time);
  float t2 = asin(sin(iTime)) * .2;
  off1 = sin(tt*45.) + t2;
  off2 = cos(tt*33.) + t2;
  off3 = sin(tt*76.) + t2;
  off4 = sin(tt*17.) + t2;
  off5 = sin(tt*99.);

  vec3 cam = normalize(vec3(.7+off3*.5,uv));
  vec3 init = vec3(-3,off1,off2);

  float zrot = iTime*.3 + off4;
  cam = erot(cam,vec3(0,0,1),zrot);
  init = erot(init,vec3(0,0,1),zrot);

  float glow = 0.;

  vec3 p = init;
  bool hit = false;
  bool trig1 = false;
  bool refl=false;
  bool trig2 = false;
  float atten = 1.;
  for (int i = 0; i< 100 && !hit; i++) {
    float dist = scene(p);
    if(dist*dist<1e-6) {
      if (sin(erot(p,normalize(vec3(1,1,1)),.96).z*10.) < -0.85) {
      vec3 n = norm(p);
      atten*=1.-abs(dot(cam,n))*.9;
      p += n*.01;
      cam = reflect(cam,n);
        refl=true;
      }else{
      hit = true;
      }
    }
    float dd = distance(p,init);
    if (dist < 0.002*dd)trig1=true;
    if (dist > 0.002*dd && trig1)trig2=true;
    p += cam*dist;
    glow += .08/(length(p)+.01) * sqrt(abs(dist));
    if(distance(p,init)>100.)break;
  }
  vec3 n = norm(p);
  vec3 r = reflect(cam,n);
  float diff = length(sin(n*2.)*.5+.5)/sqrt(3.);
  float ref = length(sin(r*2.)*.5+.5)/sqrt(3.);
  diff = mix(max(diff*2.-1.,0.), diff, .03);
  vec3 col = vec3(.9,.2,.2)*diff + pow(ref,20.)*(1.-abs(dot(cam,n))*.9)*5.;
  fragColor.xyz = sqrt(hit ? col : vec3(0));
  fragColor.xyz += glow*vec3(0.1,.5,.8);
  fragColor.xyz += glow*glow;
  fragColor *= atten;

  if (trig2 && !refl) fragColor *= 0.;
  fragColor = smoothstep(.05,1.,fragColor);

  if (inbar) off4 += 2.5;
  if (inbar) fragColor += .5;
  fragColor.xyz = erot(fragColor.xyz*2.-1.,normalize(vec3(off1,off2,off3)), off4)*.5+.5;
  if (off5 < 0.0) fragColor = 1. - fragColor;
  fragColor *= 1.-dot(uv,uv)*.5;
  fragColor += noise(uv.xyy*500.,1.)*.02;
  fragColor = floor(fragColor*8.)/8.;
}

