/* I got the idea to create this shader after observing the moon 
   on a cloudy night a few days ago, and also because I really 
   like to explore volume rendering right now.

   I started by calculating and marching real light samples 
   but this decreased the performances a lot and I wanted it 
   to be accessible, so I replaced it with a fake light which
   intensity is mapped to the distance from the camera.

   You can move the camera with the mouse to better see the perspective.
   To see the entire "scene", set BEHIND_THE_SCENE to 1 =)

   Also I tried to add stars in the background (line 123) but I got
   heavy flickering when moving the camera. Does someone know a proper 
   way to add stars without having this visual artefact ?   
*/

#define CLOUDS_SMOOTHNESS 1.5
#define LIGHT_INTENSITY   20.
#define ABSORPTION        5.5
#define VOLUME_STEPS      40.

#define BEHIND_THE_SCENE  0

#define PI 3.141592
#define hash33(p) fract(sin( (p) * mat3( 127.1,311.7,74.7 , 269.5,183.3,246.1 , 113.5,271.9,124.6) ) * 43758.5453123)

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// noise and fbm are from https://www.shadertoy.com/view/XtS3DD
float noise(in vec3 p) {
    vec3 ip = floor(p);
    vec3 fp = fract(p);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec2 rz = textureLod(iChannel0, (tap+0.5)/256.0, 0.0).yx;
	return mix(rz.x, rz.y, fp.z);
}

float fbm(in vec3 x) {   
    float rz = 0.;
    float a = .35;
    for (int i = 0; i<4; i++) {
        rz += noise(x)*a;
        a*=.35;
        x*= 4.;
    }
    return rz - .25;
}

vec2 boxIntersection(in vec3 ro, in vec3 rd) {
    vec3 rad = vec3(6., 6., 2.);
    vec3 m = 1.0/rd;
    vec3 n = m*ro;
    vec3 k = abs(m)*rad;
    vec3 t1 = -n - k;
    vec3 t2 = -n + k;
    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );
    if( tN>tF || tF<0.0) return vec2(-1.0); // no intersection   
    return vec2( tN, tF );
}

void marchVolume(vec3 ro, vec3 rd, float near, float far, inout vec3 color) {    
    vec3 vColor = vec3(0.);
    float visibility = 1.;

    float inside = far - near + hash33(ro).x*.01;
    float stepSize = inside / VOLUME_STEPS;
    float i = 0.;

    for (float t = near; t <= far; t += stepSize) {
        vec3 p = ro + t*rd;

        float s = CLOUDS_SMOOTHNESS*.01;
        float dens = smoothstep(-s, s, fbm(p + vec3(iTime*.055, iTime*.065, 1.+iTime*.02)))*.1;

        float prev = visibility;
        visibility *= exp(-stepSize*dens*ABSORPTION);

        float absorption = prev - visibility;
        float light = smoothstep(2.5, 6.5, p.z); // fake light
        vColor += absorption * dens * light * LIGHT_INTENSITY;

        if (i++ > VOLUME_STEPS) break;
    }

    color = min(vColor, 1.) + color * visibility;
}

void initCamera(vec2 fragCoord, inout vec3 ro, inout vec3 rd) {
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;
    vec2 m = iMouse.z == 0. ? vec2(.5) : iMouse.xy/iResolution.xy;

    ro = vec3(m.x-.5, m.y-.5, .01)*.8;

    #if BEHIND_THE_SCENE
    ro.x += 8.; ro.z -= 8.;
    #endif

    vec3 f = normalize(vec3(0., 0., 1.) - ro*.05),
         r = normalize(cross(vec3(0,1,0), f)),
         u = cross(f,r),
         c = ro+f,
         i = c + uv.x*r + uv.y*u;    
    rd = normalize(i-ro); // camera direction
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 ro, rd;   
    initCamera(fragCoord, ro, rd);

    // moon
    float moon = dot(rd, vec3(0., 0., 1.));
    vec3 color = (.7-fbm(rd*10.)*3.) * vec3(smoothstep(.995, .9955, moon));
    color += (1.25-color)*pow(moon+.1, 6.)*.25;

    // stars (removed due to flickering)
    vec3 h = hash33(vec3(floor(rd.xy*80.), 0.));
    float stars = length(fract(rd.xy*80. + h.xy*.75)-.5);
    // color += .006/stars * h.z;

    // clouds
    vec2 hit = boxIntersection(ro - vec3(0., 0., 4.), rd);
    marchVolume(ro, rd, hit.x, hit.y, color);

    fragColor = vec4(color, 1.);
}

vec2 hash22(vec2 p) //hashwithoutsine; hash functions return a random number based on a non-random input, 
// e.g. a range of numbers 0, 1, 2, and 3 each hashed might return a new sequence 0.5, 0.2, 0.7, and 0.9.
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float voronoi(vec2 p, float jitter, out vec2 f1, out vec3 f2)
{
    float r = 100000.0; // A big number
    vec2 fp = floor(p); // Round the current position down to the nearest whole number
    f1 = vec2(0);
    f2 = vec3(0);
    for(int x = -1; x <= 1; x++) // Loop over the space near the point
    for(int y = -1; y <= 1; y++) // Same thing
    {
        vec2 cell = fp+vec2(x, y); // Get the current square cell
        vec2 rp = cell+0.5+(hash22(cell)-0.5)*jitter; // Each square cell the point can be in has a single point, offset by a random value times the jitter
        float d = distance(p, rp); // Get the distance between the random point and the input position
        if(d < r) // If the distance is the closer than the current closest distance, it becomes the closest distance (along with the position of the point)
        {
            f2.z = r;
            r = d;
            f2.xy = f1;
            f1 = rp;
        }
    }
    return r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; // Convert coordinates to 0.0 to 1.0 range
    uv.y *= iResolution.y/iResolution.x; // Scale coordinate so that the tiles don't appear squashed

    vec2 mouse = iMouse.x == 0.0? vec2(smoothstep(-1., 1., sin(iTime)),smoothstep(-1., 1., cos(iTime))) // Animate mouse x and y when no mouse input (thumbnail) according to a square circle
                 : iMouse.xy/iResolution.xy; // Otherwise use the actual mouse input if there is some
    mouse.y *= iResolution.y/iResolution.x; // Scale mouse coordinate so that they match the range of the UV coords

    vec2 f1; // Functions can sort of "return" multiple values by using the "out" keyword, so this variable will store the position of the closest point
    vec3 f2; // Read comment for f1. vec3 because this variable stores both the position and the distance, whereas f1 only stores the position
    float v = voronoi(uv*10.0, mouse.x, f1, f2); // Evaluate voronoi

    float space = texelFetch(iChannel0, ivec2(32, 2), 0).r;

    vec3 col = uv.y < mouse.y? vec3(space > 0.5? (f2.z) : v) : vec3((space > 0.5? f2.xy : f1)/10., 0.0); // Split the screen between showing the distance and the position based on mouse position

    fragColor = vec4(col, 1.0);
}

