#define num 11.
#define Y   fract(iTime*0.25)
#define v1  1.015
#define v2  1.
#define v3  1.25
#define v4  1.

#define PI   3.1415926535
#define PI2  6.2831853071
#define PI05 1.5707963267

float sdDots( in vec2 p ) {

    float ang = atan(p.x, p.y);
    float len = length(p);

    float zone  = mod( ang / PI2 * num + 0.5, num);
    vec2  zone2 = (floor(zone) + vec2(0., sign( fract(zone) - 0.5 ))) / num;

    float sa  = sin(PI / num) * v1;
    float n2  = 2. / (sa + v3) - 1.; 

    float l   = log(len) / log(n2);
    vec2 Y2   = Y - zone2 * v4;
    vec2 val2 = pow( vec2(n2), floor( l + Y2 ) - Y2 );
    vec2 ab   = ang - zone2 * PI2;

    vec2 p2 = vec2(cos(ab.x), sin(ab.x)) * len;
    vec2 p3 = vec2(cos(ab.y), sin(ab.y)) * len;
    vec2 r2 = val2 * sa * v2;

    float t = length(p2-vec2(val2.x,   0.)) - r2.x;
    t = min(t,length(p3-vec2(val2.y,   0.)) - r2.y    );
    t = min(t,length(p2-vec2(val2.x*n2,0.)) - r2.x*n2 );            
    t = min(t,length(p3-vec2(val2.y*n2,0.)) - r2.y*n2 );

    return t;
}


