float f_n_difference_to_line_function(
    vec2 a_n_start, 
    vec2 a_n_end, 
    vec2 a_n_pixel_coord_normalized
){
    //remember that math lesson ?
    // y = m*x+q
    float n_delta_y = a_n_end.y - a_n_start.y; 
    float n_delta_x = a_n_end.x - a_n_start.x; 
    float n_m = n_delta_y / n_delta_x;
    float n_q = -(n_m*a_n_start.x) + a_n_start.y;

    float n_x = a_n_pixel_coord_normalized.x;

    float n_y = n_m * n_x + n_q; 

    float n_difference = abs(a_n_pixel_coord_normalized.y - n_y);
    return n_difference;

}


