void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_mouse_coords_norm = iMouse.xy / iResolution.xy;
    vec2 a_n_frag_coords_norm = fragCoord.xy / iResolution.xy;

    float n_high_prime = 1471343.0;
    float n_high_prime_modified = n_high_prime * a_n_mouse_coords_norm.x;

    float n_tau = 6.2831;
    int n_index_pixel = 
        int(
            a_n_frag_coords_norm.x * iResolution.x + 
            (a_n_frag_coords_norm.y * iResolution.y) * iResolution.x
           ); 
    int n_index_pixel2 = 
        int(fragCoord.x + fragCoord.y * iResolution.x);
    float n_index_pixel_normalized = float(n_index_pixel2) / iResolution.x * iResolution.y;
    float n_freq = float(n_index_pixel_normalized); 


    float n_y = sin(n_freq*n_tau) * n_high_prime_modified;

    float n_rand_method1 = abs(mod(n_y,1.0));
    float n_rand_method2 = n_y - floor(n_y);


    float n_y2 = sin(n_freq*n_tau*n_rand_method2) * n_high_prime;
    float n_rand2 = n_y2 - floor(n_y2);

    float n_y3 = sin(n_freq*n_tau*n_rand2) * n_high_prime;
    float n_rand3 = n_y3 - floor(n_y3);
    fragColor = vec4(
        n_rand_method2, 
        n_rand2, 
        n_rand3, 
        1.0
    );
