float mNoise( in vec3 pos )
{
    vec3 q = pos;
    const mat3 m = mat3( 0.20,  0.80,  0.60,
                    -0.80,  0.36, -0.48,
                    -0.60, -0.48,  0.64 );

    float amplitude = .65;
    float f  = amplitude*noise( q );
    q = m*q*2.11;
    float scale = 2.02;
    for (int i = 0; i < 3; ++i)
    {    
    	f += amplitude * noise( q );
        q = m*q*scale;        
        amplitude *= 0.5;

    }
    return f;
}



vec2 polarMap(vec2 uv, float shift, float inner)
{
    float px = 1.0 - fract(atan(uv.y, uv.x) / 6.28 + 0.25) + shift;
    float py = (sqrt(uv.x * uv.x + uv.y * uv.y) * (1.0 + inner * 2.0) - inner) * 2.0;
    return vec2(px, py);
}

