#define pow2(x) (x * x)

const float pi = atan(1.0) * 4.0;
const int samples = 35;
const float sigma = float(samples) * 0.25;

float gaussian(vec2 i) {
    return 1.0 / (2.0 * pi * pow2(sigma)) * exp(-((pow2(i.x) + pow2(i.y)) / (2.0 * pow2(sigma))));
}

vec3 blur(sampler2D sp, vec2 uv, vec2 scale) {
    vec3 col = vec3(0.0);
    float accum = 0.0;
    float weight;
    vec2 offset;

    for (int x = -samples / 2; x < samples / 2; ++x) {
        for (int y = -samples / 2; y < samples / 2; ++y) {
            offset = vec2(x, y);
            weight = gaussian(offset);
            col += texture(sp, uv + scale * offset).rgb * weight;
            accum += weight;
        }
    }

    return col / accum;
}

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;

    color.rgb = blur(iChannel0, uv, ps);
    color.a = 1.0;
}

////////////////////////////////////////////////////////////////////////////////
//
// Cos Accuracy II, mla 2022.
//
////////////////////////////////////////////////////////////////////////////////
//
// Trig function errors. This is mainly intended to show what happens on Intel
// where cos and sin are computed with a simple piecewise linear
// approximation, though there is a check for GPUs with better trig functions,
// eg. Nvidia, and zooms in vertically if so (it's just checking a single
// value so might give false positives though).

// Red channel: absolute error in cos(x)
// Green channel: absolute error in sin(x)
// Blue channel: ulps error in cos(x)
//
// Numeric display shows the main trig functions evaluated at the mouse position
// and compared with the more accurate myxxx functions. We also compute the inverse
// functions for comparison with the original theta.
//
//         mycos(theta)       mysin(theta)       mytan(theta)
//         cos(theta)         sin(theta)         tan(theta)
// theta   acos(mycos(theta)) asin(mysin(theta)) atan(mytan(theta))
//
////////////////////////////////////////////////////////////////////////////////

// mycos and mysin have been checked on CPU & agree to a few ulps with
// builtin functions in range [0..pi/2] (ulp error is larger when result is
// closer to zero, but absolute error is fine). It's possibly worth using
// sin(pi/2-x) for cos(x) in upper half of range, but there isn't a lot in it.

//#define CRENSHAW

float mycos0(float x, int N) {
  N *= 2;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n-1));
  }
  return y;
}

float mycos(float x) { return mycos0(x,8); }

// This is a pretty good Chebyshev approximaton to cos(x)
float mycos1(float x) {
  assert(x >= -1.5707963 && x <= 1.5707963);
  float x2 = x*x;
  float t = -0.0012711104;
  t = x2*t + 0.041487381;
  t = x2*t + -0.4999121;
  t = x2*t + 0.99999325;
  return t;
}

float mysin1(float x) {
  assert(x >= -1.5707963 && x <= 1.5707963);
  float x2 = x*x;
  float t = -0.00018362749;
  t = x2*t + 0.0083062861;
  t = x2*t + -0.16664824;
  t = x2*t + 0.9999966;
  t = x*t;
  return t;
}

float mysin(float x) {
  int N = 10;
  float x2 = x*x, y = 1.0;
  for (int n = N; n > 0; n-=2) {
    y = 1.0-x2*y/float(n*(n+1));
  }
  return x*y;
}

// From https://www.shadertoy.com/view/md3cDM by Envy24
// Source: "Math Toolkit For Real-Time Development", Jack W. Crenshaw
vec2 sincos(float x)
{
    const float sin_30 =      0.5;
    const float cos_30 =      0.8660254037844386;
    const float pi_over_six = 0.5235987755982988;    // pi / 6
    const float inv =         1.9098593171027440292; // 1 / pi_over_six
    const float tau =         6.2831853071795862;
    const float inv_tau =     0.1591549430918953;

    // Increases accuracy for negative angles (see this shader: https://www.shadertoy.com/view/NtVBWG)
    float X = fract(x*inv_tau);
          x = X*tau;

    int n = int(x * inv + 0.5); 
    x -= float(n) * pi_over_six;

    n = n < 0 ? (n + 12) % 12 : n % 12;
    //n %= 12; if (n < 0) n += 12;

    float z = x*x, s = 0., c = 0.,
          s1 = ((z/20.0-1.0)*z/6.0+1.0)*x,
          c1 = ((z/30.0+1.0)*z/12.0-1.0)*z/2.0+1.0;        

    switch(n)
    {
    case 0:  s =  s1;                        c =  c1;                        break;
    case 1:  s =  cos_30 * s1 + sin_30 * c1; c = -sin_30 * s1 + cos_30 * c1; break;
    case 2:  s =  sin_30 * s1 + cos_30 * c1; c = -cos_30 * s1 + sin_30 * c1; break;
    case 3:  s =  c1;                        c = -s1;                        break;
    case 4:  s = -sin_30 * s1 + cos_30 * c1; c = -cos_30 * s1 - sin_30 * c1; break;
    case 5:  s = -cos_30 * s1 + sin_30 * c1; c = -sin_30 * s1 - cos_30 * c1; break;
    case 6:  s = -s1;                        c = -c1;                        break;
    case 7:  s = -cos_30 * s1 - sin_30 * c1; c =  sin_30 * s1 - cos_30 * c1; break;
    case 8:  s = -sin_30 * s1 - cos_30 * c1; c =  cos_30 * s1 - sin_30 * c1; break;
    case 9:  s = -c1;                        c =  s1;                        break;
    case 10: s =  sin_30 * s1 - cos_30 * c1; c =  cos_30 * s1 + sin_30 * c1; break;
    case 11: s =  cos_30 * s1 - sin_30 * c1; c =  sin_30 * s1 + cos_30 * c1; break;
    }

    return vec2(s, c);
}

// Chebyshev approximation tailored for 45 interval
float mysin2(float x) {
  assert(x >= -0.78539816 && x <= 0.78539816);
  float x2 = x*x;
  float t = 0.0081214934;
  t = x2*t + -0.16660157;
  t = x2*t + 0.99999499;
  t = x*t;
  return t;
}

float mycos2(float x) {
  assert(x >= -0.78539816 && x <= 0.78539816);
  float x2 = x*x;
  float  t = -0.0013585844;
  t = x2*t + 0.041655021;
  t = x2*t + -0.49999857;
  t = x2*t + 0.99999997;
  return t;
}

