#define time iTime*pi/10.

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}


vec3 cpos() {
    vec3 c = vec3(0.,0.,-2.);
    c.xz *= r2d(deg*40.);
    c.yz *= r2d(deg*-40.);
    c.xz *= r2d(sin(time*2.)*0.2-0.1);
    c.yz *= r2d(cos(time*2.)*0.2-0.1);
    c.xz *= r2d(time*2.);
    //c.yz *= r2d(time);
    return c;
}

mat4 eye() {
    vec3 c = cpos();
    vec3 l = vec3(0.);
    vec3 f = normalize(l-c);
    vec3 r = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = cross(f,r);
    return mat4(r,0.,u,0.,f,0.,vec3(0.),1.);
}

float sdBox(vec3 p, vec3 s) {
    vec3 q = abs(p)-s;
    return max(q.z,max(q.x,q.y));
}

vec4 map(vec3 p) {
    vec3 o = p;
    p = (eye()*vec4(p,1.)).xyz;
    p += cpos();
    float d = sdBox(p,vec3(0.5));
    d = min(d,sdBox(p-vec3(0.4),vec3(0.2)));
    return vec4(p,d);
}

vec4 dbmap(vec3 p) {
    vec3 o = p;
    p = (eye()*vec4(p,1.)).xyz;
    p += cpos();
    float d = map(o).w;


    //d = min(d,abs(p.z)-0.02);
    d = abs(p.z)-0.01;
    return vec4(p,d);
}

