////////////////////////////////////////////////////////////////////////////////
//
// Gosper Closed Curve. mla, 2022.
//
// Six Gosper curves joined back to back and coloured.

// Uses an explicit stack to keep track of the recursion.
// Keeps track of intermediate segments and avoids recursing far from
// the point being plotted.
//
// Mouse down shows segments actually drawn around mouse position
//
////////////////////////////////////////////////////////////////////////////////


#define C(turns,offset) (((turns) << 16) | ((offset) & 0xffff))
// Each instruction is an integer number of turns, followed by
// either an instruction offset for the recursive call or -1
// to terminate the current level.
const int A = 0, B = 8, END = -1, L = -1, R = 1;
const int program[16] =
  int[] (C(0,A),C(L,B),C(L+L,B),C(R,A), C(R+R,A),C(0,A),C(R,B),C(L,END),
         C(R,A),C(L,B),C(0,B),C(L+L,B), C(L,A),C(R+R,A),C(R,B),C(0,END));

const float angle = PI/3.0;
const int maxdepth = 4;

vec2 p = vec2(0);       // The drawing point
vec2 point = vec2(100); // The moving point
float plen = 0.0;       // Cumulative path length
int wind = 0;           // Winding number here

void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.y <= 0.0) {
    if (r.y > 0.0 && t > 0.0) wind++;
  } else {
    if (r.y <= 0.0 && t < 0.0) wind--;
  }
}

float drawsegment(vec2 pos, float seglen, float theta, float t) {
  vec2 seg = seglen*vec2(cos(theta),sin(theta));
  if (t >= plen && t < plen+seglen) point = p + (t-plen)/seglen*seg;
  vec2 q = p + seg;
  float d = segment(pos,p,q);
  polywind(pos,p,q);
  plen += seglen;
  p = q;
  return d;
}

// Segment rotation at each recursion level - just the net turn the path pattern.
float magic = 0.333473; 

vec2 centre = vec2(1.5,0.35);
vec2 map(vec2 pos) {
  pos = (2.0*pos - iResolution.xy)/iResolution.y;
  //pos = rotate(pos,0.1*iTime);
  pos *= 4.2;
  pos -= centre;
  return pos;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 pos = map(fragCoord);
  vec2 pos2 = (iMouse.z <= 0.0) ? pos : map(iMouse.xy);
  int stack[maxdepth];
  int N = 6;
  float factor = sqrt(7.0);
  float totallength = float(N)*factor*pow(factor,float(maxdepth));
  float t = mod(iTime,totallength); // Moving point
  float fillhue;
  float d = 1e8;
  float theta = 0.0;    // Cumulative angle
  //N = min(N,int(iTime));
  for (int i = 0; i < N; i++, theta-=((i&1)==0?0.0:2.0*PI/3.0)){
    int depth = 0;
    stack[depth++] = B;//(i&1)==0?A:B;
    float seglen = 1.0;
    while (depth > 0) {
      int instr = program[stack[depth-1]++];
      int turns = instr>>16;    // Decode instruction
      int code = instr<<16>>16;
      theta += float(turns)*angle; // Apply turn
      if (code == END) {
        depth--;    // Terminate level & unwind
        seglen *= factor;
      } else if (depth == maxdepth) {
        float d0 = drawsegment(pos,seglen,theta,t); // Segment
        if (d0 < d) {
          fillhue = plen/totallength;
          d = d0;
        }
      } else {
        // The segment is a diagonal of a hexagon off to the left or the right,
        // so centre is at 30 to the segment, at distance radius of hexagon.
        float offset = float(maxdepth-depth)*magic; // Segment angle adjustment
        float sign = code == A ? -1.0 : 1.0;
        float centreangle = sign*PI/6.0 - offset;
        float radius = seglen/sqrt(3.0);
        vec2 centre = p + radius*vec2(cos(theta+centreangle),sin(theta+centreangle));
        float bound = 1.1*radius; // 1.0 gives some artefacts, 1.05 seems OK. 
        if (distance(pos2,centre) > bound) {
          // Short circuit this segment
          vec2 seg = seglen*vec2(cos(theta-offset),sin(theta-offset));
          polywind(pos,p,p+seg);
          // Show skipped segments on mouse down.
          if (iMouse.z > 0.0) d = min(d,segment(pos,p,p+seg));
          p += seg;
          plen += seglen*pow(factor,float(maxdepth-depth));
        } else {
          stack[depth++] = code;  // Recurse
          seglen /= sqrt(7.0);
        }
      }
    }
  }
  polywind(pos,p,vec2(0));
  float px = fwidth(pos.x);
  vec3 col = vec3(0);
  vec3 fillcol = h2rgb(fillhue+0.00*iTime);
  if ((wind&1) == 0) col = vec3(1);
  col = mix(fillcol,col,smoothstep(0.0,px,d-0.01));
  col = mix(vec3(0), col,smoothstep(0.0,px,distance(pos,point)-0.02));
  col *= 1.0-smoothstep(0.0,px,distance(pos,vec2(-1.5,-0.35))-4.2);
  col *= 1.6/(1.0+col);
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

