// DEFINES -------------------------------------------------------------------//

// Ray marching-related
#define MAX_STEPS 1000
#define MAX_DIST 1e3
#define MIN_DIST 5e-3

// Optical-effect related
#define N_REFLS 2
#define SHADW_SHARPN 50
#define MIN_LIGHT .0125

// Other shader quantities
#define PERIOD 10.
#define BCKG_COL vec3(.3+.15*cos(p.x/1000.-iTime/PERIOD))

// Misc
#define PI 3.141593
#define TP 2.*PI
#define OFFSET (10.*MIN_DIST)
#define VECX vec3(1.,0.,0.)
#define VECY vec3(0.,1.,0.)
#define VECZ vec3(0.,0.,1.)

// MISC ----------------------------------------------------------------------//

mat3 rx(float t)
{
    float ct = cos(t);
    float st = sin(t);
    return mat3(1., 0., 0., 0., ct, -st, 0., st, ct);
}
mat3 ry(float t)
{
    float ct = cos(t);
    float st = sin(t);
    return mat3(ct, 0., st, 0., 1., 0., -st, 0., ct);
}
mat3 rz(float t)
{
    float ct = cos(t);
    float st = sin(t);
    return mat3(ct, -st, 0., st, ct, 0., 0., 0., 1);
}
vec3 lattice(vec3 p, vec3 c)
{
    return mod(p+0.5*c, c)-0.5*c;
}

// PRIMITIVE SIGNED DISTANCE FUNCTIONS (SDFs) AND OPERATIONS -----------------//

float SDFSphere(vec3 p, float r)
{
    return length(p)-r;
}

float SDFBox(vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q, 0.)) + min(max(q.x, max(q.y,q.z)),0.);
}

float SDFInfColY(vec3 p, vec2 b)
{
    vec2 q = abs(p.xz) - b;
    return length(max(q, 0.)) + min(max(q.x, q.y),0.);
}

float DIntersect(float d0, float d1)
{
    return max(d0, d1);
}

float DSmoothIntersect(float d0, float d1, float k)
{
    return .5*(d0+d1+sqrt((d0-d1)*(d0-d1)+k));
}

float DUnite(float d0, float d1)
{
    return min(d0, d1);
}

float DSmoothUnite(float d0, float d1, float k)
{
    float h = clamp( 0.5 + 0.5*(d1-d0)/k, 0.0, 1.0 );
    return mix(d1, d0, h ) - k*h*(1.0-h);
}

float DSubtract(float d0, float d1)
{
    return max(d0, -d1);
}

float DSmoothSubtract(float d0, float d1, float k)
{
    return .5*(d0-d1+sqrt((d0+d1)*(d0+d1)+k));
}

// SURFACE -------------------------------------------------------------------//

struct Surface
{
    float d; // Distance from cast ray origin
    vec3 c; // Surface color
    float r; // Surface reflectance
};

Surface SIntersect(Surface s0, Surface s1)
{
    Surface s;
    if (abs(s0.d) < abs(s1.d))
        s = s0;
    else
        s = s1;
    s.d = max(s0.d, s1.d);
    return s;
}

Surface SSmoothIntersect(Surface s0, Surface s1, float k)
{
    float d = DSmoothIntersect(s0.d, s1.d, k);
    float h = d/s0.d;
    vec3 c = s1.c*(1.-h) + s0.c*h;
    float r = mix(s1.r, s0.r, h);
    return Surface(d, c, r);
}

Surface SUnite(Surface s0, Surface s1)
{
    if (s0.d < s1.d)
        return s0;
    return s1;
}

Surface SSmoothUnite(Surface s0, Surface s1, float k)
{
    float h = clamp(.5 + .5*(s1.d-s0.d)/k, 0.0, 1.0 );
    float d = mix(s1.d, s0.d, h ) - k*h*(1.0-h);
    vec3 c = s1.c*(1.-h) + s0.c*h;
    float r = mix(s1.r, s0.r, h);
    return Surface(d, c, r);
}

Surface SSubtract(Surface s0, Surface s1)
{
    return Surface(max(s0.d, -s1.d), s0.c, s0.r);
}

Surface SSmoothSubtract(Surface s0, Surface s1, float k)
{
    Surface s = s0;
    s.d = DSmoothSubtract(s0.d, s1.d, k);
    return s;
}

// GLOBAL SCENE SDF ----------------------------------------------------------//

Surface sceneSDF(vec3 p)
{
    float t = TP*iTime/PERIOD;
    p = rx(t+.5*p.x)*(p);
    p.y = abs(p.y)-5.-1.*sin(t);
    float h = 10.+6.*sin(t/4.);
    vec3 pb = lattice(p-h*fract(t)*VECX, h*VECX);//*ry(p.y*sin(t));
    Surface s1 = Surface(SDFInfColY(pb, vec2(.5)), VECX, 0.25);
    s1.d -= .25;
    s1.d -= 2.*sin( .5*p.y - t)*(.5+.5*sin(t/4.));
    Surface s2 = Surface(SDFSphere(p+VECY*(2.), 4.), vec3(1.,.5,0.), .75);
    s2.d -= .25*sin(5.*p.x+25.*t);
    Surface pl = Surface(p.y+2., vec3(.6,.3,.15), 0.25);
    pl.d += .1*sin(p.x*10.+20.*t)+.1*sin(p.z*5.-20.*t);
    s1 = SSmoothUnite(s1, s2, 4.+2.*sin(2.*t));
    float x = .5;//1.+.9*sin(t/2.);
    s1.d -= x;
    pl = SSmoothSubtract(pl, s1, 0.5);
    s1.d += x;
    s1 = SUnite(s1, pl);
    float m = sin(t)*sin(.5*p.y-5.*t)+cos(t)*sin(0.25*p.x-.5*t);
    s1.d = DSmoothIntersect(
        s1.d, 
        SDFBox(p-vec3(0.,0.,0), vec3(MAX_DIST, 6., 3.+2.*m)),
        0.125
    );
    s1.d /= 7.5;
    return s1;
}

// RAY MARCHING RELATED ------------------------------------------------------//

struct Ray 
{
    vec3 orig; // Ray origin
    vec3 dir; // Ray direction
    float af; // Attenuation factor
    int n; // Number of bounces
};

Ray newRay(vec3 orig, vec3 dir)
{
    return Ray(orig, dir, 1., 0);
}

Ray cameraRay(vec2 uv, vec3 cp, vec3 la, float zoom)
{
    vec3 f = normalize(la-cp);
    vec3 r = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f, r));
    vec3 c = cp + f*zoom;
    return newRay(cp, normalize(c + uv.x * r + uv.y * u-cp));
}

Surface rayMarch(Ray ray, out float pn)
{
    Surface s;
    float d, dd, dd0 = MIN_DIST;
    pn = 1.;
    for (int iter = 0; iter < MAX_STEPS; iter++)
    {
        dd0 = dd; // I am not using dd0 for anything, I know...
        s = sceneSDF(ray.orig + ray.dir*d);
        dd = s.d;
        if (dd > 0. && dd <= MIN_DIST || d >= MAX_DIST)
            break;
        pn = min(pn, float(SHADW_SHARPN)*dd/d);
        d += dd;
    }
    s.d = d;
    return s;
}

Surface rayMarch(Ray ray) // Overload without penumbra inout
{
    float tmp;
    return rayMarch(ray, tmp);
}

vec3 getNormal(vec3 p)
{
    float d = sceneSDF(p).d;
    vec2 e = vec2(OFFSET, 0.);
    vec3 n = d - vec3
        (
            sceneSDF(p-e.xyy).d,
            sceneSDF(p-e.yxy).d,
            sceneSDF(p-e.yyx).d
        );
    return normalize(n);
}

float getLighting(vec3 p, inout vec3 n)
{
    // Light source position
    vec3 lp = vec3(-.5, 0, -40.);
    n = getNormal(p);
    p += n*OFFSET;
    vec3 dir = normalize(lp-p);
    float pn;
    if (rayMarch(newRay(p, dir), pn).d < length(lp-p))
        return MIN_LIGHT;
    return max(abs(dot(n, dir)*pn), MIN_LIGHT);
}

void propagate(inout Ray ray, inout vec3 col)
{
    // If the ray is fully attenuated, stop propagating
    if (ray.af == 0.)
        return;
    Surface s = rayMarch(ray);
    vec3 p = ray.orig + ray.dir*s.d;
    //ray.af *= exp(s.d);
    vec3 lCol, n;
    lCol = BCKG_COL;
    if (s.d < MAX_DIST)
        lCol = max(getLighting(p, n), 0.)*s.c;
    // If at the last reflection, set reflectance of hit surface to 0
    // so it is rendered as fully opaque
    s.r *= float(ray.n<N_REFLS);
    // Cumulate col
    col += lCol*ray.af*(1.0-s.r);
    // Adjust attenuation by currently hit surface and prepare for next
    // propagation
    ray.af *= s.r;
    if (ray.af != 0.)
    {
        ray.orig = p+n*OFFSET;
        ray.dir = reflect(ray.dir, n);
    }
    ray.n++;
}

vec3 render(Ray r)
{
    // Base pass
    vec3 col = vec3(0.);
    propagate(r, col);
    // Reflection passes
    for (int i = 0; i < N_REFLS; i++)
    {
        propagate(r, col);
    }
    col = pow(col, vec3(.4545));
    return col;
}

// MAIN ----------------------------------------------------------------------//

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    // Camera position, look-at point and resulting ray
    float t = TP*iTime/PERIOD;
    vec3 cp = vec3(-25.*sin(t/2.), 1., -25.);
    vec3 la = vec3(0., 0., 0.);
    Ray ray = cameraRay(uv, cp, la, 1.);
    // Render pixel
    fragColor = vec4(render(ray),1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.x*7.0;

    vec3 col = vec3(0.75);
    float v = sin(pow(uv.x, 2.0)) + sin(pow(uv.y, 2.0));
    bool b = abs(v-2.0*sin(iTime/3.0))<0.1;
    if (b){
        // Time varying pixel color
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

const float n_tau = 6.28318530;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 a_n_frag_coord_norm = fragCoord.xy / iResolution.xy;
    vec2 a_n_mouse_coord_norm = iMouse.xy / iResolution.xy;

    float n = 0.0; 
    float n_amp = 0.4;
    float n_freq_1 = a_n_frag_coord_norm.x*n_tau; 
    float n_y = 
        sin(n_freq_1)*n_amp
        +(0.5);

    float n_freq_2 = 
        a_n_frag_coord_norm.x*n_tau*
        a_n_mouse_coord_norm.x*20.0;

    n_y += sin(n_freq_2)*0.1;
    float n_distance = length(
        vec2(
           a_n_frag_coord_norm.x, 
           n_y
        )
        -
        a_n_frag_coord_norm
    ); 

    fragColor = vec4(1.0-n_distance*20.0);

        // mirror it 
    if(a_n_frag_coord_norm.x>0.5){
        fragColor = vec4(1.0) - fragColor;
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 20.0*(2.0*fragCoord-iResolution.xy)/iResolution.x;

    vec3 col = vec3(0.75);
    float v = sin(uv.x)+sin(uv.y);
    bool b = abs(v-2.0*sin(iTime/3.0))<0.1;
    if (b){
        // Time varying pixel color
        col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .0005
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define Tri(p,s,a) max(-dot(p,vec2(cos(-a),sin(-a))),max(dot(p,vec2(cos(a),sin(a))),max(abs(p).x-s.x,abs(p).y-s.y)))
#define DF(a,b) length(a) * cos( mod( atan(a.y,a.x)+6.28/(b*8.0), 6.28/((b*8.0)*0.5))+(b-1.)*6.28/(b*8.0) + vec2(0,11) )
#define ZERO (min(iFrame,0))

float Hash21(vec2 p) {
    p = fract(p*vec2(234.56,789.34));
    p+=dot(p,p+34.56);
    return fract(p.x+p.y);
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdTorus( vec3 p, vec2 t )
{
    vec2 q = vec2(length(p.xy)-t.x,p.z);
    return length(q)-t.y;
}

// thx iq! https://iquilezles.org/articles/distfunctions/
float sdBox( vec3 p, vec3 b )
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

// thx iq! https://iquilezles.org/articles/distfunctions/
// tweaked as the center aligned horizontal capsule. 
float sdHorizontalCapsule( vec3 p, float w, float r )
{
  p.x-= clamp( p.x, -w*0.5, w*0.5 );
  return length( p ) - r;
}

float speaker(vec3 p){
    vec3 prevP = p;
    float d = sdBox(p, vec3(0.45,0.95,0.34))-0.03;
    float d2 = length(p-vec3(0.,-0.2,-0.53))-0.38;

    d = max(-d2,d);

    d2 = sdTorus(p-vec3(0.,-0.2,-0.36),vec2(0.36,0.03));
    d = min(d,d2);

    d2 = sdTorus(p-vec3(0.,-0.2,-0.32),vec2(0.32,0.025));
    d = min(d,d2);
    d2 = length(p-vec3(0.,-0.25,-0.08))-0.12;
    d = min(d,d2);

    d2 = sdHorizontalCapsule(p-vec3(0.,-0.75,-0.36),0.6,0.06);
    d = max(-d2,d);

    d2 = length(p-vec3(0.,0.55,-0.36))-0.2;
    d = max(-d2,d);

    d2 = sdTorus(p-vec3(0.,0.55,-0.36),vec2(0.2,0.03));
    d = min(d,d2);

    p.z-=-0.36;
    p.x = abs(p.x)-0.4;
    p.y = abs(p.y)-0.9;
    d2 = length(p)-0.03;
    d = min(d,d2);

    return d;
}

float speaker2(vec3 p){
    vec3 prevP = p;
    float d = sdBox(p, vec3(0.95,0.45,0.34))-0.03;
    float d2 = sdBox(p-vec3(0.,0.,-0.35), vec3(0.9,0.4,0.01))-0.03;
    d = max(-d2,d);

    p.x = abs(p.x);
    d2 = length(p-vec3(0.4,0.,-0.5))-0.36;
    d = max(-d2,d);

    d2 = sdTorus(p-vec3(0.4,0.,-0.3),vec2(0.34,0.03));
    d = min(d,d2);

    d2 = sdTorus(p-vec3(0.4,0.,-0.29),vec2(0.3,0.025));
    d = min(d,d2);
    d2 = length(p-vec3(0.45,0.,-0.08))-0.1;
    d = min(d,d2);

    p.z-=-0.3;
    p.x = abs(p.x)-0.86;
    p.y = abs(p.y)-0.36;
    d2 = length(p)-0.03;
    d = min(d,d2);    

    return d;
}

float speaker3(vec3 p){
    vec3 prevP = p;
    float d = sdBox(p, vec3(0.95,0.95,0.34))-0.03;

    float d2 = length(p-vec3(0.0,0.,-0.68))-0.66;
    d = max(-d2,d);

    d2 = sdTorus(p-vec3(0.0,0.,-0.35),vec2(0.64,0.05));
    d = min(d,d2);

    d2 = sdTorus(p-vec3(0.0,0.,-0.33),vec2(0.6,0.045));
    d = min(d,d2);

    d2 = length(p-vec3(0.0,0.,0.1))-0.2;
    d = min(d,d2);

    d2 = sdTorus(p-vec3(0.0,0.,-0.3),vec2(0.56,0.035));
    d = min(d,d2);    

    d2 = sdTorus(p-vec3(0.0,0.,-0.24),vec2(0.52,0.035));
    d = min(d,d2);        

    d2 = sdTorus(p-vec3(0.0,0.,-0.19),vec2(0.47,0.035));
    d = min(d,d2);  

    d2 = abs(length(p.xy)-0.73)-0.07;
    d = min(d,max((abs(p.z)-0.38),d2));

    p.z-=-0.37;
    p.x = abs(p.x)-0.86;
    p.y = abs(p.y)-0.86;
    d2 = length(p)-0.03;
    d = min(d,d2);    

    p = prevP;
    p.z-=-0.37;
    p.xy = DF(p.xy,3.0);
    p.xy -= vec2(0.52);
    d2 = length(p)-0.03;
    d = min(d,d2); 

    p = prevP;
    p.xy*=Rot(radians(sin(iTime)*120.));
    p.z-=-0.37;
    p.y=abs(p.y)-0.93;
    d2 = Tri(p.xy,vec2(0.08),radians(45.));
    d = min(d,max((abs(p.z)-0.02),d2));    

    p = prevP;
    p.xy*=Rot(radians(90.+sin(iTime)*120.));
    p.z-=-0.37;
    p.y=abs(p.y)-0.93;
    d2 = Tri(p.xy,vec2(0.08),radians(45.));
    d = min(d,max((abs(p.z)-0.02),d2));      

    return d;
}

float changeSpeakers(vec3 p, float start, float speed){
    vec3 prevP = p;
    float endTime = 3.;
    float t = iTime*speed;
    float scenes[3] = float[](0.,1.,2.);
    for(int i = 0; i<scenes.length(); i++){
        scenes[i] = mod(scenes[i]+start,endTime);
    }

    float scene = scenes[int(mod(t,endTime))];

    float d = 10.;
    if(scene<1.) {
        p.x=abs(p.x)-0.5;
        d = speaker(p);
    } else if (scene >= 1. && scene<2.){
        p.y=abs(p.y)-0.5;
        d = speaker2(p);
    } else {
        d = speaker3(p);
    }

    return d;
}

vec2 GetDist(vec3 p) {
    vec3 prevP = p;

    p.y -=iTime*0.5;
    vec2 id = floor(p.xy*0.5);
    p.z-=3.;
    p.xy = mod(p.xy,2.0)-1.0;

    id*=.5;
    float rand = Hash21(id);

    float d = 10.;
    p.z-=rand*0.3;
    if(rand<0.3) {
        d = changeSpeakers(p,1.,0.5+rand);
    } else if(rand>=0.3 && rand<0.7) {
        d = speaker3(p);
    } else {
        p.x=abs(p.x)-0.5;
        d = speaker(p);
    }

    return vec2(d,0);
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,10,-10));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 60.)*occ; // spec

    return diffCol;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    col = diffuseMaterial(n,rd,p,vec3(1.3));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 0, -1.5);
    if(iMouse.z>0.){
        ro.yz *= Rot(m.y*3.14+1.);
        ro.y = max(-0.9,ro.y);
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        float scene = mod(iTime,15.);
        float rotY = -10.;
        float rotX = 0.;
        if(scene>=5. && scene<10.){
            rotY = 0.;
            rotX = -30.;
        } else if(scene>=10.){
            rotY = 0.;
            rotX = 30.;
        }

        ro.yz *= Rot(radians(rotY));
        ro.xz *= Rot(radians(rotX));
    }

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
    }

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(sqrt(col),1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = 2.0 * fragCoord/iResolution.x;

    vec3 col = vec3(0.75);

    float a = uv.x + 2.0;
    float y = uv.y;   
    float x = 0.1;

    for(int j = 0; j < 250; j++) x=a*x*(1.0-x);

    for(int j = 0; j < 100; j++){

        x = a*x*(1.0-x);

        // plotPointAt( a, y );
        if (abs(y-x) < 0.003){

            // Time varying pixel color
            col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
            // vec3 col = vec3(0.1);
            fragColor = vec4(col,1.0);
            return;
        }
    }

    // Output to screen
    fragColor = vec4(col, 1.0);

}

#define sunpos vec2(.5,.25)

float clouds(vec2 uv) {
    uv.x += iTime*.001;
    float d = fbm( vec3(uv*vec2(4.,10.)*1.1+50., iTime*.01) );
    d += abs(cos(uv.x*70.)*.5 + cos(uv.x*160.)*.25)*0.07;
    d += uv.y-1.;
    return d+.2;
}

vec4 background(vec2 uv, vec2 p) {
    vec4 col = vec4(0., 0., 0., 1.);

    // greenish sky
    col.rgb = pow( vec3(.5), vec3(5., 2.5*uv.y, 2.*(uv.y*-.1+1.)) );

    // clouds
    float c = smoothstep(0.3,.32,clouds(uv));
    float cs = smoothstep(0.3,.32, clouds(uv - normalize(p-sunpos)*.015));
    col.rgb += vec3(.5,.4,.3) * c * (1.-cs);
    col.rgb += vec3(.5,.4,.3)*.75 * c * (cs);

    // stars
    col.rgb += vec3(1.) * smoothstep(0.8,1., noise(vec3(uv*300., iTime*.1))) * (1.-c);


    // sun
    float d = distance(p, sunpos);
    col.rgb += vec3(1.) * smoothstep(0.16,.15,d);
    col.rgb += vec3(1.,1.,.5) * pow(1./(1.+d), 8.)*1.;

    return col;
}

vec4 pyramids(vec2 p, float freq, float proba) {
    vec4 col = vec4(0., 0., 0., 0.);

    float seed = floor(p.x*freq-.5);
    float h = fract(p.x*freq);
    float d = -p.y + abs(h-.5)/freq * step(proba,hash1(seed));
    float m = smoothstep(0.,0.01,d) ;

    float ds = -p.y + saturate(h-.5)/freq * step(proba,hash1(seed));
    float ms = smoothstep(0.,0.01,ds) ;

    col.rgb = vec3(1.,.5,.0) * smoothstep(0.6,.5, fbm(p*vec2(10.,100.)*freq));
    col.rgb = mix(col.rgb, vec3(1.,.5,.0)*.3, ms);

    col.rgb *= smoothstep(0.,0.015, abs(d-0.005)); // outline

    col.rgb = saturate(col.rgb);

    col.a = max(m, ms);

    return col * col.a;
}

float moutainsHeight(vec2 p, float amp, float power) {
    float d = - pow(abs(sin(p.x*5.)*.5+ sin(p.x*2.+2.5)*.25 + sin(p.x*4.+2.)*.125), power) * amp;
    return d;
}

vec4 mountains(vec2 p, float amp, float power) {
    vec4 col = vec4(0., 0., 0., 0.);

    float h = -p.y + moutainsHeight(p,amp,power);
    float hs = -p.y +moutainsHeight(p + normalize(p-(sunpos*2.-1.))*.05,amp,power);
    float d = smoothstep(0.,0.01,h);
    float ds = smoothstep(0.,0.01,hs);

    col = vec4(1.,1., 1., 1.) * d;
    col.rgb *= vec3(1.,.4,.2)*(smoothstep(0.,-1.,ds-d)*.75+.25);
    col.rgb *= smoothstep(0.,0.02,abs(h-.01)); // outline
    //col.rgb *= (sin(d*50.+fbm(p*vec2(5.,50.)))*.5+.5)*.5+.5;
    col.rgb = saturate(col.rgb);
    return col * col.a;
}

vec4 cactus(vec2 p, float freq) {

    vec4 col = vec4(0., 0., 0., 0.);


    vec2 ip = floor(p*freq);
    vec2 fp = fract(p*freq)-.5;
    float seed = hash1(ip.x);
    fp.y = p.y*2. + (seed)*.4;

    if (hash1(ip.x+1000.) > .3) {
        return vec4(0.);
    }

    float d = line(fp, vec2(0.,-.3), vec2(0.,.3));

    if (hash1(ip.x+100.) > .5) {
        fp.x = -fp.x;
    }

    if (seed > .25) {
        d = min(d, line(fp, vec2(0.,0.), vec2(0.3,.0))*1.8);
        d = min(d, line(fp, vec2(0.3,0.015), vec2(0.3,.2))*1.3);
    }

    d = min(d, line(fp, vec2(0.,-.15), vec2(-0.3,-.15))*1.8);
    d = min(d, line(fp, vec2(-0.3,-.14), vec2(-0.3,.05))*1.3);
    d = d-p.y*.3 - fbm(p*300.+5.)*.005;


    col = vec4(vec3(0.4,1.,0.)*.5 * (smoothstep(0.5,.6, fbm(p*vec2(300.,5.)+5.)*.5+.5)*.25+.75), smoothstep(0.1,0.09,d));

    col.rgb *= vec3(1.) * smoothstep(0.007,0.012, abs(d-.098)); // outline

    return col * col.a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 invRes = vec2(1.) / iResolution.xy;
    vec2 uv = fragCoord * invRes;
    vec2 p = (2.*fragCoord - iResolution.xy) / iResolution.y;
    vec2 pp = p;


    // background
    vec4 col = background(uv, pp);


    // layers
    #define LAYER_SPEED 0.05
    #define LAYER_COUNT 8
    vec4 layer[LAYER_COUNT];
    p.x += iTime*LAYER_SPEED;    layer[0] = mountains(p*vec2(.5,3.)-vec2(0.,.3), .75, 1.2) * vec4(vec3(.25),1.);
    p.x += iTime*LAYER_SPEED;    layer[1] = pyramids(p-vec2(0.,-.4), 1., .6);
    p.x += iTime*LAYER_SPEED;    layer[2] = pyramids(p-vec2(0.,-.39), .5, .6);
    p.x += iTime*LAYER_SPEED;    layer[3] = mountains(p*vec2(.25,2.25)-vec2(10.,-0.5), 1., 1.2);
    p.x += iTime*LAYER_SPEED;    layer[4] = cactus(p*1.5-vec2(0.,-.7),3.)*1.;
    p.x += iTime*LAYER_SPEED;    layer[5] = mountains(p*vec2(.25,2.)-vec2(0.,-0.6), 1., 1.2);
    p.x += iTime*LAYER_SPEED;    layer[6] = mountains(p*vec2(.15,2.)-vec2(1000.,-0.7), 1., 1.2);
    p.x += iTime*LAYER_SPEED;    layer[7] = cactus(p*.2-vec2(0.,-.0),3.);

    // merge layers with alpha premultiplied
    for(int i=0; i<LAYER_COUNT; i++) {
        col.rgb = col.rgb * (1.-layer[i].a) + layer[i].rgb * (2./(pow(float(i),2.5)+1.));
    }

    // flare
    float d = distance(pp, sunpos);
    col.rgb += vec3(1.,1.,.5) * pow(1./(1.+d), 3.)*.1;


    // color grading
    col.rgb = pow(col.rgb, vec3(1.0,1.5,1.3));

    // vignetting
    col.rgb *= pow( uv.x*uv.y*(1.-uv.x)*(1.-uv.y)*100., .1);

    // gamma correction
    col.rgb = pow(col.rgb, vec3(1./2.2));

    // output to the screen
    fragColor = vec4(col.rgb * smoothstep(0.,3., iTime),1.0);
}

