// edit of xt95's fractal doodling: https://www.shadertoy.com/view/ds2GRW
// which was inspired by gaz: https://www.shadertoy.com/user/gaz/

// timer loop length
#define TIMER 20.
// constant zoom mode
// #define CZOOM

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/R.xy;
    vec3 rd = normalize(vec3(uv*2.-1., 2.));

    if (iMouse.z>0.5) {
        vec2 muv = iMouse.xy/R.xy-0.5;
        rd.xz = rot(muv.x*PI*2.) * rd.xz;
        rd.yz = rot(muv.y*PI*2.) * rd.yz;
    }

    fragColor = vec4(0.,0.,0.,1.);

    for(float cycle=0.;cycle<1.5;cycle++) {
        vec3 ro = vec3(0.,0.,-.5);
        float t = 0.;
        float time = iTime + cycle*TIMER*100.5 + 157.;
        float ntime = time/TIMER;
        vec3 seed = hash3( uint(ntime) + 3u );
        vec3 seed2 = hash3( uint(ntime) );
        time = mod(time, TIMER);
        ntime = time/TIMER;
        ro.z += ntime*.25;
        // cycle progress bars for debugging
        /* if (uv.y>0.95 && cycle<0.5) {
            if (uv.x>ntime) {fragColor = vec4(1.); return;}
        }
        if (uv.y<0.95 && uv.y>0.9 && cycle > 0.5) {
            if (uv.x>ntime) {fragColor = vec4(1.); return;}
        } */
        #ifdef CZOOM
            float fade = sin((ntime*2.-0.5)*PI)*0.5+0.5;
            fade = smoothstep(0.,1.,fade);
            float zoom = 2. - ntime*2.;
        #else
            float ease_in = smoothstep(.0,.2, ntime);
            float ease_out = smoothstep(.75,.65, ntime);
            float fade = ease_in * ease_out;
            ease_in = smoothstep(.0,.3, ntime);
            ease_out = smoothstep(.8,.6, ntime);
            float zoom = .9 - ease_in + ease_out - ntime*0.5;
        #endif

        vec3 col = vec3(0.);
        for(int i=0; i<100; i++) {
            vec3 p = ro + rd * t;
            p.z -= zoom * 0.6;
            if (cycle>0.5)
                p.xz = rot(-iTime*0.02+0.3) * p.xz;
            else
                p.xz = rot(iTime*0.02+0.3) * p.xz;
            p.xy = rot(-iTime*0.03-.2) * p.xy;
            float s = 2., r2;
            for(int j=0; j<5+int(cycle*3.); j++) {
                r2 = (3.2-zoom)/min(dot(p,p),1.9);
                p = abs(p*r2) - (seed+.1)*5.;
                s *= r2;
            }

            float d = length(p.xy)/s;
            t += d*.5;
            col += vec3(cos(vec3(1.,2.,3.)-iTime*1.5*(seed2.g*2.-1.) + log(s))*.5+.5) * 0.03 * exp(-.5*d*float(i*i));
        }
        col = clamp(col,0.,1.) * fade;
        fragColor.rgb += col;;
    }
}

