// "smoke"
vec2 laplacianAB(in vec2 xy) {
    vec2 sum = vec2(0.);
    vec2 px = 1./iResolution.xy;
    px *= 3.;
    int range = 1;
    for (int i=-range;i<range+1;i++){
        for (int j=-range;j<range+1;j++){
            vec2 offset = vec2(float(i),float(j))*px;
            float factor = 0.05;
            if (i==0||j==0) factor = 0.2;
            if (i==0&&j==0) factor = -1.;
            if (i<-1||j<-1) factor = 0.02;
            if (i>1||j>1) factor = 0.02;
            sum += texture(iChannel0,xy+offset).ab*factor;
        }
    }
    px *= 1.;
    range = 1;
    for (int i=-range;i<range+1;i++){
        for (int j=-range;j<range+1;j++){
            vec2 offset = vec2(float(i),float(j))*px;
            float factor = 0.05;
            if (i==0||j==0) factor = 0.2;
            if (i==0&&j==0) factor = -1.;
            if (i<-1||j<-1) factor = 0.025;
            if (i>1||j>1) factor = 0.025;
            sum += texture(iChannel0,xy+offset).ab*factor;
        }
    }
    return sum / 2.;
}

vec2 setAB(in vec2 xy, in float cdist) {

    float dA = 0.95;
    float dB = .3;
    float feed = 0.06 + sin(iTime*4.)*0.05;
    float k = 0.06 + 0.01*sin(iTime);

    float a = texture(iChannel0,xy).a;
    float b = texture(iChannel0,xy).b;
    vec2 lapAB = laplacianAB(xy);
    return vec2(
        a +
        dA * lapAB.x -
        a * b * b +
        feed * (1. - a),
        b +
        dB * lapAB.y +
        a * b * b -
        (k + feed) * b
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/R.xy;
    vec2 muv = iMouse.xy/R.xy;
    vec2 uvc = (fragCoord*2. - R.xy)/R.y;
    vec2 muvc = (iMouse.xy*2. - R.xy)/R.y;
    float mdist = distance(uvc,muvc);
    float cdist = length(uvc);

    vec2 offset = vec2(0.,0.001) + noise(vec3(uvc,iTime*0.5))*0.01;
    fragColor.ab = setAB(uv-offset,cdist);
    if (iMouse.z > 0.5 && mdist<0.1) { 
        fragColor.b = 1.; 
        fragColor.a = 0.; 
    }

    // init animation
    if (iMouse.z < 0.00001) {
        vec2 p = vec2(0.,.75);
        p.x += sin(iTime)*0.666;
        p.x += sin(iTime*3.3)*0.25;
        p.x += smoothstep(.1,.9,sin(iTime*.5)*0.5+0.5)*1.4 - 0.7;
        p.y += sin(iTime*3.7)*0.05;
        p.y -= smoothstep(.4,.9,sin(iTime*0.2)*.5+.5)*1.5;
        float pdist = distance(uvc, p);
        if (pdist < .1+sin(iTime*1.6)*.05) {
            fragColor.b = 1.;
            fragColor.a = 0.;
        }
    }
}

