// "slime"
vec2 laplacianAB(in vec2 xy) {
    vec2 sum = vec2(0.);
    vec2 px = 1./iResolution.xy;
    px *= 1.;
    int range = 1;
    for (int i=-range;i<range+1;i++){
        for (int j=-range;j<range+1;j++){
            vec2 offset = vec2(float(i),float(j))*px;
            float factor = 0.05;
            if (i==0||j==0) factor = 0.2;
            if (i==0&&j==0) factor = -1.;
            if (i<-1||j<-1) factor = 0.02;
            if (i>1||j>1) factor = 0.02;
            sum += texture(iChannel0,xy+offset).ab*factor;
        }
    }
    return sum;
}

vec2 setAB(in vec2 xy, in float cdist) {

    float dA = 1.1;
    float dB = .35;
    float feed = 0.055;
    float k = 0.059;

    float a = texture(iChannel0,xy).a;
    float b = texture(iChannel0,xy).b;
    vec2 lapAB = laplacianAB(xy);
    return vec2(
        a +
        dA * lapAB.x -
        a * b * b +
        feed * (1. - a),
        b +
        dB * lapAB.y +
        a * b * b -
        (k + feed) * b
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (R.y-fragCoord.y<1.5) {fragColor = vec4(0.);return;} // skip the top pixel row to avoid artifacts
    vec2 uv = fragCoord/R.xy;
    vec2 muv = iMouse.xy/R.xy;
    vec2 uvc = (fragCoord*2. - R.xy)/R.y;
    vec2 muvc = (iMouse.xy*2. - R.xy)/R.y;
    float mdist = distance(uvc,muvc);
    float cdist = length(uvc);

    vec2 px = 1./iResolution.xy;

    /* 
       slime stacking:
       this works by comparing mipmaps at the current location vs 
       a location below to get a "slime differential" (?).
       this was coded by feel, I think this general method could 
       be tuned to be more interesting or "realistic"
   */
    float drip_speed = 1.;
    vec2 look_below = vec2(0,.05);
    look_below.x += noise(vec3(uvc,iTime))*px.x; // fuzz left and right slightly
    // check how much "slime" is below
    float slime_below = texture(iChannel0, uv-look_below, 4.).a;
    // wider "slime" check
    slime_below += texture(iChannel0, uv-look_below*2., 8.).a;
    // negative check within current space - to avoid slime columns
    slime_below /= .2+.8*texture(iChannel0, uv, 4.).a;
    slime_below = smoothstep(2.,.5,slime_below);
    fragColor.r = slime_below/2.; // debug
    drip_speed *= 1.-slime_below;
    // slow down at the bottom
    drip_speed *= 0.1+0.9*smoothstep(0.02,0.2,uv.y);
    // drip
    uv.y += drip_speed*px.y;

    float xoffset = -uvc.x*px.x;
    uv.x += mix(0.,xoffset,slime_below);
    // uv.x += sin(slime_below*3.)*px.x;

    vec2 buffA = texture(iChannel1,uv).ab;

    fragColor.ab = mix(
        mix(setAB(uv,cdist),vec2(0.),0.001),
        buffA,
        (1.-buffA.x)*0.05
      );
    if (iMouse.z > 0.5) {
        if (mdist<0.05) { fragColor.b = 1.; fragColor.a = 0.; }
    }
}

