/* Testing my recent volumetric raymarcher on more complex 
   and dynamic scenes.
   I think the lighting (especially the sky) could be improved.   
*/
#define MAX_ITER 300.
#define MAX_DIST 25.
#define ABSORPTION 10.

#define hash33(p) fract(sin( (p) * mat3( 127.1,311.7,74.7 , 269.5,183.3,246.1 , 113.5,271.9,124.6) ) * 43758.5453123)

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

// noise and fbm are from https://www.shadertoy.com/view/XtS3DD
float noise(in vec3 p) {
    vec3 ip = floor(p);
    vec3 fp = fract(p);
	fp = fp*fp*(3.0-2.0*fp);
	vec2 tap = (ip.xy+vec2(37.0,17.0)*ip.z) + fp.xy;
	vec2 rz = textureLod(iChannel0, (tap+0.5)/256.0, 0.0).yx;
	return mix(rz.x, rz.y, fp.z);
}

float fbm(in vec3 x) {
    x += vec3(iTime*.015, iTime*.035, 0.);

    float rz = 0.;
    float a = .35;
    for (int i = 0; i<4; i++) {
        rz += noise(x)*a;
        a*=.35;
        x*= 4.;
    }
    return rz - .25;
}


// signed distance to volume
float vol(vec3 p) {
    p.z += iTime*.8;
    float r = fbm(p) + max(0., p.y) - .1 - .08*smoothstep(1.,0.,length(p.xy));
    return p.y - r*((cos(iTime*.146+1.)*.5+.5)*.1+.9) - (sin(iTime*.2)*.5+.5)*abs(p.x)*.1;
}

// get light visibility at point
float sampleLight(vec3 ro, vec3 lightPos, float att) {  
    float t = 0., visibility = 1., stepSize = 0.005*2.;
    vec3 rd = normalize(ro-lightPos);
    float dstLight = length(ro-lightPos);

    // directional light if att < 0., else point light
    float attenuation = att < 0. ? 1. : smoothstep(0., 1., att / pow(dstLight, 2.0));

    for (int i = 0; i < 9; i++) {
        vec3 p = ro + t*rd;
        float d = vol(p);
        t += max(stepSize, d) + hash33(p).x*.002;
        if (d < 0.) visibility *= exp(-stepSize*ABSORPTION);
        if (t > dstLight) break;
    }

    return visibility * attenuation;
}

void initCamera(vec2 fragCoord, inout vec3 ro, inout vec3 rd) {
    vec2 uv = (fragCoord - iResolution.xy*.5)/iResolution.y;
    vec2 m = iMouse.z == 0. ? vec2(.5) : iMouse.xy/iResolution.xy;

    ro = vec3(0., 0.25, 0.);                                   // camera position
    float mx = (sin(-(iTime-3.)*.2)*.5)*.8 - (m.x*2.-1.)*.5 + 1.54; // camera x rotation
    float my = (m.y-.5)*.5;                                    // camera y rotation

    vec3 f = normalize(vec3(cos(mx), my, sin(mx))),
         r = normalize(cross(vec3(0,1,0), f)),
         u = cross(f,r),
         c = ro+f,
         i = c + uv.x*r + uv.y*u;    
    rd = normalize(i-ro); // camera direction
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec3 ro, rd;

    initCamera(fragCoord, ro, rd);

    float visibility = 1.;
    vec3 vColor = vec3(0.), color = vec3(.14,0.08,.12)*.85;

    float t = 0., eps = 0.0005;
    float inside = vol(ro) < 0. ? 1. : 0.;

    for (float i = 0.; i < MAX_ITER; i++) {
        vec3 p = ro + t*rd;

        float dvol = vol(p);
        float d = abs(dvol) + 0.001*t;
        t += d;

        bool isInsideVolume = mod(inside, 2.) > .5;

        if (!isInsideVolume) { 
            if (dvol < eps) { 
                inside++; t += eps*10.; // enter volume
            }
        } else {    
            if (dvol > -eps) {  
                inside++; t += eps*10.; // exit volume
            } else {
                // sample density & light
                float prev = visibility;
                visibility *= exp(-d*ABSORPTION);

                vec3 absorption = vec3(prev - visibility);

                absorption *= mix(color, vec3(1.), exp(-t*.05));
                vColor += absorption * sampleLight(p, -vec3(0., 4., 0.), 10.) * vec3(1.2,.6,.3)*.75;
                vColor += absorption * sampleLight(p, -vec3(0., 1e6, 0.), -1.) * (vec3(1.3, 1.,.8)*.5 + 0.5*fbm(p));

                if (visibility <= .01 || dot(vColor, vColor) >= 3.) break;    
            }
        }

        if (t > MAX_DIST) { i = 0.; break; }
    }

    color = vColor + color * visibility;

    fragColor = vec4(color,1.0);
}

