    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

// Some iq's function
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
  return length( pa - ba*h ) - r;
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }

vec2 hash22(vec2 p)
{
    p *= 100.;
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract(sin(p3.xy+p3.yz)*p3.zy);
}

float sdSphere(vec3 p, float r, vec3 offset)
{
  return length(p - offset) - r;
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }


float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdRoundBox( vec3 p, vec3 b, float r)
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sdEllipsoid( vec3 p, vec3 r )
{
  float k0 = length(p/r);
  float k1 = length(p/(r*r));
  return k0*(k0-1.0)/k1;
}

float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  // sampling independent computations (only depend on shape)
  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);

  // sampling dependant computations
  vec2 q = vec2( length(p.xz), p.y );
  float k = dot(q,vec2(-b,a));
  if( k<0.0 ) return length(q) - r1;
  if( k>a*h ) return length(q-vec2(0.0,h)) - r2;
  return dot(q, vec2(a,b) ) - r1;
}

float Tux(vec3 p, out vec3 col, out float obj, vec3 q, vec3 ro){

    float d = 1e10;

    // CORE
    vec3 tmp = vec3(0. ,cos(iTime)*.1, 0.05-.03*cos(iTime));
    vec3 tmp2 = vec3(cos(iTime)*.1, 0., 0.);
    p += tmp;
    vec3 b = vec3(.25,.8,.3);
    vec3 pt = p;
    pt.y -= .2;
    float d2 = min(d, sdRoundBox(pt, b, .4));
    if (d2<d){
        d = d2;
        col = vec3(.2);
        obj = 1.;
    }

    pt = p;
    pt.y += .1;
    pt *= 1.1;
    d2 = sdSphere(pt, 1., vec3(0.));
    if (d2<d){
        col = mix(col, vec3(1.), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);
    }
    d = opSmoothUnion(d, d2, .5);


    pt = p+tmp2;
    pt.y -= 1.;
    pt *= 2.;
    d2 = sdSphere(pt, 1.2, vec3(0.));   
    if (d2<d){
        col = vec3(.2);

    }
    d = opSmoothUnion(d, d2, .5);

    // NOSE
    pt = p+tmp2;
    pt += vec3(0.,-.7, -.5);
    pt *= 2.;
    d2 = sdOctahedron(pt, 1.);
    if (d2<d){
        col = mix(col, vec3(.7, .7, 0.), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);
    }
    d = opSmoothUnion(d, d2, .05);



    // EYES
    pt = p+tmp2;
    pt.x = abs(pt.x);
    pt += vec3(-.35, -1., -.55);
    pt *= 3.5;
    d2 = sdSphere(pt, 1., vec3(0.));   
    if (d2<d){
        col = mix(col, vec3(1.), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);
    }
    d = opSmoothUnion(d, d2, .05);

    pt = p+tmp2;
    pt.x = abs(pt.x);
    pt += vec3(-.35, -1., -.7);
    pt *= 5.5;
    d2 = sdSphere(pt, 1., vec3(0.));   
    if (d2<d){
        col = mix(col, vec3(0.), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);
    }
    d = opSmoothUnion(d, d2, .05);


    // NAGEOIRE
    pt = p;
    pt.x = -abs(pt.x);
    pt.y -= .3;
    mat3 rot = rotateX(.25+sign(p.x)*.7*cos(iTime*4.));
    pt *= rot;
    pt.y += .3;

    pt += vec3(1., 0., 0.);
    rot = rotateZ(-.5);
    pt = rot*pt;
    vec3 r = vec3(1.,2.,1.)/3.;
    d2 = sdEllipsoid(pt, r);   
    if (d2<d){
        col = mix(col, vec3(.1), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);
    }
    d = opSmoothUnion(d, d2, .01);


    // PALME
    pt = p;
    pt.x = abs(pt.x);
    pt += vec3(-.4, 1., -.3);
    pt *= 3.5;
    rot = rotateX(.3+sign(p.x)*.5*sin(iTime/2.));
    pt *= rot;
    r = vec3(1.3,1.,2.);
    d2 = sdEllipsoid(pt, r);
    if (d2<d){
        col = mix(col, vec3(.7, .7, 0.), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);

    }
    d = opSmoothUnion(d, d2, .05);


    // Queue
    pt = p;
    pt += vec3(0., .9, 1.1);
    pt.z -= .9;
    rot = rotateY(.3*sin(iTime*4.));
    pt *= rot;
    pt.z += .9;    

    rot = rotateX(-PI/2.+.7);
    pt *= rot;
    d2 = sdRoundCone(pt, .2, .5, .7);
    if (d2<d){
        col = mix(col, vec3(.1), smoothstep(0. ,0.01, abs(d2-d)-.02));
        obj = smoothstep(0. ,0.01, abs(d2-d)-.03);

    }
    d = opSmoothUnion(d, d2, .05);

    return d;
}


float map(vec3 p, out vec3 col, out float obj, vec3 ro){

    float d = 1e10;    
    vec3 c = vec3(3.)*2.;
    vec3 q = mod(p+.5*c, c)-.5*c;
    q = p; // Comment for a multitude of pinguin !!!!
    d = min(d, Tux(q, col, obj, p, ro));

    return d;
}

float rayMarch(vec3 ro, vec3 rd, float start, float end, out vec3 col, out float L, out float obj) {
  float depth = start;
  float id = 0.;
  float depth2 = 1e10;
  float dit = fract(sin(dot(rd, vec3(123.4588, 789.457, 0.))))*0.0001;
  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    float d = map(p, col, obj, ro);
    depth += d;
    if (d < PRECISION){
        depth2 = depth;
        L = 1.;
        break;
        }
    if(d < PRECISION*10.){
        if (id != 1.){
            col *= 0.;
            depth2 = depth+dit;
            L *= 0.;

        }
      }
    }
  return depth2;
}

vec3 calcNormal(vec3 p, vec3 ro) {
    vec2 e = vec2(1.0, -1.0) * 0.005; // epsilon
    float r = 1.; // radius of sphere
    vec3 c;
    float obj;

    return normalize(
      e.xyy * map(p + e.xyy, c, obj, ro) +
      e.yyx * map(p + e.yyx, c, obj, ro) +
      e.yxy * map(p + e.yxy, c, obj, ro) +
      e.xxx * map(p + e.xxx, c, obj, ro));
}

mat2 rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}


mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up

	return mat3(-cr, cu, -cd);
}

vec3 phong(vec3 lightDir, vec3 normal, vec3 rd, vec3 col) {
  // ambient
  vec3 ambient = col*(.5);

  // diffuse
  float dotLN = smoothstep(.45, .5, dot(lightDir, normal))/4.;
  vec3 diffuse = col * dotLN;

  // specular
  float dotRV = smoothstep(.85, .9, (dot(reflect(lightDir, normal), -rd)));
  vec3 specular = 2.*col * pow(dotRV, 5.);

  return ambient + diffuse; + specular;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  vec3 backgroundColor = vec3(0.1, .1, .1)*4.;

  vec3 col = vec3(0);
  vec3 col_tot = vec3(0.);
  vec3 ro = vec3(4., 4., 4.);
  vec2 m = iMouse.xy/iResolution.xy;
  if (m.xy == vec2(0.)){
      m.x = .6;
      m.y = .5;
  }
    vec3 tmp = vec3(0.,cos(iTime)*.1, 0.05-.03*cos(iTime));
    tmp.z -= .8;
    tmp.y -= 1.;
    tmp.x -= .35;
  vec3 lookat = tmp;// ray origin that represents camera position
  ro.yz = ro.yz * 1. * rot(mix(PI/2., 0., m.y));
  ro.xz = ro.xz * rot(mix(-PI, PI, m.x)) + vec2(lookat.x, lookat.z); // remap mouseUV.x to <-pi, pi> range  

  vec3 rd = camera(ro, lookat)*normalize(vec3(uv, -1)); // ray direction

  float w;
  float obj = 1.;
  for (int i = 0; i<AA+1; i++){
      for (int j = 0; j<AA+1; j++){

          vec3 rd2 = rd + vec3(float(i), float(j), 0.)*(cos(iTime)*.0005);
          float L = 1.;
          float d = rayMarch(ro, rd2, MIN_DIST, MAX_DIST, col, L, obj); // distance to sphere

          if (d > MAX_DIST) {
            col_tot += backgroundColor/float(2*(AA+1)); // ray didn't hit anything
          } else {
            vec3 p = ro + rd * d; // point on sphere we discovered from ray marching
            vec3 normal = calcNormal(p, ro);
            vec3 lightPosition = vec3(1, 1, 2);
            vec3 lightDirection = normalize(lightPosition - p);
            float lint = 1.2;

            // Multiply the diffuse reflection value by an orange color and add a bit
            // of the background color to the sphere to blend it more with the background.
            if (L != 0.) col_tot += obj*lint*phong(lightDirection, normal, rd, col)/float((AA+1));
            else col_tot *= 0.;
          }
    }
  }  

  //vec3 tmp = fwidth(col_tot);

  // Output to screen
  fragColor = vec4(col_tot, 1.0);
}

