// Originally [SH17A] Lake Drops by fizzer.
// Great job on the shader, fizzer.
//
// This was originally code golfed to be sent out in a Twitter competition (< 255 characters).
//
// https://www.shadertoy.com/view/MdjBDh

#define PI 3.1415936535

void mainImage(out vec4 color,vec2 fragCoord) {
	vec2 screen_window = fragCoord / iResolution.x * 2.0 - 1.0; // x goes from [-1 to 1].
    // This is used to accumulate over all ripples at the fragment location.
    vec2 displacement = vec2(0.0);
    // Same ripples over and over again, they repeat with this frequency.
    float loop_time = 3.0;
    // Lower numbers make wider ripples.
    float ripple_width_inverse = 20.0;
    float limits = PI / ripple_width_inverse;
    // Increase decay to quicken fading of ripples with time.
    float decay = 2.0;
    // Controls the concentration of ripples (how far they spawn from the origin).
    float spread = 4.0;
    // Increase number of ripples. It can be cheaper to decrease the loop time.
    int num_ripples = 68;

    // Change the perspective from top down (straight) to an angular view.
    screen_window /= (screen_window.y += 1.6) - 2.00;

    for (float i = 0.0; i < float(num_ripples); ++i) {
        // Distance from the origin of a ripple.
        vec2 origin = vec2(919.,154.) * i;
        // The cos wraps the ripple spawn points to within the local area.
        vec2 dist = screen_window - spread * (cos(origin) - 0.5);
        // Increasing this value staggers the creation time of ripples. For zero, they all create at the
        // same time.
        float time_offset = i * 0.12;
        float t = length(dist) - mod(time_offset + iTime, loop_time);
        // Only show one period of each sine wave.
        float t_one_period = clamp(t, -limits, limits);
        // Waves decay with time. I've added a decay parameter to control this more as well as
        // a small offset to prevent a divide by zero artifact.
        vec2 amplitude = dist / (pow(dot(dist, dist), decay) + 1e-3);
        displacement += amplitude * sin(ripple_width_inverse * t_one_period);        
    }
    // Original: use the displacement to offset the read from the texture.
    color = 0.7 * texture(iChannel0, vec3(displacement - screen_window * 6.0, 5.0)) +
            // Bottom of the pond. No magic here.
            0.4 * texture(iChannel1, screen_window);
    // View only the displacement.
    // color = vec4(vec3(displacement + 0.5,0.5), 1);


}

////////////////////////////////////////////////////////////////////////////////
//
// Metakoch! mla, 2022.
//
// Koch curves but where the central triangle has an arbitrary apex, 
// set by the mouse, so including the original curve, the Kochawave & 
// many others. Heavily overlapping curves (with mouse toward top or sides) 
// can get a bit slow.
//
// Code should generalize to more than 4 segments, all non-end points being
// adjustable etc.
//
// Inspired by https://www.shadertoy.com/view/Dsj3zD by FabriceNeyret2
//
// Uses an explicit stack to keep track of the recursion.
// This one keeps track of intermediate segments and avoids recursing far from
// the point being plotted.
//
// 'a': adaptive mode where we iterate down to a minimum segment length
// 'x': show segments actually drawn around mouse position
// 'w': showing winding number
// 'f': show moving point
// 'r': show radius
// 'c': cycle
// 'p': progressive depth
// 'k': Kochawave curve (just to check)
//
////////////////////////////////////////////////////////////////////////////////


// Stack representation
#if 1
#define PACK(x) (pack(x))
#define UNPACK(x) (unpack(x))
#define STACKTYPE uint
#else
#define PACK(x) x
#define UNPACK(x) x
#define STACKTYPE vec2
#endif

const int MAXDEPTH = 12; //Maximum stack depth
const vec2 initp = vec2(-1,0); // Initial drawing point

vec2 p = initp;                // The drawing point
vec2 point = vec2(0);          // The moving point
bool gotpoint = false;
float plen = 0.0;              // Cumulative path length
int wind = 0; // Compute winding number here

// Fixed point packing for floats [0,1)
uint pack(float s) {
  s = round(s*65536.0);
  uint n = uint(s);
  assert(n < 65536u);
  return n;
}

// Divide length multiplier by 16 and angle by 2PI.
uint pack(vec2 s) {
 return (pack(s.x/8.0)<<16)|pack(mod(s.y/(2.0*PI)+0.5,1.0));
}

vec2 unpack(uint n) {
 return vec2(float(n>>16)/65536.0*8.0,
             2.0*PI*(float(n&0xffffu)/65536.0-0.5));
}

void polywind(vec2 p, vec2 q, vec2 r) {
  // NR winding number computation
  q -= p; r -= p; // Relative to p
  float t = determinant(mat2(q,r));
  if (q.x <= 0.0) {
    if (r.x > 0.0 && t > 0.0) wind++;
  } else {
    if (r.x <= 0.0 && t < 0.0) wind--;
  }
}

float drawsegment(vec2 pos, float seglen, float theta, float t) {
  vec2 seg = seglen*vec2(cos(theta),sin(theta));
  if (t < plen+seglen && !gotpoint) {
    gotpoint = true;
    point = p + (t-plen)/seglen*seg;
  }
  vec2 q = p + seg;
  float d = segment(pos,p,q);
  polywind(pos,p,q);
  plen += seglen;
  p = q;
  return d;
}

// Map screen coordinates
vec2 map(vec2 pos) {
  pos = (2.0*pos - iResolution.xy)/iResolution.y;
  pos.y += 0.65;
  pos /= 1.5;
  return pos;
}

// This should get something from a buffer so we can set all three points
vec2 getpoint(int i) {

  if (i == 1) return vec2(1.0/3.0,0);
  if (i == 2) {
    if (key(CHAR_K)) return vec2(1.0/3.0,0) + vec2(0.5,0.5/sqrt(3.0));
    else if (length(iMouse.xy) > 10.0) return iMouse.xy/iResolution.xy;
    else if (!key(CHAR_C)) return vec2(0.5,0.25-0.25*cos(0.5*iTime));
    else return vec2(0.5);
  }
  if (i == 3) return vec2(2.0/3.0,0);
  assert(false);
  return vec2(0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  int maxdepth = 8;
  bool adaptive = key(CHAR_A);
  if (adaptive) maxdepth = MAXDEPTH;
  else if (key(CHAR_P)) maxdepth = 1+int(iTime)%maxdepth;
  vec2 pos = map(fragCoord);
  vec2 pos2 = pos;
  if (key(CHAR_X)) pos2 = map(iMouse.xy);
  float pwidth = fwidth(pos.x);
  STACKTYPE program[5];
  float radius = 0.0;
  float pathlen = 0.0;
  {
    #define C(a,b) (PACK(vec2(a,b)))
    vec2 p[] =
      vec2[](vec2(0,0),
             getpoint(1),
             getpoint(2),
             getpoint(3),
             vec2(1,0));
    float theta = 0.0;
    float seglen = 1.0;
    for (int i = 0; i < 4; i++) {
      radius = max(radius,distance(vec2(0.5,0),p[i]));
      float theta1 = arg(p[i+1]-p[i]);
      float seglen1 = length(p[i+1]-p[i]);
      pathlen += seglen1;
      program[i] = C(seglen1/seglen,theta1-theta);
      theta = theta1;
      seglen = seglen1;
    }
    program[4] = C(1.0/seglen,-theta);
    #undef C
  }
  int stack[MAXDEPTH];
  int depth = 0;
  stack[depth++] = 0;
  float d = 1e8;
  float seglen = 2.0;
  float theta = 0.0;    // Cumulative angle
  float t = mod(iTime,seglen*pow(pathlen,float(maxdepth))); // Moving point
  while (depth > 0) {
    vec2 instr = UNPACK(program[stack[depth-1]++]);
    float lenfact = instr[0];
    float angle = instr[1];
    theta += angle; // Apply turn
    seglen *= lenfact;
    if (stack[depth-1] == program.length()) {
      depth--;    // Terminate level & unwind
    } else if (depth == maxdepth || seglen < 2.0*pwidth) {
      d = min(d,drawsegment(pos,seglen,theta,t)); // Segment
    } else {
      vec2 seg = seglen*vec2(cos(theta),sin(theta));
      vec2 next = p+seg; // Where we end up
      if (distance(pos2,p+0.5*seg) > 1.5*radius*length(seg)) {
        polywind(pos,p,next);
        // Show skipped segments on mouse down.
        if (key(CHAR_X)) d = min(d,segment(pos,p,next));
        p = next;
        plen += seglen*pow(pathlen,float(maxdepth-depth));
      } else {
        stack[depth++] = 0;  // Recurse
      }
    }
  }
  // A bodge to close off the winding number calculation
  vec2 offset = vec2(10,0);
  polywind(pos,initp,initp-offset);
  d = min(d,segment(pos,initp,initp-offset));
  polywind(pos,p,p+offset);
  d = min(d,segment(pos,p,p+offset));
  float px = fwidth(pos.x);
  vec3 col = vec3(smoothstep(0.0,px,d));
  if (!key(CHAR_W) && (wind&1) != 0) col *= mix(0.2,0.8,1.0-fragCoord.y/iResolution.y);;
  if (key(CHAR_F) && gotpoint) col = mix(vec3(1,0,0), col,smoothstep(0.0,px,distance(pos,point)-0.01));
  if (key(CHAR_R)) col = mix(vec3(1,0,0), col,smoothstep(0.0,px,abs(distance(pos,vec2(0))-2.0*radius)));
  col = pow(col,vec3(0.4545));
  if (alert) col.r = 1.0;
  fragColor = vec4(col,1);
}

common_code:
bool alert = false;
void assert(bool b) {
  if (!b) alert = true;
}

const float PI = 3.1415927;

float segment(vec2 p, vec2 a, vec2 b) {
  p -= a; b -= a;
  float h = dot(p,b)/dot(b,b);
  h = clamp(h, 0.0, 1.0);
  return length(p-b*h);
